            <li key={f.id} className="bg-zinc-800 p-3 rounded text-sm">
              <span className="font-bold">{f.action}</span> on <code>{f.block_id.slice(0, 8)}</code>
              {f.message && <p className="text-zinc-300 mt-1 italic">‚Äú{f.message}‚Äù</p>}
