        {sent.length === 0 && <p className="text-zinc-400 text-sm">No outgoing feedback yet.</p>}
        <ul className="space-y-2">
          {sent.map((f: any) => (
            <li key={f.id} className="bg-zinc-800 p-3 rounded text-sm">
