  const [loading, setLoading] = useState(true);
  const [debug, setDebug] = useState<any>(null);

  const assignRoleOnLogin = false;
  const showSpinner = false;

  // ‚úÖ Check for session token in URL hash
  useEffect(() => {
    const hasSessionToken =
      typeof window !== 'undefined' && window.location.hash.includes('access_token');

    if (hasSessionToken) {
      supabase.auth.getSession().then(({ data }) => {
        console.log('[üîÅ Refreshed session from hash]', data);
      });
    }

    const checkUser = async () => {
      const { data: { user }, error } = await supabase.auth.getUser();
      if (!user || error) {
        setDebug({ error, user });
        setLoading(false);
        return;
      }

      const { data: profile } = await supabase
        .from('user_profiles')
        .select('role, plan')
        .eq('user_id', user.id)
        .maybeSingle();

      const role = profile?.role ?? 'viewer';
      const plan = profile?.plan ?? 'free';

      setDebug({ user, profile, role, plan });

      if (role !== 'viewer') {
        const redirect =
          plan === 'enterprise'
            ? '/enterprise/dashboard'
            : ['admin', 'owner', 'reseller'].includes(role)
              ? '/admin/dashboard?message=welcome'
              : '/?message=welcome';

        // router.replace(redirect);
      } else {
        setShowOnboarding(true);
      }

      setLoading(false);
    };

    checkUser();
  }, []);

  // ‚úÖ Dev AutoLogin Trigger
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const devEmail = params.get('dev-login');

    if (
      process.env.NODE_ENV === 'development' &&
      devEmail &&
      devEmail.includes('@')
    ) {
      console.log(`[‚öôÔ∏è Dev AutoLogin Trigger] ${devEmail}`);

      supabase.auth
        .signInWithOtp({
          email: devEmail,
          options: {
            emailRedirectTo: `${window.location.origin}/login`,
          },
        })
        .then(({ error }) => {
          if (error) {
            console.error('[‚ùå Dev AutoLogin Error]', error.message);
          } else {
            console.log('[‚úÖ Dev Magic Link Sent]');
          }
        });
    }
  }, []);

  // ‚úÖ Handle magic link sign-in
  useEffect(() => {
    const { data: listener } = supabase.auth.onAuthStateChange((event, session) => {
      if (event === 'SIGNED_IN' && session?.user) {
        console.log('[‚úÖ SIGNED_IN from magic link]', session.user);
        router.replace('/login'); // Forces refresh of current logic
      }
    });

    return () => {
      listener.subscription.unsubscribe();
    };
  }, []);

  // ‚úÖ Handle manual login
  const handleLogin = async () => {
    const token = await recaptchaRef.current?.executeAsync();
    recaptchaRef.current?.reset();

    if (!token) {
      setMessage('‚ùå reCAPTCHA failed. Try again.');
      return;
    }

    const res = await fetch('/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, recaptchaToken: token }),
    });

    const data = await res.json();

    if (!res.ok) {
      setMessage(`‚ùå ${data.error || 'Login failed.'}`);
    } else {
      setMessage('‚úÖ Check your email for the magic link.');
    }
  };

  // ‚úÖ Handle role assignment
  const assignRole = async (selected: string) => {
    const { data: { user } } = await supabase.auth.getUser();
    const id = user?.id;
    const email = user?.email ?? '';
    if (!id || !email) return;

    const now = new Date().toISOString();

    await supabase.from('user_profiles').upsert({
      user_id: id,
      email,
      role: selected,
      last_seen_at: now,
    });

    await supabase.from('session_logs').insert({
      type: 'assign_role',
      user_id: id,
      email,
      role: selected,
      timestamp: now,
    });

    setShowOnboarding(false);
    setToast(`‚úÖ Role set to "${selected}"`);
    setTimeout(() => setToast(null), 3000);

    const redirect = selected === 'admin' ? '/admin/dashboard' : '/?message=role-assigned';
    router.replace(redirect);
    setTimeout(() => window.location.reload(), 1000);
  };

  // ‚úÖ Render the login page
  return (
    <div className="min-h-screen flex items-center justify-center bg-black text-white p-4 relative">
      {loading && (
        <div className="fixed inset-0 z-50 bg-black/90 flex flex-col items-center justify-center">
          {showSpinner && (
            <svg
              className="animate-spin h-8 w-8 text-white mb-3"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle
                className="opacity-25"
                cx="12"
                cy="12"
                r="10"
                stroke="currentColor"
                strokeWidth="4"
              />
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z"
              />
            </svg>
          )}
          <p className="text-sm text-gray-300">Checking session‚Ä¶</p>
        </div>
      )}

      <div className="bg-zinc-900 p-6 rounded shadow max-w-sm w-full">
        <h1 className="text-xl font-bold mb-4 text-center">Login</h1>

        {showOnboarding ? (
          <div className="text-sm text-center space-y-4">
            <p>üëã Welcome! Choose your starting role:</p>
            <div className="flex flex-col gap-2">
              {['viewer', 'reseller'].map((r) => (
                <button
                  key={r}
                  onClick={() => assignRole(r)}
                  className="bg-blue-600 hover:bg-blue-700 px-3 py-2 rounded"
                >
                  Continue as {r}
                </button>
              ))}
            </div>
          </div>
        ) : (
          <>
            {toast && <p className="text-center mb-4 text-yellow-400 text-sm">{toast}</p>}
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="you@example.com"
              className="w-full px-3 py-2 mb-4 rounded bg-zinc-800 border border-zinc-700 text-white"
            />
            <button
              onClick={handleLogin}
              className="w-full bg-blue-600 hover:bg-blue-700 px-3 py-2 rounded text-sm font-semibold"
            >
              Send Magic Link
            </button>
            {message && (
              <p
                className={`mt-4 text-sm text-center ${message.startsWith('‚úÖ') ? 'text-green-400' : 'text-red-400'}`}
              >
                {message}
              </p>
            )}
            <ReCAPTCHA
              sitekey={process.env.NEXT_PUBLIC_RECAPTCHA_SITE_KEY!}
              size="invisible"
              ref={recaptchaRef}
            />
          </>
        )}

        {debug && (
          <pre className="text-xs text-left text-gray-400 bg-zinc-900 p-2 mt-4 rounded max-w-lg overflow-auto">
            {JSON.stringify(debug, null, 2)}
          </pre>
        )}
      </div>
    </div>
  );
}

