'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useUser, useSession } from '@supabase/auth-helpers-react';

export default function ShareSettings() {
  const user = useUser();
  const session = useSession();
  const accessToken = session?.access_token;

  const [profile, setProfile] = useState<any>(null);
  const [bio, setBio] = useState('');
  const [emoji, setEmoji] = useState('üå±');
  const [tags, setTags] = useState('');
  const [visible, setVisible] = useState(false);
  const [status, setStatus] = useState<'idle' | 'saving' | 'done' | 'error'>('idle');

  useEffect(() => {
    if (!user) return;
    fetch('/api/public-profile?handle=' + user.user_metadata.handle)
      .then((res) => res.json())
      .then((data) => {
        setProfile(data);
        setBio(data.bio || '');
        setEmoji(data.emoji || 'üå±');
        setTags((data.goal_tags || []).join(', '));
        setVisible(data.visible);
      });
  }, [user]);

  const handleSave = async () => {
    if (!accessToken || !user) return;
    setStatus('saving');
    const res = await fetch('/api/update-profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        bio,
        emoji,
        visible,
        goal_tags: tags.split(',').map((t) => t.trim()),
        handle: user.user_metadata.handle,
      }),
    });
    setStatus(res.ok ? 'done' : 'error');
  };

  return (
    <div className="p-6 text-white max-w-xl mx-auto space-y-4">
      <h1 className="text-2xl font-bold">üì° Share My World</h1>
      <p className="text-zinc-400 text-sm">
        This page controls what others can see at /world/[handle]/share
      </p>

      <div>
        <label className="block text-sm mb-1">Emoji</label>
        <input
          value={emoji}
          onChange={(e) => setEmoji(e.target.value)}
          className="text-black w-full p-1 rounded"
        />
      </div>

      <div>
        <label className="block text-sm mb-1">Short Bio</label>
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
          rows={2}
          className="text-black w-full p-1 rounded"
        />
      </div>

      <div>
        <label className="block text-sm mb-1">Goal Tags (comma separated)</label>
        <input
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          className="text-black w-full p-1 rounded"
        />
      </div>

      <div className="flex items-center space-x-2">
        <input type="checkbox" checked={visible} onChange={() => setVisible(!visible)} />
        <label>Make my page public</label>
      </div>

      <button onClick={handleSave} className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded">
        {status === 'saving' ? 'Saving...' : 'Save Changes'}
      </button>
      {status === 'done' && <p className="text-green-400 text-sm">‚úÖ Updated</p>}
      {status === 'error' && <p className="text-red-500 text-sm">‚ö†Ô∏è Something went wrong</p>}
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useRouter } from 'next/navigation';
import { useUser, useSession } from '@supabase/auth-helpers-react';

export default function StartPage() {
  const user = useUser();
  const session = useSession();
  const router = useRouter();

  const [templates, setTemplates] = useState([]);
  const [loc, setLoc] = useState<GeolocationCoordinates | null>(null);
  const [selected, setSelected] = useState('');
  const [status, setStatus] = useState<'idle' | 'saving' | 'done' | 'error'>('idle');

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => setLoc(pos.coords),
      () => setLoc(null)
    );
    fetch('/api/habit-templates')
      .then((_res) => _res.json())
      .then(setTemplates);
  }, []);

  const handleAdd = async () => {
    if (!selected || !user || !session || !loc) return;
    setStatus('saving');

    const accessToken = session.access_token;

    const res = await fetch('/api/create-block', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        lat: loc.latitude,
        lon: loc.longitude,
        title: selected.charAt(0).toUpperCase() + selected.slice(1),
        message: `Tracking: ${selected}`,
        emoji: selected === 'floss' ? 'ü¶∑' : selected === 'water' ? 'üíß' : 'üßò',
        slug: selected,
        type: 'tracking',
        actions: [{ label: 'Check In', type: 'check-in', target: selected }],
      }),
    });

    if (res.ok) {
      setStatus('done');
      router.push('/world/me');
    } else {
      setStatus('error');
    }
  };

  return (
    <div className="text-white p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">üëã Welcome to Your AR Habit Tracker</h1>
      <p className="text-zinc-400 mb-4">
        Choose a habit to start tracking, and we&apos;ll place it in your world:
      </p>
      <ul className="space-y-3">
        {templates.map((t: any) => (
          <li key={t.slug}>
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="habit"
                value={t.slug}
                checked={selected === t.slug}
                onChange={() => setSelected(t.slug)}
                className="form-radio"
              />
              <span>
                {t.emoji} {t.title}
              </span>
            </label>
          </li>
        ))}
      </ul>
      <button
        onClick={handleAdd}
        disabled={!selected || !loc || status === 'saving'}
        className="mt-6 px-4 py-2 rounded bg-blue-600 hover:bg-blue-700 disabled:opacity-50"
      >
        {status === 'saving' ? 'Creating...' : '‚ûï Add to My World'}
      </button>
      {status === 'error' && (
        <p className="text-red-500 mt-4">Something went wrong. Please try again.</p>
      )}
    </div>
  );
}

