export default function SummaryPage() {
  const user = useUser();
  const [summary, setSummary] = useState<any>(null);

  useEffect(() => {
    if (!user) return;
    fetch('/api/feedback-summary?user_id=' + user.id)
      .then((res) => res.json())
      .then(setSummary);
  }, [user]);

  if (!summary) return <div className="p-6 text-white">Loading summary...</div>;

  return (
    <div className="max-w-2xl mx-auto p-6 text-white space-y-6">
      <h1 className="text-2xl font-bold">üß† Weekly Summary</h1>

      <div>
        <h2 className="text-lg font-semibold mb-2">üì• Received Feedback</h2>
        {summary.received_feedback.length === 0 && (
          <p className="text-zinc-400">No feedback yet.</p>
        )}
        <ul className="space-y-2">
          {summary.received_feedback.map((f: any, i: number) => (
            <li key={i} className="bg-zinc-800 p-3 rounded text-sm">
              <span className="font-bold">{f.action}</span> on <code>{f.block_id.slice(0, 8)}</code>
              {f.message && <p className="italic mt-1 text-zinc-300">‚Äú{f.message}‚Äù</p>}
              <div className="text-xs text-zinc-400">{new Date(f.created_at).toLocaleString()}</div>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2 className="text-lg font-semibold mb-2 mt-6">üìÜ Check-in History</h2>
        {summary.checkin_history.length === 0 && <p className="text-zinc-400">No check-ins yet.</p>}
        <ul className="space-y-2">
          {summary.checkin_history.map((c: any, i: number) => (
            <li key={i} className="bg-zinc-900 p-2 rounded text-sm">
              ‚úÖ <strong>{c.slug}</strong> @ {new Date(c.created_at).toLocaleString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';

type Supporter = {
  user_id: string;
};

export default function ThankSupporters() {
  const [supporters, setSupporters] = useState<Supporter[]>([]);
  const [sent, setSent] = useState<Record<string, boolean>>({});

  useEffect(() => {
    fetch('/api/supporters?handle=me') // TODO: replace 'me' with dynamic handle
      .then((res) => res.json())
      .then(setSupporters)
      .catch((err) => console.error('Failed to load supporters:', err));
  }, []);

  const handleSendThanks = async (userId: string) => {
    const message = prompt('What would you like to say?');
    if (!message) return;

    try {
      await fetch('/api/send-thanks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('supabase.auth.token') || ''}`,
        },
        body: JSON.stringify({
          recipient_id: userId,
          block_id: 'N/A', // Replace if needed
          message,
        }),
      });

      setSent((prev) => ({ ...prev, [userId]: true }));
    } catch (err) {
      console.error('Failed to send message:', err);
      alert('Failed to send. Please try again.');
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 text-white space-y-4">
      <h1 className="text-2xl font-bold">üôè Send Thanks</h1>
      {supporters.map((s) => (
        <div key={s.user_id} className="bg-zinc-800 p-3 rounded">
          <div className="text-green-400 text-sm mb-2">User: {s.user_id.slice(0, 8)}</div>
          <button
            disabled={sent[s.user_id]}
            onClick={() => handleSendThanks(s.user_id)}
            className="bg-blue-600 hover:bg-blue-700 px-4 py-1 text-sm rounded disabled:opacity-50"
          >
            {sent[s.user_id] ? '‚úÖ Sent' : 'Send Thanks'}
          </button>
        </div>
      ))}
    </div>
  );
}

