'use client';
import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { useUser } from '@supabase/auth-helpers-react';
import { useSession } from '@supabase/auth-helpers-react';

export default function AddHabitBlock() {
  const router = useRouter();
  const session = useSession(); // ‚úÖ
  const user = session?.user;
  const accessToken = session?.access_token; // ‚úÖ
  const params = useSearchParams();
  const slug = params?.get('slug') || '';
  const [status, setStatus] = useState('initial');

  useEffect(() => {
    if (!slug || !accessToken || !user) return;

    navigator.geolocation.getCurrentPosition(async (pos) => {
      setStatus('saving');
      const res = await fetch('/api/create-block', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${accessToken}`, // ‚úÖ
        },
        body: JSON.stringify({
          lat: pos.coords.latitude,
          lon: pos.coords.longitude,
          title: slug.charAt(0).toUpperCase() + slug.slice(1),
          message: `Tracking: ${slug}`,
          emoji: slug === 'floss' ? 'ü¶∑' : slug === 'water' ? 'üíß' : 'üßò',
          slug,
          type: 'tracking',
          actions: [{ label: 'Check In', type: 'check-in', target: slug }],
        }),
      });

      if (res.ok) {
        setStatus('done');
        router.push('/world/me');
      } else {
        setStatus('error');
      }
    });
  }, [slug, accessToken, user]);

  return (
    <div className="text-white p-6 text-center">
      {status === 'saving' && <p>üìç Pinning to your current location...</p>}
      {status === 'done' && <p>‚úÖ Added! Redirecting...</p>}
      {status === 'error' && <p className="text-red-500">‚ö†Ô∏è Error adding block.</p>}
    </div>
  );
}

