export default function ShareSettings() {
  const user = useUser();
  const session = useSession();
  const accessToken = session?.access_token;

  const [profile, setProfile] = useState<any>(null);
  const [bio, setBio] = useState('');
  const [emoji, setEmoji] = useState('üå±');
  const [tags, setTags] = useState('');
  const [visible, setVisible] = useState(false);
  const [status, setStatus] = useState<'idle' | 'saving' | 'done' | 'error'>('idle');

  useEffect(() => {
    if (!user) return;
    fetch('/api/public-profile?handle=' + user.user_metadata.handle)
      .then((res) => res.json())
      .then((data) => {
        setProfile(data);
        setBio(data.bio || '');
        setEmoji(data.emoji || 'üå±');
        setTags((data.goal_tags || []).join(', '));
        setVisible(data.visible);
      });
  }, [user]);

  const handleSave = async () => {
    if (!accessToken || !user) return;
    setStatus('saving');
    const res = await fetch('/api/update-profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        bio,
        emoji,
        visible,
        goal_tags: tags.split(',').map((t) => t.trim()),
        handle: user.user_metadata.handle,
      }),
    });
    setStatus(res.ok ? 'done' : 'error');
  };

  return (
    <div className="p-6 text-white max-w-xl mx-auto space-y-4">
      <h1 className="text-2xl font-bold">üì° Share My World</h1>
