      {status === 'done' && <p className="text-green-400 text-sm">‚úÖ Updated</p>}
      {status === 'error' && <p className="text-red-500 text-sm">‚ö†Ô∏è Something went wrong</p>}
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useRouter } from 'next/navigation';
import { useUser, useSession } from '@supabase/auth-helpers-react';

export default function StartPage() {
  const user = useUser();
  const session = useSession();
  const router = useRouter();

  const [templates, setTemplates] = useState([]);
  const [loc, setLoc] = useState<GeolocationCoordinates | null>(null);
  const [selected, setSelected] = useState('');
  const [status, setStatus] = useState<'idle' | 'saving' | 'done' | 'error'>('idle');

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => setLoc(pos.coords),
      () => setLoc(null)
    );
    fetch('/api/habit-templates')
      .then((_res) => _res.json())
      .then(setTemplates);
  }, []);

  const handleAdd = async () => {
    if (!selected || !user || !session || !loc) return;
    setStatus('saving');

    const accessToken = session.access_token;

    const res = await fetch('/api/create-block', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        lat: loc.latitude,
        lon: loc.longitude,
        title: selected.charAt(0).toUpperCase() + selected.slice(1),
        message: `Tracking: ${selected}`,
        emoji: selected === 'floss' ? 'ü¶∑' : selected === 'water' ? 'üíß' : 'üßò',
        slug: selected,
        type: 'tracking',
        actions: [{ label: 'Check In', type: 'check-in', target: selected }],
      }),
    });

    if (res.ok) {
      setStatus('done');
      router.push('/world/me');
    } else {
      setStatus('error');
    }
  };

  return (
    <div className="text-white p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">üëã Welcome to Your AR Habit Tracker</h1>
      <p className="text-zinc-400 mb-4">
        Choose a habit to start tracking, and we&apos;ll place it in your world:
      </p>
      <ul className="space-y-3">
        {templates.map((t: any) => (
          <li key={t.slug}>
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="habit"
                value={t.slug}
                checked={selected === t.slug}
                onChange={() => setSelected(t.slug)}
                className="form-radio"
              />
              <span>
                {t.emoji} {t.title}
              </span>
            </label>
          </li>
        ))}
      </ul>
      <button
        onClick={handleAdd}
        disabled={!selected || !loc || status === 'saving'}
        className="mt-6 px-4 py-2 rounded bg-blue-600 hover:bg-blue-700 disabled:opacity-50"
      >
        {status === 'saving' ? 'Creating...' : '‚ûï Add to My World'}
      </button>
      {status === 'error' && (
        <p className="text-red-500 mt-4">Something went wrong. Please try again.</p>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState, useRef } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import Image from 'next/image';
import { createClient } from '@supabase/supabase-js';
import { generateBaseSlug } from '@/lib/slugHelpers';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

const copyToClipboard = (text: string) => {
  navigator.clipboard.writeText(text).then(() => {
    alert('Link copied to clipboard!');
  });
};

export default function StarterPage() {
  const router = useRouter();
  const searchParams = useSearchParams();

  const [bizName, setBizName] = useState('');
  const [location, setLocation] = useState('');
  const [slug, setSlug] = useState('');
  const [domain, setDomain] = useState('');
  const [template, setTemplate] = useState<any>(null);
  const [templateVersionId, setTemplateVersionId] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [slugAvailable, setSlugAvailable] = useState<boolean | null>(null);
  const [generatingSlug, setGeneratingSlug] = useState(false);
  const [typedDomain, setTypedDomain] = useState('');
  const bizNameRef = useRef<HTMLInputElement | null>(null);

  useEffect(() => {
    bizNameRef.current?.focus();
  }, []);

  useEffect(() => {
    const name = searchParams?.get('template') || '';
    if (!name) return;

    supabase
      .from('template_versions')
      .select('*')
      .eq('template_name', name)
      .order('created_at', { ascending: false })
      .limit(1)
      .then(({ data, error }) => {
        if (error || !data?.length) {
          console.error('Template version load error:', error);
          setTemplate(null);
          setTemplateVersionId(null);
        } else {
          setTemplate(data[0]);
          setTemplateVersionId(data[0].id);
        }
      });
  }, [searchParams]);

  useEffect(() => {
    if (!slug) return setSlugAvailable(null);

    const delay = setTimeout(async () => {
      const { data } = await supabase.from('sites').select('id').eq('slug', slug).maybeSingle();
      setSlugAvailable(!data);
    }, 400);

    return () => clearTimeout(delay);
  }, [slug]);

  useEffect(() => {
    if (!bizName.trim()) return;

    setGeneratingSlug(true);
    const baseSlug = generateBaseSlug(bizName, location);
    const fullDomain = `${baseSlug}.com`;

    const delay = setTimeout(() => {
      setSlug(baseSlug);
      setDomain(fullDomain);

      let i = 0;
      const typeInterval = setInterval(() => {
        setTypedDomain(fullDomain.slice(0, i + 1));
        i++;
        if (i === fullDomain.length) {
          clearInterval(typeInterval);
          setGeneratingSlug(false);
        }
      }, 50);
    }, 300);

    return () => clearTimeout(delay);
  }, [bizName, location]);

  const claimIt = async () => {
    if (!templateVersionId) {
      alert('Template failed to load. Please choose a different template or try again.');
      return;
    }

    if (slugAvailable === false) {
      alert('That slug is already taken. Please choose another.');
      return;
    }

    setLoading(true);

    try {
      const res = await fetch('/api/sites/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          template_version_id: templateVersionId,
          business_name: bizName,
          location,
          domain,
          slug,
        }),
      });

      if (!res.ok) {
        const errorText = await res.text();
        console.error('Create site error:', errorText);
        alert('Error creating site. Check console for details.');
        setLoading(false);
        return;
      }

      const site = await res.json();
      const slugToUse = site.slug ?? site.site_id;
      router.push(`/edit/${slugToUse}`);
    } catch (err) {
      console.error('Unexpected error in claimIt:', err);
      alert('Unexpected error. Please try again.');
      setLoading(false);
    }
