'use client';

import { useEffect, useMemo, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import { isAfter, isBefore, parseISO, subDays } from 'date-fns';
import { Button } from '@/components/ui';
import { CalendarIcon, DownloadIcon } from 'lucide-react';
import { DateRange } from 'react-day-picker';
import { Calendar } from '@/components/ui';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui';

interface UpgradeEvent {
  id: string;
  user_id: string | null;
  email: string | null;
  context: string | null;
  event_type: string;
  trigger_reason: string | null;
  created_at: string;
}

export default function GuestUpgradeDashboard() {
  const [events, setEvents] = useState<UpgradeEvent[]>([]);
  const [userLogs, setUserLogs] = useState<any[]>([]);
  const [contextFilter, setContextFilter] = useState<string | null>(null);
  const [dateRange, setDateRange] = useState<DateRange | undefined>({
    from: subDays(new Date(), 7),
    to: new Date(),
  });
  const [openStep, setOpenStep] = useState<string | null>(null);

  const supabase = createClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
  );

  const toggleStep = (key: string) => setOpenStep((prev) => (prev === key ? null : key));

  useEffect(() => {
    const loadEvents = async () => {
      const { data, error } = await supabase
        .from('guest_upgrade_events')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(1000);
      if (!error) setEvents(data);
    };
    const loadUserLogs = async () => {
      const { data, error } = await supabase
        .from('user_action_logs')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(1000);
      if (!error) setUserLogs(data);
    };
    loadEvents();
    loadUserLogs();
  }, []);

  const filtered = useMemo(() => {
    return events.filter((e) => {
      const date = parseISO(e.created_at);
      const inRange =
        (!dateRange?.from || isAfter(date, dateRange.from)) &&
        (!dateRange?.to || isBefore(date, dateRange.to));
      const inContext = !contextFilter || e.context === contextFilter;
      return inRange && inContext;
    });
  }, [events, contextFilter, dateRange]);

  const getKey = (e: { user_id: string | null; email?: string | null; id: string }) =>
    e.user_id || e.email || `anon-${e.id}`;
  const getLabel = (e: { user_id: string | null; email?: string | null }) =>
    e.email || e.user_id || 'anonymous';

  const funnelCounts = useMemo(() => {
    const views = new Set<string>();
    const clicks = new Set<string>();
    const signups = new Set<string>();
    const publishes = new Set<string>();

    for (const e of filtered) {
      const key = getKey(e);
      if (e.event_type === 'view') views.add(key);
      if (e.event_type === 'click') clicks.add(key);
    }

    for (const log of userLogs) {
      const logDate = parseISO(log.created_at);
      const inRange =
        (!dateRange?.from || isAfter(logDate, dateRange.from)) &&
        (!dateRange?.to || isBefore(logDate, dateRange.to));
      if (!log.user_id || !inRange) continue;
      if (log.event_type === 'signup_complete') signups.add(log.user_id);
      if (log.event_type === 'site_published') publishes.add(log.user_id);
    }

    return {
      views: views.size,
      clicks: clicks.size,
      signups: signups.size,
      publishes: publishes.size,
    };
  }, [filtered, userLogs, dateRange]);

  const funnelDetails = useMemo(() => {
    const views = new Map<string, string>();
    const clicks = new Map<string, string>();
    const signups = new Map<string, string>();
    const publishes = new Map<string, string>();

    for (const e of filtered) {
      const key = getKey(e);
      const label = getLabel(e);
      if (e.event_type === 'view') views.set(key, label);
      if (e.event_type === 'click') clicks.set(key, label);
    }

    for (const log of userLogs) {
      const logDate = parseISO(log.created_at);
      const inRange =
        (!dateRange?.from || isAfter(logDate, dateRange.from)) &&
        (!dateRange?.to || isBefore(logDate, dateRange.to));
      if (!log.user_id || !inRange) continue;
      if (log.event_type === 'signup_complete') signups.set(log.user_id, log.user_id);
      if (log.event_type === 'site_published') publishes.set(log.user_id, log.user_id);
    }

    return {
      views: [...views.entries()],
      clicks: [...clicks.entries()],
      signups: [...signups.entries()],
      publishes: [...publishes.entries()],
    };
  }, [filtered, userLogs, dateRange]);

  const exportFunnelCSV = () => {
    const rows = [
      ['Step', 'Count'],
      ['Modal Viewed', funnelCounts.views],
      ['Clicked Upgrade', funnelCounts.clicks],
      ['Signup Complete', funnelCounts.signups],
      ['Site Published', funnelCounts.publishes],
    ];
    const blob = new Blob([rows.map((r) => r.join(',')).join('\n')], {
      type: 'text/csv',
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'funnel_counts.csv';
    link.click();
  };

  return (
    <div className="max-w-4xl mx-auto p-4 space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold">Guest Upgrade Funnel</h1>
        <Popover>
          <PopoverTrigger asChild>
            <Button variant="outline" className="w-[280px] justify-start text-left font-normal">
              <CalendarIcon className="mr-2 h-4 w-4" />
              {dateRange?.from ? (
                dateRange.to ? (
                  <>
                    {dateRange.from.toLocaleDateString()} - {dateRange.to.toLocaleDateString()}
                  </>
                ) : (
                  dateRange.from.toLocaleDateString()
                )
              ) : (
                <span>Select date range</span>
              )}
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-auto p-0">
            <Calendar
              mode="range"
              selected={dateRange}
              onSelect={setDateRange}
              numberOfMonths={2}
            />
          </PopoverContent>
        </Popover>
      </div>

      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold flex items-center gap-2">
            üß≠ Funnel Drop-Off (unique users)
          </h2>
          <Button size="sm" variant="outline" onClick={exportFunnelCSV}>
            <DownloadIcon className="w-4 h-4 mr-2" /> Export Funnel
          </Button>
        </div>

        {(
          [
            {
              key: 'views',
              label: 'üëÅÔ∏è Modal Viewed',
              count: funnelCounts.views,
            },
            {
              key: 'clicks',
              label: 'üöÄ Clicked Upgrade',
              count: funnelCounts.clicks,
            },
            {
              key: 'signups',
              label: '‚úÖ Signup Complete',
              count: funnelCounts.signups,
            },
            {
              key: 'publishes',
              label: 'üì§ Site Published',
              count: funnelCounts.publishes,
            },
          ] as const
        ).map((step) => (
          <div
            key={step.key}
            className="w-full bg-muted rounded-md p-2 cursor-pointer"
            onClick={() => toggleStep(step.key)}
          >
            <div className="flex items-center justify-between">
              <span>{step.label}</span>
              <span className="font-bold">{step.count}</span>
            </div>
            {openStep === step.key && (
              <div className="mt-2 pl-2 text-sm text-muted-foreground space-y-1 max-h-40 overflow-y-auto">
                {(funnelDetails as any)[step.key]?.map(([id, label]: [string, string]) => (
                  <div key={id} className="truncate">
                    ‚Ä¢ {label}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

// admin/lib/fixInvalidBlocksInTemplate.ts
import { BlockSchema } from '@/admin/lib/zod/blockSchema';
import type { Template } from '@/types/template';
import type { Page } from '@/types/page';
import type { Block } from '@/types/blocks';

export function fixInvalidBlocksInTemplate(template: Template): Template {
  const fallback = {
    type: 'text',
    value: { value: 'Placeholder content (auto-fixed)' },
  };

  const fixedPages = template.data.pages.map((page) => {
    const fixedBlocks = page.content_blocks.map((block) => {
      const valid = BlockSchema.safeParse(block).success;
      return valid ? block : { ...fallback, id: crypto.randomUUID() };
    });
    return { ...page, content_blocks: fixedBlocks as Block[] };
  });

  return {
    ...template,
    data: { ...template.data, pages: fixedPages as unknown as Template['data']['pages'] },
  };
}

import Link from 'next/link';
import { useUrlBuilder } from './useUrlBuilder';
import { buildSafeLink } from '@/admin/lib/links/buildSafeLink';
import { linkThemeMap } from '@/admin/lib/links';
import type { ReactNode } from 'react';

type LinkProps = React.ComponentProps<typeof Link> & {
  theme?: keyof typeof linkThemeMap;
};

export function useLinkBuilder() {
  const { getTemplateUrl, getSnapshotUrl } = useUrlBuilder();

  const templateLink = (
    id: string,
    query?: Record<string, string | number | boolean>,
    children?: ReactNode,
    linkProps?: LinkProps
  ) => {
    const href = getTemplateUrl(id, query);
    if (!href) return null;

    const resolved = linkProps?.theme ? linkThemeMap[linkProps.theme] : linkThemeMap.primary;

    const { theme: _t, href: _h, prefetch: _p, target: _tg, ...rest } = linkProps || {};
    const target = _tg as '_blank' | '_self' | '_parent' | '_top' | undefined;
    return buildSafeLink(id, href.toString(), 'templateLink', children, {
      className: resolved,
      prefetch: _p ?? undefined,
      target,
      ...rest,
    });
  };

  const snapshotLink = (
    id: string,
    query?: Record<string, string | number | boolean>,
    children?: ReactNode,
    linkProps?: LinkProps
  ) => {
    const href = getSnapshotUrl(id, query);
    if (!href) return null;

    const resolved = linkProps?.theme ? linkThemeMap[linkProps.theme] : linkThemeMap.primary;

    const { theme: _t, href: _h, prefetch: _p, ...rest } = linkProps || {};
    return buildSafeLink(id, href.toString(), 'snapshotLink', children, {
      className: resolved,
      prefetch: _p ?? undefined,
      target: undefined as any,
      ...rest,
    });
  };

  return {
    templateLink,
    snapshotLink,
  };
}

import { supabase } from '@/admin/lib/supabaseClient';

export async function saveAsTemplate(template: any, type: 'template' | 'site'): Promise<string | null> {
  const newName = `Copy of ${template.template_name || 'untitled'}`;
  const { data, error } = await supabase
    .from('templates')
    .insert([
      {
        template_name: `${newName} (${type}) ${new Date().toISOString().replace('T', ' ').substring(0, 19)}`,
        slug: `${template.slug}-${type}-${new Date().toISOString().replace('T', '-').substring(0, 19).replace(/:/g, '')}`,
        layout: template.layout,
        color_scheme: template.color_scheme,
        industry: template.industry,
        data: template.data,
        is_site: type === 'site',
        published: false,
        domain: null,
        custom_domain: null,
      },
    ])
    .select()
    .single();

  if (error) {
    console.error('Failed to save copy:', error.message);
    return null;
  }

  return data?.id || null;
}

