import { z } from 'zod';

// Shared link schema
const LinkSchema = z.object({
  label: z.string().min(1, 'Label is required'),
  href: z.string().min(1, 'URL is required'),
});

// Step 1: Define content schema map w/ UI metadata
export const blockContentSchemaMap = {
  text: {
    label: 'Text Block',
    icon: 'üìù',
    schema: z.object({
      value: z.string().min(1, 'Text content is required'),
    }),
  },
  quote: {
    label: 'Quote',
    icon: '‚ùù',
    schema: z.object({
      text: z.string().min(1, 'Quote text is required'),
      attribution: z.string().optional(),
    }),
  },
  button: {
    label: 'Button',
    icon: 'üîò',
    schema: z.object({
      label: z.string().min(1, 'Button label is required'),
      href: z.string().url('Link must be a valid URL'),
      style: z.enum(['primary', 'secondary', 'ghost']).optional(),
    }),
  },
  cta: {
    label: 'Call to Action',
    icon: 'üöÄ',
    schema: z.object({
      label: z.string().min(1, 'CTA label is required'),
      link: z.string().min(1, 'CTA link is required'),
    }),
  },
  faq: {
    label: 'FAQ',
    icon: '‚ùì',
    schema: z.object({
      title: z.string().min(1, 'FAQ title is required'),
      items: z.array(z.object({ question: z.string().min(1, 'Question is required'), answer: z.string().min(1, 'Answer is required') })).min(1, 'At least one FAQ item is required'),
    }),
  },
  testimonial: {
    label: 'Testimonial',
    icon: 'üí¨',
    schema: z.object({
      testimonials: z.array(
        z.object({
          quote: z.string().min(1, 'Quote is required'),
          attribution: z.string().optional(),
          avatar_url: z.string().url().optional(),
          rating: z.number().min(1).max(5).optional(),
        })
      ).min(1, 'At least one testimonial is required'),
      randomized: z.boolean().optional(),
    }),
  },
  hero: {
    label: 'Hero',
    icon: 'üéØ',
    schema: z.object({
      headline: z.string().min(1, 'Headline is required'),
      subheadline: z.string().optional(),
      cta_text: z.string().optional(),
      cta_link: z.string().optional(),
      image_url: z.union([z.string().url(), z.literal('')]).optional(),
      layout_mode: z.enum(['inline', 'background', 'full_bleed']).optional(),
      blur_amount: z.number().min(0).max(100).optional(),
      parallax_enabled: z.boolean().optional(),
      image_position: z.enum(['top', 'center', 'bottom']).optional(),
      image_x: z.number().min(0).max(100).optional(),
      image_y: z.number().min(0).max(100).optional(),
    }),
  },
  services: {
    label: 'Services',
    icon: 'üß∞',
    schema: z.object({
      items: z.array(z.string()).min(1, 'At least one service is required'),
    }),
  },
  audio: {
    label: 'Audio',
    icon: 'üéß',
    schema: z.object({
      provider: z.enum(['spotify', 'soundcloud', 'suno']),
      url: z.string().url('Audio URL must be valid'),
      title: z.string().optional(),
    }),
  },
  video: {
    label: 'Video',
    icon: 'üìπ',
    schema: z.object({
      url: z.string().url('Video URL must be valid'),
      caption: z.string().optional(),
    }),
  },
  footer: {
    label: 'Footer',
    icon: 'üè†',
    schema: z.object({
      businessName: z.string(),
      address: z.string(),
      cityState: z.string(),
      phone: z.string(),
      links: z.array(LinkSchema),
      logo_url: z.string().optional(),
      social_links: z
        .array(z.object({ platform: z.string(), url: z.string() }))
        .optional(),
      copyright: z.string().optional(),
    }),
  },
  header: {
    label: 'Header',
    icon: 'üè†',
    schema: z.object({
      logoUrl: z.string().optional(),
      navItems: z.array(LinkSchema),
    }),
  },
  service_areas: {
    label: 'Service Areas',
    icon: 'üåç',
    schema: z.object({
      cities: z.array(z.string()).min(1, 'At least one city is required'),
    }),
  },
  contact_form: {
    label: 'Contact Form',
    icon: 'üìß',
    schema: z.object({
      title: z.string().min(1, 'Title is required'),
      notification_email: z.string().email('Invalid email address'),
    }),
  },
};

// Step 2: Build base block schemas with shared fields
export function createBlockUnion<
  T extends Record<string, { label: string; icon: string; schema: z.ZodTypeAny }>
>(map: T) {
  const schemas: z.ZodDiscriminatedUnionOption<'type'>[] = [];
  const meta: Record<keyof T, { label: string; icon: string }> = {} as any;

  for (const [type, config] of Object.entries(map)) {
    schemas.push(
      z.object({
        type: z.literal(type),
        content: config.schema,
        _id: z.string().optional(),
        tone: z.string().optional(),
        industry: z.string().optional(),
        tags: z.array(z.string()).optional(),
        meta: z.record(z.any()).optional(),
      }) as z.ZodDiscriminatedUnionOption<'type'>
    );
    meta[type as keyof T] = {
      label: config.label,
      icon: config.icon,
    };
  }

  return { schemas, meta };
}

const { schemas: BasicBlockSchemas, meta: blockMeta } = createBlockUnion(blockContentSchemaMap);

// Step 3: Final block schema (with grid + recursion)
export const BlockSchema: z.ZodTypeAny = z.lazy(() => {
  const GridBlockSchema: z.ZodDiscriminatedUnionOption<'type'> = z.object({
    type: z.literal('grid'),
    content: z.object({
      columns: z.number().min(1).max(12),
      items: z.array(BlockSchema),
    }),
    _id: z.string().optional(),
    tone: z.string().optional(),
    industry: z.string().optional(),
    tags: z.array(z.string()).optional(),
    meta: z.record(z.any()).optional(),
  }) as z.ZodDiscriminatedUnionOption<'type'>;

  return z.discriminatedUnion('type', [
    ...BasicBlockSchemas as unknown as [z.ZodDiscriminatedUnionOption<'type'>, ...z.ZodDiscriminatedUnionOption<'type'>[]],
    GridBlockSchema,
  ]);
});

// Step 4: Export helpers and types
export const BlocksArraySchema = z.array(BlockSchema);
export type Block = z.infer<typeof BlockSchema>;

export function isValidBlock(data: unknown): data is Block {
  return BlockSchema.safeParse(data).success;
}

export function migrateLegacyBlock(block: any): any {
  if ('content' in block) return block;
  if ('value' in block) return { ...block, content: block.value };
  return block;
}

export const blockPreviewFallback: Record<Block['type'], string> = Object.entries(
  blockMeta as Record<Block['type'], { label: string; icon: string }>
).reduce((acc, [key, val]) => {
  acc[key as Block['type']] = `${val.icon} ${val.label}`;
  return acc;
}, {} as Record<Block['type'], string>);

export { blockMeta };

import { z } from 'zod';
import { BlockSchema } from './blockSchema';

// Blocks
export type ValidatedBlock = z.infer<typeof BlockSchema>;

// Pages
export const PageSchema = z.object({
  id: z.string(),
  title: z.string(),
  slug: z.string(),
  content_blocks: z.array(BlockSchema),
  show_header: z.boolean().optional().default(true),
  show_footer: z.boolean().optional().default(true),
});
export type ValidatedPage = z.infer<typeof PageSchema>;

// TemplateData
export const TemplateDataSchema = z.object({
  pages: z.array(PageSchema),
});
export type ValidatedTemplateData = z.infer<typeof TemplateDataSchema>;

// Full Template
export const TemplateSaveSchema = z.object({
  id: z.string().optional(),
  slug: z
    .string()
    .min(1, 'Slug is required')
    .regex(/^[a-z0-9]+(?:-[a-z0-9]+)*$/, {
      message: 'Slug must be lowercase, alphanumeric, and hyphen-separated',
    }),
  site_id: z.string().optional(),
  template_name: z.string(),
  layout: z.string(),
  color_scheme: z.string(),
  industry: z.string(),
  theme: z.string(),
  is_site: z.boolean().optional(),
  published: z.boolean().optional(),
  headline: z.string().optional(),
  description: z.string().optional(),
  hero_url: z.string().nullable().optional(),
  banner_url: z.string().nullable().optional(),
  logo_url: z.string().nullable().optional(),
  team_url: z.string().nullable().optional(),

  // üÜï Global blocks
  headerBlock: BlockSchema.optional().nullable(),
  footerBlock: BlockSchema.optional().nullable(),

  data: TemplateDataSchema,
  updated_at: z.string().optional(),
  verified: z.boolean().optional(),
  meta: z.object({
    title: z.string().optional(),
    description: z.string().optional(),
    ogImage: z.string().optional(),
    faviconSizes: z.string().optional(),
    appleIcons: z.string().optional(),
  }).optional(),
});

export type ValidatedTemplate = z.infer<typeof TemplateSaveSchema>;

'use client';

import { useEffect, useState } from 'react';
import { Button } from '@/components/ui';

export default function PrebuildOGPage() {
  const [slugs, setSlugs] = useState<string[]>([]);
  const [results, setResults] = useState<Record<string, 'pending' | 'success' | 'error'>>({});
  const [loading, setLoading] = useState(false);
  const [totalMB, setTotalMB] = useState<string>('0.0');

  const fetchSlugs = async () => {
    const res = await fetch('/api/compare-slugs');
    const data = await res.json();
    setSlugs(data.slugs);
    setResults(Object.fromEntries(data.slugs.map((slug: string) => [slug, 'pending'])));
  };

  const fetchStorageInfo = async () => {
    const res = await fetch('/api/list-og-zips');
    const data = await res.json();
    setTotalMB(data.totalMB || '0.0');
  };

  const triggerBuilds = async () => {
    setLoading(true);
    for (const slug of slugs) {
      try {
        const res = await fetch(`/og/compare/${slug}`);
        if (res.ok) {
          setResults((prev) => ({ ...prev, [slug]: 'success' }));
        } else {
          setResults((prev) => ({ ...prev, [slug]: 'error' }));
        }
      } catch {
        setResults((prev) => ({ ...prev, [slug]: 'error' }));
      }
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchSlugs();
    fetchStorageInfo();
  }, []);

  const overQuota = parseFloat(totalMB) > 100;

  return (
    <main className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="flex justify-between items-center">
        <div className="flex flex-col gap-2">
          <h1 className="text-2xl font-bold">
            Prebuild OG Images{' '}
            {overQuota ? (
              <span className="text-sm text-red-500 ml-2">({totalMB} MB used ‚Äì over quota)</span>
            ) : (
              <span className="text-sm text-muted-foreground ml-2">({totalMB} MB used)</span>
            )}
          </h1>
          <div className="w-full h-3 bg-gray-200 rounded overflow-hidden">
            <div
              className={`h-full ${overQuota ? 'bg-red-500' : 'bg-blue-500'}`}
              style={{ width: `${Math.min(100, parseFloat(totalMB))}%` }}
            />
          </div>
        </div>
        <Button
          variant="outline"
          onClick={() => {
            const zipUrl = `/api/download-og-zip?slugs=${encodeURIComponent(
              slugs.filter((s) => results[s] === 'success').join(',')
            )}`;
            window.open(zipUrl, '_blank');
          }}
          disabled={!slugs.some((s) => results[s] === 'success')}
        >
          Download All as ZIP
        </Button>
      </div>

      {overQuota && (
        <div className="text-sm text-yellow-600 bg-yellow-100 border border-yellow-300 rounded px-4 py-2">
          Storage limit exceeded. Please delete old exports before generating new ones.
        </div>
      )}

      <Button onClick={triggerBuilds} disabled={loading || slugs.length === 0 || overQuota}>
        {loading ? 'Generating...' : 'Start Prebuild'}
      </Button>

      <ul className="text-sm space-y-4">
        {slugs.map((slug) => (
          <li key={slug} className="border p-4 rounded shadow-sm">
            <div className="flex justify-between mb-2">
              <span className="font-mono text-sm">{slug}</span>
              <span
                className={
                  results[slug] === 'success'
                    ? 'text-green-500'
                    : results[slug] === 'error'
                      ? 'text-red-500'
                      : 'text-muted-foreground'
                }
              >
                {results[slug]}
              </span>
            </div>
            {results[slug] === 'success' && (
              <img
                src={`/og/compare/${slug}`}
                alt={`OG image for ${slug}`}
                className="w-full border rounded"
              />
            )}
          </li>
        ))}
      </ul>
    </main>
  );
}

