// app/_sites/[slug]/page.tsx
'use server';

import { getSupabase } from '@/lib/supabase/server';
import { notFound } from 'next/navigation';
import type { TemplateData, Template } from '@/types/template';
import RenderBlock from '@/components/admin/templates/render-block';
import { Metadata } from 'next';
import MetaHead from '@/components/head/MetaHead';

export const generateMetadata = async ({ params }: { params: { slug: string; page: string } }): Promise<Metadata> => {
  const { slug, page } = params;
  const supabase = await getSupabase();
  const { data: site } = await supabase
    .from('templates')
    .select('data, template_name, description, slug')
    .eq('slug', slug)
    .eq('is_site', true)
    .maybeSingle();

  const pages = (site?.data as TemplateData)?.pages || [];
  const currentPage = pages.find((p) => p.slug === page);

  return {
    title: `${currentPage?.title || site?.template_name} | QuickSites`,
    description: currentPage?.meta?.description || site?.description || 'A professional QuickSites website.',
    openGraph: {
      title: currentPage?.title || site?.template_name,
      description: currentPage?.meta?.description || site?.description,
      url: `https://quicksites.ai/${slug}/${page}`,
    },
  };
};

export default async function SitePage({ params }: { params: { slug: string; page: string } }) {
  const { slug, page } = await Promise.resolve(params);
  const supabase = await getSupabase();

  const { data: site, error } = await supabase
    .from('templates')
    .select('*')
    .eq('slug', slug)
    .eq('is_site', true)
    .maybeSingle();

  if (!site || error) return notFound();

  const pages = (site.data as TemplateData)?.pages || [];
  const currentPage = pages.find((p) => p.slug === page);

  if (!currentPage) return notFound();

  // üß† Use template-level fallback for header/footer visibility
  const showHeader = currentPage.show_header ?? site.show_header ?? true;
  const showFooter = currentPage.show_footer ?? site.show_footer ?? true;

  return (
    <>
      <MetaHead
        title={currentPage.meta?.title || site.template_name || ''}
        description={currentPage.meta?.description || site.description || ''}
        ogImage={currentPage.meta?.ogImage || site.logo_url || 'https://quicksites.ai/og-cache/og-default.png'}
        faviconSizes={currentPage.meta?.faviconSizes || site.logo_url || 'https://quicksites.ai/og-cache/og-default.png'}
        appleIcons={currentPage.meta?.appleIcons || site.logo_url || 'https://quicksites.ai/og-cache/og-default.png'}
      />

      <div className="bg-black text-white min-h-screen">
        {showHeader && site.headerBlock && (
          <div className="mb-8">
            <RenderBlock block={site.headerBlock} />
          </div>
        )}

        <main className="py-8 space-y-8">
          {currentPage.content_blocks?.map((block, i) => (
            <div
              key={block._id || i}
              className={
                block.type === 'hero' && block.content?.layout_mode === 'full_bleed'
                  ? ''
                  : 'px-4 max-w-5xl mx-auto'
              }
            >
              <RenderBlock block={block} />
            </div>
          ))}
        </main>

        {showFooter && site.footerBlock && (
          <div className="mt-12 border-t border-white/10">
            <RenderBlock block={site.footerBlock} />
          </div>
        )}
      </div>
    </>
  );
}

import { GetServerSideProps } from 'next';
import { createClient } from '@supabase/supabase-js';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  Legend,
} from 'recharts';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export default function SharedAnalytics({ domain, views }: { domain: string; views: any[] }) {
  const byDay: Record<string, number> = {};
  views.forEach((v) => {
    const day = new Date(v.viewed_at).toISOString().slice(0, 10);
    byDay[day] = (byDay[day] || 0) + 1;
  });

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-6">
      <h1 className="text-2xl font-bold">Analytics for {domain}</h1>

      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={async () => {
            const QRCode = await import('qrcode');
            const url = window.location.href;
            QRCode.toDataURL(url).then((dataUrl: string) => {
              const win = window.open();
              if (win) win.document.write(`<img src='${dataUrl}' /><p>${url}</p>`);
            });
          }}
          className="text-sm text-blue-600 underline"
        >
          üì∏ Generate QR Code
        </button>

        <form
          onSubmit={async (e) => {
            e.preventDefault();
            const email = (
              (e.target as HTMLFormElement).querySelector('input[type=email]') as HTMLInputElement
            )?.value;
            if (email) {
              await fetch('/api/subscribe', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ domain, email }),
              });
              alert('Subscribed to updates for ' + domain);
            }
          }}
          className="flex gap-2 items-center"
        >
          <input
            type="email"
            required
            placeholder="you@example.com"
            className="border rounded px-2 py-1 text-sm"
          />
          <button type="submit" className="text-sm text-green-600 underline">
            üì¨ Subscribe to reports
          </button>
        </form>
      </div>

      <section>
        <h2 className="text-lg font-semibold mb-2">Views per Day</h2>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={Object.entries(byDay).map(([day, count]) => ({ day, count }))}>
            <XAxis dataKey="day" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="count" fill="#6366f1" />
          </BarChart>
        </ResponsiveContainer>
      </section>

      <p className="text-sm text-muted-foreground">Total views: {views.length}</p>
    </div>
  );
}

'use client';

import { useRouter, useSearchParams } from 'next/navigation';
import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import QRCode from 'qrcode';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export default function BrandingProfilePage() {
  const searchParams = useSearchParams();
  const id = searchParams?.get('id') as string;
  const [profile, setProfile] = useState<any>(null);
  const [qr, setQr] = useState<string | null>(null);
  const [accessOk, setAccessOk] = useState(false);
  const [passwordAttempt, setPasswordAttempt] = useState('');

  useEffect(() => {
    if (!id || typeof id !== 'string') return;

    supabase
      .from('branding_profiles')
      .select('*')
      .eq('id', id)
      .single()
      .then(({ data }) => {
        if (data) {
          const accessParam = new URLSearchParams(window.location.search).get('access');
          if (!data.access_token && !data.password) {
            setAccessOk(true);
          } else if (data.access_token && accessParam === data.access_token) {
            setAccessOk(true);
          } else {
            setAccessOk(false);
          }
          setProfile(data);
          const previewUrl = `${window.location.origin}/api/og/snapshot?theme=${data.theme}&brand=${data.brand}`;
          QRCode.toDataURL(previewUrl).then(setQr);
        }
      });
  }, [id]);

  if (!profile) return <p className="p-6 text-sm text-muted-foreground">Loading profile...</p>;

  if (!accessOk && profile?.password) {
    return (
      <div className="p-6">
        <h2 className="text-lg font-semibold mb-2">Password Required</h2>
        <input
          className="border px-2 py-1 rounded mr-2"
          placeholder="Enter password"
          value={passwordAttempt}
          onChange={(e) => setPasswordAttempt(e.target.value)}
        />
        <button
          className="bg-black text-white px-3 py-1 rounded"
          onClick={() => {
            if (passwordAttempt === profile.password) setAccessOk(true);
            else alert('Incorrect password');
          }}
        >
          Submit
        </button>
      </div>
    );
  }

  const previewUrl = `/api/og/snapshot?theme=${profile.theme}&brand=${profile.brand}`;

  async function downloadPDF() {
    const html2pdf = (await import('html2pdf.js')).default;

    const element = document.getElementById('branding-preview-pdf');
    if (element) {
      html2pdf()
        .set({
          filename: `${profile.name}-preview.pdf`,
          margin: 10,
          html2canvas: { scale: 2 },
          jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' },
        })
        .from(element)
        .save();
    }
  }

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-6">
      <div id="branding-preview-pdf" className="space-y-6">
        <h1 className="text-2xl font-bold">{profile.name}</h1>

        <div className="flex items-center gap-4">
          <span className="text-sm font-medium">Theme:</span>
          <span className="px-2 py-1 rounded bg-gray-200">{profile.theme}</span>

          <span className="text-sm font-medium">Brand:</span>
          <span className="px-2 py-1 rounded bg-gray-200">{profile.brand}</span>

          {profile.accent_color && (
            <span
              className="w-5 h-5 rounded-full border"
              style={{ backgroundColor: profile.accent_color }}
            />
          )}

          {profile.logo_url && (
            <img src={profile.logo_url} alt="Logo" className="w-8 h-8 rounded-full" />
          )}
        </div>

        <div>
          <p className="text-sm text-muted-foreground mb-1">OG Preview:</p>
          <img
            src={previewUrl}
            alt="OG Preview"
            className="w-full max-w-xl border shadow rounded"
          />
        </div>

        {qr && (
          <div>
            <p className="text-sm text-muted-foreground mb-1">QR Code:</p>
            <img src={qr} alt="QR Code" className="w-32 h-32 border rounded" />
          </div>
        )}

        <div>
          <p className="text-sm">Link:</p>
          <code className="text-xs bg-gray-100 p-2 rounded block">{previewUrl}</code>
        </div>
      </div>

      <button className="bg-blue-600 text-white px-4 py-2 rounded mt-4" onClick={downloadPDF}>
        Download PDF Preview
      </button>
    </div>
  );
}

import { useState } from 'react';
