import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import Head from 'next/head';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

function getFlagEmoji(countryCode: string) {
  if (!countryCode || countryCode.length !== 2) return 'üåê';
  return countryCode
    .toUpperCase()
    .replace(/./g, (char) => String.fromCodePoint(127397 + char.charCodeAt(0)));
}

export default function EmbedViewsDashboard() {
  const [views, setViews] = useState<any[]>([]);
  const [locationTotals, setLocationTotals] = useState<Record<string, number>>({});
  const [startDate, setStartDate] = useState<string>('');
  const [endDate, setEndDate] = useState<string>('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const recentCounts = views.reduce(
      (acc, v) => {
        const now = new Date();
        const ts = new Date(v.created_at);
        const deltaHours = (now.getTime() - ts.getTime()) / 36e5;
        if (deltaHours <= 24) {
          acc[v.schema_id] = (acc[v.schema_id] || 0) + 1;
        }
        return acc;
      },
      {} as Record<string, number>
    );
    for (const [schema_id, count] of Object.entries(recentCounts)) {
      if ((count as number) >= 100) {
        fetch('/api/slack/embed-alert', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ schema_id, count }),
        });
      }
    }
    supabase
      .from('embed_views')
      .select('*')
      .order('created_at', { ascending: false })
      .then(({ data }) => {
        setViews(data || []);
        const locs: Record<string, number> = {};
        (data || []).forEach((v) => {
          const loc = v.location || 'Unknown';
          locs[loc] = (locs[loc] || 0) + 1;
        });
        setLocationTotals(locs);
        setLoading(false);
      });
  }, []);

  return (
    <>
      <Head>
        <title>Embed View Logs</title>
      </Head>
      <div className="max-w-4xl mx-auto p-6">
        <div className="flex gap-4 mb-4">
          <label className="text-sm text-gray-500">Date Range:</label>
          <input
            type="date"
            className="border px-2 py-1 rounded text-sm"
            onChange={(e) => setStartDate(e.target.value)}
          />
          <span className="text-gray-500">to</span>
          <input
            type="date"
            className="border px-2 py-1 rounded text-sm"
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>
        <div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-4 mb-4">
          <input
            type="text"
            placeholder="Filter by schema ID, referrer, or date..."
            onChange={(e) => {
              const val = e.target.value.toLowerCase();
              setViews((prev) =>
                prev.filter(
                  (v) =>
                    v.schema_id.toLowerCase().includes(val) ||
                    (v.referrer || '').toLowerCase().includes(val) ||
                    new Date(v.created_at).toLocaleString().toLowerCase().includes(val)
                )
              );
            }}
            className="border text-sm px-2 py-1 rounded w-full md:max-w-xs"
          />
          <button
            onClick={() => {
              const csv = [
                ['schema_id', 'referrer', 'created_at'],
                ...views.map((v) => [v.schema_id, v.referrer || '', v.created_at]),
              ]
                .map((r) => r.join(','))
                .join('\n');
              const blob = new Blob([csv], { type: 'text/csv' });
              const url = URL.createObjectURL(blob);
              const a = document.createElement('a');
              a.href = url;
              a.download = 'embed-views.csv';
              a.click();
              URL.revokeObjectURL(url);
            }}
            className="bg-blue-700 text-white text-sm px-4 py-1 rounded"
          >
            Export CSV
          </button>
        </div>
        <h1 className="text-xl font-bold mb-4">üìä Embed View Logs</h1>
        <div className="w-full h-64 mb-6">
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              data={views.reduce(
                (acc, v) => {
                  const day = new Date(v.created_at).toISOString().slice(0, 10);
                  const ref = v.referrer || 'Other';
                  const key = `${day}|${ref}`;
                  const existing = acc.find((d: { key: string }) => d.key === key);
                  if (existing) existing.count++;
                  else acc.push({ date: day, referrer: ref, count: 1, key });
                  return acc;
                },
                [] as {
                  date: string;
                  referrer: string;
                  count: number;
                  key: string;
                }[]
              )}
            >
              <XAxis dataKey="date" interval="preserveStartEnd" fontSize={12} />
              <YAxis allowDecimals={false} fontSize={12} />
              <Tooltip />
              {[...new Set(views.map((v) => v.referrer || 'Other'))].map((ref, i) => (
                <Bar
                  key={ref}
                  dataKey={(entry) => (entry.referrer === ref ? entry.count : 0)}
                  name={ref}
                  stackId="a"
                  fill={`hsl(${(i * 70) % 360}, 60%, 60%)`}
                />
              ))}
            </BarChart>
          </ResponsiveContainer>
        </div>
        <div className="mb-4">
          <label className="text-sm text-gray-600">Filter by country:</label>
          <select
            onChange={(e) => {
              const c = e.target.value;
              if (c === '') return;
              setViews((prev) => prev.filter((v) => v.country_code === c));
            }}
            className="border px-2 py-1 text-sm rounded w-full md:max-w-sm"
          >
            <option value="">-- All Countries --</option>
            {[...new Set(views.map((v) => v.country_code).filter(Boolean))].map((code) => (
              <option key={code} value={code}>
                {code}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="text-sm text-gray-600">Filter by schema ID:</label>
          <select
            onChange={(e) => {
              const id = e.target.value;
              if (id === '') return;
              setViews((prev) => prev.filter((v) => v.schema_id === id));
            }}
            className="border px-2 py-1 text-sm rounded w-full md:max-w-sm"
          >
            <option value="">-- All Schemas --</option>
            {[...new Set(views.map((v) => v.schema_id))].map((id) => (
              <option key={id} value={id}>
                {id}
              </option>
            ))}
          </select>
        </div>
        <input
          type="text"
          placeholder="Filter by schema ID, referrer, or date..."
          onChange={(e) => {
            const val = e.target.value.toLowerCase();
            setViews((prev) =>
              prev.filter(
                (v) =>
                  v.schema_id.toLowerCase().includes(val) ||
                  (v.referrer || '').toLowerCase().includes(val) ||
                  new Date(v.created_at).toLocaleString().toLowerCase().includes(val)
              )
            );
          }}
          className="border text-sm px-2 py-1 rounded w-full mb-4"
        />
        {loading && <p className="text-gray-400">Loading...</p>}
        {!loading && views.length === 0 && <p>No embed views recorded.</p>}
        <table className="w-full text-sm border mt-4">
          <thead>
            <tr className="bg-gray-100 text-left">
              <th className="p-2">Schema ID</th>
              <th className="p-2">Country</th>
              <th className="p-2">Region</th>
              <th className="p-2">Schema ID</th>
              <th className="p-2">Referrer</th>
              <th className="p-2">Date</th>
            </tr>
          </thead>
          <tbody>
            {views
              .filter((v) => {
                const created = new Date(v.created_at);
                const start = startDate ? new Date(startDate) : null;
                const end = endDate ? new Date(endDate + 'T23:59:59') : null;
                return (!start || created >= start) && (!end || created <= end);
              })
              .map((v) => (
                <tr key={v.id} className="border-t">
                  <td className="p-2 text-blue-600">
                    <a
                      href={`/admin/zod-playground?schema_id=${v.schema_id}`}
                      className="underline"
                    >
                      {v.schema_id}
                    </a>
                  </td>
                  <td className="p-2 text-gray-600">{v.country_code || '‚Äî'}</td>
                  <td className="p-2 text-gray-600">{v.region || '‚Äî'}</td>
                  <td className="p-2 text-gray-600">{v.referrer || '‚Äî'}</td>
                  <td className="p-2 text-gray-500">{new Date(v.created_at).toLocaleString()}</td>
                </tr>
              ))}
          </tbody>
        </table>

        <div className="mt-8">
          <h2 className="text-lg font-semibold mb-2">üåç Views by Location</h2>
          <ul className="text-sm text-gray-600 list-disc pl-5">
            {Object.entries(locationTotals).map(([loc, count]) => (
              <li key={loc}>
                {loc}: {count}
              </li>
            ))}
          </ul>
        </div>
        <div className="mt-8">
          <h2 className="text-lg font-semibold mb-2">üåê Views by Country</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4 text-sm text-gray-600">
            {Object.entries(locationTotals).map(([loc, count]) => (
              <div key={loc} className="flex items-center gap-2">
                <span className="text-xl">{getFlagEmoji(loc)}</span>
                <span>
                  {loc}: {count}
                </span>
              </div>
            ))}
          </div>
        </div>

        <button
          onClick={() => {
            const csv = [
              ['schema_id', 'referrer', 'created_at'],
              ...views.map((v) => [v.schema_id, v.referrer || '', v.created_at]),
            ]
              .map((r) => r.join(','))
              .join('\n');
            const blob = new Blob([csv], { type: 'text/csv' });
            const url = URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = 'embed-views.csv';
            a.click();
            URL.revokeObjectURL(url);
          }}
          className="mt-6 bg-blue-700 text-white px-4 py-1 rounded"
        >
          Export CSV
        </button>
      </div>
    </>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useCurrentUser } from '@/hooks/useCurrentUser';
import { Bar, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { supabase } from '@/admin/lib/supabaseClient';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function AdminLogsPage() {
  const { user } = useCurrentUser();
  const [logs, setLogs] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [filterType, setFilterType] = useState('');
  const [filterUser, setFilterUser] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const isAdmin = user?.role === 'admin';

  useEffect(() => {
    if (!isAdmin) return;
    const fetchLogs = async () => {
      let query = supabase
        .from('site_events')
        .select('*')
        .order('created_at', { ascending: false });
      if (filterType) query = query.eq('type', filterType);
      if (filterUser) query = query.ilike('payload->>user_email', `%${filterUser}%`);
      if (startDate) query = query.gte('created_at', startDate);
      if (endDate) query = query.lte('created_at', endDate + 'T23:59:59Z');
      const { data, error } = await query;
      if (error) console.error(error);
      else setLogs(data || []);
      setLoading(false);
    };
    fetchLogs();
  }, [filterType, filterUser, startDate, endDate, isAdmin]);

  const exportCSV = () => {
    const rows = logs.map((log) => ({
      created_at: log.created_at,
      type: log.type,
      user_email: log.payload?.user_email || '',
      payload: JSON.stringify(log.payload),
    }));
    const csv =
      'created_at,type,user_email,payload\n' +
      rows
        .map((r) => `${r.created_at},${r.type},${r.user_email},"${r.payload.replace(/"/g, '""')}"`)
        .join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'logs.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const downloadPayload = (payload: any, id: string) => {
    const blob = new Blob([JSON.stringify(payload, null, 2)], {
      type: 'application/json',
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `payload-${id}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const chartData = (() => {
    const counts: Record<string, number> = {};
    logs.forEach((log) => {
      const date = new Date(log.created_at).toISOString().split('T')[0];
      counts[date] = (counts[date] || 0) + 1;
    });
    const labels = Object.keys(counts).sort();
    return {
      labels,
      datasets: [
        {
          label: 'Logs per Day',
          data: labels.map((label) => counts[label]),
          backgroundColor: 'rgba(59, 130, 246, 0.7)',
        },
      ],
    };
  })();

  const exportChartImage = (canvasId: string, filename: string) => {
    const canvas = document.getElementById(canvasId) as HTMLCanvasElement;
    if (!canvas) return;
    const url = canvas.toDataURL('image/png');
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    a.click();
  };

  const exportWeeklySummaryPDF = async () => {
    const html2pdf = (await import('html2pdf.js')).default;
    const element = document.getElementById('weekly-summary-container');
    if (!element) return;
    html2pdf()
      .set({
        margin: 0.5,
        filename: 'weekly-summary.pdf',
        html2canvas: { scale: 2 },
        jsPDF: { unit: 'in', format: 'letter', orientation: 'portrait' },
      })
      .from(element)
      .save();
  };

  const emailWeeklySummary = async () => {
    const session = await supabase.auth.getSession();
    const accessToken = session.data.session?.access_token;
    const userEmail = session.data.session?.user?.user_metadata?.email;

    const res = await fetch('https://<your-project>.functions.supabase.co/email-weekly-summary', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({ summary: chartData, userEmail }),
    });

    const result = await res.json();
    alert(result.message || 'Email request sent');
  };

  if (!user?.id) return <p className="p-6 text-center">üîí You must be signed in to view logs.</p>;
  if (!isAdmin)
    return <p className="p-6 text-center text-red-600">‚õî Access denied. Admins only.</p>;

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold">üìú Deployment Logs</h1>
        <button onClick={exportCSV} className="bg-gray-800 text-white px-4 py-2 rounded text-sm">
          Export CSV
        </button>
      </div>

      <div className="flex gap-4">
        <button onClick={exportCSV} className="text-sm text-green-700 underline">
          Export CSV
        </button>
        <button
          onClick={() => exportChartImage('weekly-summary-canvas', 'weekly-summary.png')}
          className="text-sm text-blue-600 underline"
        >
          Export Chart as Image
        </button>
        <button onClick={exportWeeklySummaryPDF} className="text-sm text-purple-700 underline">
          Download Weekly PDF
        </button>
        <button onClick={emailWeeklySummary} className="text-sm text-amber-700 underline">
          Email Summary
        </button>
      </div>

      <div id="weekly-summary-container" className="bg-white p-4 border rounded shadow space-y-4">
        <h2 className="text-lg font-semibold">üìä Log Breakdown</h2>
        <Bar
          id="weekly-summary-canvas"
          data={chartData}
          options={{
            responsive: true,
            plugins: { legend: { position: 'top' } },
          }}
        />
        <h2 className="text-lg font-semibold">üìà Total Logs Over Time</h2>
        <Line
          data={chartData}
          options={{
            responsive: true,
            plugins: { legend: { position: 'top' } },
          }}
        />
      </div>

      {loading && <p>Loading...</p>}
      {!loading && logs.length === 0 && <p>No logs found.</p>}
      <div className="space-y-4">
        {logs.map((log) => (
          <div key={log.id} className="bg-white p-4 border rounded shadow text-sm">
            <p className="text-gray-500">üïí {new Date(log.created_at).toLocaleString()}</p>
            <p className="font-semibold text-blue-700">{log.type}</p>
            <p className="text-xs text-gray-600">üë§ {log.payload?.user_email || 'N/A'}</p>
            <pre className="overflow-auto max-h-64 bg-gray-100 mt-2 p-2 rounded">
              {JSON.stringify(log.payload, null, 2)}
            </pre>
            <button
              onClick={() => downloadPayload(log.payload, log.id)}
              className="mt-2 text-xs text-blue-600 underline"
            >
              Download Payload JSON
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

// pages/admin/param-lab.tsx
import { useSearchParams } from 'next/navigation';
import { useEffect, useState } from 'react';
import QRCode from 'react-qr-code';
import { createClient } from '@supabase/supabase-js';

import { queryParamSchemas } from '@/admin/lib/queryParamSchemas';
import { extractTags, summarizeQuery } from '@/admin/lib/queryUtils';
import QueryParamEditor from '@/components/admin/query-param-editor';
import TagFilterControls from '@/components/admin/tag-filter-controls';
import { useCurrentUser } from '@/hooks/useCurrentUser';
// import PresetCard from '@/components/admin/preset-card';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export default function ParamLabPage() {
  const searchParams = useSearchParams();
  const { user } = useCurrentUser();
  const slug = (searchParams?.get('slug') || 'campaign') as string;
  const schema = queryParamSchemas[slug];

  const [paramsString, setParamsString] = useState('');
  const [shareUrl, setShareUrl] = useState('');
  const [presets, setPresets] = useState<Record<string, string>>({});
  const [presetName, setPresetName] = useState('');
  const [tagFilter, setTagFilter] = useState<string[]>(() => {
    if (typeof window !== 'undefined') {
      const url = new URL(window.location.href);
      const param = url.searchParams.get('tags');
      return param
        ? param
            .split(',')
            .map((t) => t.trim().toLowerCase())
            .filter(Boolean)
        : [];
    }
    return [];
  });
  const [tagMatchMode, setTagMatchMode] = useState<'any' | 'all'>(() => {
    if (typeof window !== 'undefined') {
      const url = new URL(window.location.href);
      const mode = url.searchParams.get('tagMode');
      return mode === 'any' ? 'any' : 'all';
    }
    return 'all';
  });

  const updateShareInfo = () => {
    const url = new URL(window.location.href);
    const paramEntries = Array.from(url.searchParams.entries()).filter(([k]) => k !== 'slug');
    const query = paramEntries.map(([k, v]) => `${k}=${encodeURIComponent(v)}`).join('&');
    setParamsString(query);
    setShareUrl(window.location.href);
  };

  useEffect(() => {
    updateShareInfo();
  }, [slug, searchParams]);

  useEffect(() => {
    if (!user?.id) return;
    supabase
      .from('param_presets')
      .select('*')
      .eq('slug', slug)
      .eq('user_id', user.id)
      .then(({ data }) => {
        const fromCloud = data?.reduce(
          (acc, curr) => {
            acc[`‚òÅÔ∏è ${curr.name}`] = curr.query;
            return acc;
          },
          {} as Record<string, string>
        );
        const local = JSON.parse(localStorage.getItem('param-presets') || '{}');
        setPresets({ ...fromCloud, ...local });
      });
  }, [slug, user]);

  const allTags = Object.entries(
    Object.values(presets)
      .flatMap((query) => extractTags(query))
      .reduce(
        (acc, tag) => {
          const key = tag.toLowerCase();
          acc[key] = (acc[key] || 0) + 1;
          return acc;
        },
        {} as Record<string, number>
      )
  )
    .filter(([_, count]) => count >= 2)
    .sort((a, b) => b[1] - a[1]);

  const savePreset = async () => {
    const updated = { ...presets, [presetName]: paramsString };
    setPresets(updated);
    localStorage.setItem('param-presets', JSON.stringify(updated));
    if (user?.id) {
      await supabase.from('param_presets').upsert({
        slug,
        name: presetName,
        query: paramsString,
        user_id: user.id,
      });
    }
    alert('Preset saved!');
  };

  const deletePreset = async (name: string) => {
    if (!confirm(`Delete preset "${name}"?`)) return;
    const updated = { ...presets };
    delete updated[name];
    setPresets(updated);
    localStorage.setItem('param-presets', JSON.stringify(updated));
    await supabase
      .from('param_presets')
      .delete()
      .match({ slug, name: name.replace(/^‚òÅÔ∏è /, ''), user_id: user?.id });
  };

  const handleApply = (query: string) => {
    const url = new URL(window.location.href);
    url.search = `?slug=${slug}&${query}`;
    window.location.href = url.pathname + url.search;
  };

  const handleShare = (name: string, query: string) => {
    const url = `${window.location.origin}/admin/param-lab?slug=${slug}&${query}`;
    navigator.clipboard.writeText(url);
    alert(`Copied shareable link for "${name}"!`);
  };

  if (!schema) return <div className="p-4 text-red-600">Unknown schema: {slug}</div>;

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold">üß™ Param Lab</h1>
        <select
          className="border px-2 py-1 rounded"
          value={slug}
          onChange={(e) => (window.location.href = `/admin/param-lab?slug=${e.target.value}`)}
        >
          {Object.keys(queryParamSchemas).map((key) => (
            <option key={key} value={key}>
              {key}
            </option>
          ))}
        </select>
      </div>
      <div className="text-sm text-gray-500 space-y-2">
        <p>
          <strong>Query Preview:</strong> {paramsString || '(none)'}
        </p>
        <div className="border p-2 inline-block bg-white">
          <QRCode value={shareUrl} />
        </div>
      </div>
      <TagFilterControls
        tagFilter={tagFilter}
        setTagFilter={setTagFilter}
        tagMatchMode={tagMatchMode}
        setTagMatchMode={setTagMatchMode}
        allTags={allTags}
      />
      <div className="flex gap-2">
        <input
          value={presetName}
          onChange={(e) => setPresetName(e.target.value)}
          placeholder="Preset name"
          className="border px-2 py-1 rounded w-full"
        />
        <button onClick={savePreset} className="text-sm bg-green-600 text-white px-4 py-1 rounded">
          Save Preset
        </button>
      </div>
      <ul className="space-y-2">
        {Object.entries(presets)
          .sort(([a], [b]) => a.localeCompare(b))
          .filter(([_, query]) => {
            const tags = extractTags(query).map((t) => t.toLowerCase());
            return tagMatchMode === 'all'
              ? tagFilter.every((f) => tags.includes(f))
              : tagFilter.length === 0 || tagFilter.some((f) => tags.includes(f));
          })
          .map(([name, query]) => (
            // <PresetCard
            //   key={name}
            //   name={name}
            //   query={query}
            //   slug={slug}
            //   onDelete={deletePreset}
            //   onShare={handleShare}
            //   onApply={handleApply}
            // />
            <div key={name}>{name}</div>
          ))}
      </ul>
      <QueryParamEditor schema={schema as any} slug={slug} />
    </div>
  );
}

