export default function PrintCard() {
  const searchParams = useSearchParams();
  const id = searchParams?.get('id') as string;
  const [lead, setLead] = useState<any>(null);
  const [logs, setLogs] = useState<any[]>([]);

  useEffect(() => {
    if (!id) return;

    supabase
      .from('leads')
      .select('*, domains(domain)')
      .eq('id', id)
      .single()
      .then(({ data }) => setLead(data));

    supabase
      .from('user_action_logs')
      .select('*')
      .eq('lead_id', id)
      .order('timestamp', { ascending: false })
      .then(({ data }) => setLogs(data || []));
  }, [id]);

  const claimUrl = lead?.domains?.domain
    ? `https://${lead.domains.domain}`
    : 'https://quicksites.ai/claim';

  return lead ? (
    <div className="p-6 max-w-lg mx-auto text-black bg-white print:bg-white print:text-black">
      <h1 className="text-2xl font-bold mb-4">You&apos;ve Got a Site!</h1>
      <p className="mb-2">
        Hey <strong>{lead.business_name}</strong>,
      </p>
      <p className="mb-2">We built you a website and it&apos;s ready to preview or claim at:</p>
      <p className="mb-2 text-blue-600">{claimUrl}</p>

      <div className="mt-4 w-full border rounded overflow-hidden">
        <Image
          src={`/screenshots/${lead.domains?.domain || 'default'}.png`}
          alt="Site preview"
          width={800}
          height={450}
          className="w-full h-auto"
          priority
        />
      </div>

      <div className="mt-6 text-center">
        <p className="text-sm mb-1">Scan this to preview or claim:</p>
        <QRCode
          value={claimUrl}
          size={128}
          style={{ height: 'auto', maxWidth: '128px', width: '128px' }}
          viewBox={`0 0 128 128`}
        />
      </div>

      {logs.length > 0 && (
        <div className="mt-6 border-t pt-4 text-xs">
          <h2 className="font-bold text-gray-600 mb-2">Recent Activity</h2>
          <ul className="text-gray-700">
            {logs.map((log, idx) => (
              <li key={idx}>
                [{new Date(log.timestamp).toLocaleString()}] {log.action_type} by{' '}
                {log.triggered_by || '‚Äî'}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  ) : (
    <p className="p-6">Loading...</p>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useCanonicalRole } from '@/hooks/useCanonicalRole';
import { useRouter } from 'next/navigation';
import { supabase } from '@/admin/lib/supabaseClient';

const DEBUG = process.env.NEXT_PUBLIC_DEBUG_AUTH === 'true';

export default function ReferralsPage() {
  const [users, setUsers] = useState<any[]>([]);
  const [profiles, setProfiles] = useState<Record<string, any>>({});
  const [error, setError] = useState('');
  const { role } = useCanonicalRole();
  const router = useRouter();

  useEffect(() => {
    if (role === null) return;

    if (role !== 'admin') {
      console.log('üîê [referrals] Redirecting to login (Reason: Unauthorized)');
      router.push('/login?error=unauthorized');
      return;
    }

    const fetchData = async () => {
      if (DEBUG) console.log('[üë• Fetching Users + Profiles]');

      const { data: userData, error: userError } = await supabase.auth.admin.listUsers();
      if (userError) {
        setError(userError.message);
        return;
      }
      setUsers(userData?.users || []);

      const { data: profileData } = await supabase
        .from('user_profiles')
        .select('user_id, role, referrer_id');

      const profileMap: Record<string, any> = {};
      profileData?.forEach((p) => {
        profileMap[p.user_id] = { role: p.role, referrer_id: p.referrer_id };
      });
      setProfiles(profileMap);

      if (DEBUG) console.log('[‚úÖ Profiles Loaded]', profileMap);
    };

    fetchData();
  }, [role]);

  const referrers = users.filter((u) => profiles[u.id]?.role === 'affiliate_referrer');

  return (
    <div className="p-6 text-white">
      <h1 className="text-2xl font-bold mb-4">Referral Dashboard</h1>
      {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
      <table className="w-full text-sm text-left text-gray-300 bg-gray-800 rounded overflow-hidden">
        <thead className="bg-gray-700 text-gray-100 uppercase text-xs tracking-wide">
          <tr>
            <th className="px-4 py-2">Referrer</th>
            <th className="px-4 py-2">Resellers Referred</th>
            <th className="px-4 py-2">Est. Sites Claimed</th>
            <th className="px-4 py-2">Est. Earnings</th>
          </tr>
        </thead>
        <tbody>
          {referrers.map((r, i) => {
            const refId = r.id;
            const resellers = users.filter(
              (u) => profiles[u.id]?.referrer_id === refId && profiles[u.id]?.role === 'reseller'
            );
            const estimatedSites = resellers.length * 10;
            const estimatedEarnings = estimatedSites * 49 * 0.1;

            return (
              <tr key={r.id} className={i % 2 === 0 ? 'bg-gray-800' : 'bg-gray-900'}>
                <td className="px-4 py-2">{r.email}</td>
                <td className="px-4 py-2">{resellers.length}</td>
                <td className="px-4 py-2">{estimatedSites}</td>
                <td className="px-4 py-2">${estimatedEarnings.toFixed(2)}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { supabase } from '@/admin/lib/supabaseClient';

export default function RegisterPage() {
  const router = useRouter();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [referrerId, setReferrerId] = useState<string | null>(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const ref = new URLSearchParams(window.location.search).get('ref');
    if (ref) {
      localStorage.setItem('referrer_id', ref);
      setReferrerId(ref);
    }
  }, []);

  const register = async (e: any) => {
    e.preventDefault();

    const { error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: {
          role: 'reseller',
          referrer_id: localStorage.getItem('referrer_id') || null,
        },
      },
    });

    if (error) {
      setMessage(error.message);
    } else {
      setMessage('Registration successful! Check your email.');
      setEmail('');
      setPassword('');
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto text-white">
      <h1 className="text-2xl font-bold mb-4">Register</h1>
      <form onSubmit={register} className="space-y-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full px-3 py-2 rounded bg-gray-700 border border-gray-600"
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full px-3 py-2 rounded bg-gray-700 border border-gray-600"
          required
        />
        {referrerId && (
          <p className="text-xs text-gray-400">Signing up via referral: {referrerId}</p>
        )}
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 px-3 py-2 rounded text-white"
        >
          Create Account
        </button>
        {message && <p className="text-sm text-green-400 mt-2">{message}</p>}
      </form>
    </div>
  );
}

