  const [nightly, setNightly] = useState(true);
  const [weekly, setWeekly] = useState(true);

  useEffect(() => {
    fetch('/reports/activity.log')
      .then((res) => res.text())
      .then(setLog);

    fetch('/reports/analytics/index.json')
