'use client';
import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase/client';

import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Tooltip } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip);

export default function TokenLogsDashboard() {
  const [logs, setLogs] = useState<any[]>([]);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    supabase
      .from('token_logs')
      .select('*')
      .order('downloaded_at', { ascending: false })
      .then(({ data }) => {
        setLogs(data || []);
      });
  }, [supabase]);

  const tokenCounts = logs.reduce((acc: any, log: any) => {
    acc[log.token_hash] = (acc[log.token_hash] || 0) + 1;
    return acc;
  }, {});

  const isSuspicious = (hash: string) => tokenCounts[hash] > 5;

  const filtered = logs.filter(
    (log) =>
      log.file_name.toLowerCase().includes(filter.toLowerCase()) ||
      log.token_hash.toLowerCase().includes(filter.toLowerCase())
  );

  const dateCounts = filtered.reduce((acc: any, log: any) => {
    const day = log.downloaded_at?.slice(0, 10);
    acc[day] = (acc[day] || 0) + 1;
    return acc;
  }, {});

  const chartData = {
    labels: Object.keys(dateCounts),
    datasets: [
      {
        label: 'Downloads',
        data: Object.values(dateCounts),
        backgroundColor: 'var(--color-accent)',
      },
    ],
  };

  const exportCSV = () => {
    const rows = filtered.map((log) => ({
      file: log.file_name,
      time: log.downloaded_at,
      token: log.token_hash,
      user_agent: log.user_agent,
      suspicious: isSuspicious(log.token_hash),
    }));
    const csv = ['file,time,token,user_agent,suspicious']
      .concat(rows.map((r) => `${r.file},${r.time},${r.token},"${r.user_agent}",${r.suspicious}`))
      .join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'token_logs.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="p-6 text-text">
      <h1 className="text-2xl font-bold mb-4">üìä Token Download Logs</h1>

      <input
        type="text"
        placeholder="Filter by file or token hash"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="w-full max-w-md mb-2 px-3 py-2 rounded bg-zinc-900 border border-zinc-700 text-sm"
      />

      <button
        onClick={exportCSV}
        className="mb-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Download CSV
      </button>

      <div className="bg-black p-4 rounded shadow mb-6">
        <Bar data={chartData} options={{ responsive: true }} />
      </div>

      <table className="w-full text-sm text-left bg-zinc-900 text-zinc-300 rounded overflow-hidden">
        <thead className="bg-zinc-700 text-xs uppercase text-zinc-100">
          <tr>
            <th className="p-2">File</th>
            <th className="p-2">Time</th>
            <th className="p-2">Token Hash</th>
            <th className="p-2">User Agent</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((log, i) => (
            <tr key={i} className="border-b border-zinc-800">
              <td className="p-2">{log.file_name}</td>
              <td className="p-2">{new Date(log.downloaded_at).toLocaleString()}</td>
              <td className="p-2 text-xs truncate">
                {isSuspicious(log.token_hash) && <span className="text-yellow-400">‚ö†Ô∏è </span>}
                {log.token_hash}
              </td>
              <td className="p-2 text-xs truncate">{log.user_agent}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase/client';

export default function TokenManager() {

  const [tokens, setTokens] = useState<any[]>([]);
  const [fileName, setFileName] = useState('');
  const [expiresAt, setExpiresAt] = useState('');

  const loadTokens = async () => {
    const { data } = await supabase
      .from('report_tokens')
      .select('*')
      .order('expires_at', { ascending: false })
      .limit(20);
    setTokens(data || []);
  };

  useEffect(() => {
    loadTokens();
  }, []);

  const createToken = async () => {
    const raw = Math.random().toString(36).slice(2) + Date.now().toString(36);
    const hash = await crypto.subtle.digest('SHA-256', new TextEncoder().encode(raw));
    const tokenHash = Array.from(new Uint8Array(hash))
      .map((b) => b.toString(16).padStart(2, '0'))
      .join('');

    await supabase.from('report_tokens').insert({
      file_name: fileName,
      token_hash: tokenHash,
      expires_at: expiresAt,
    });

    alert(`‚úÖ Token created! Link: /api/reports/download?file=${fileName}&token=${raw}`);
    setFileName('');
    setExpiresAt('');
    loadTokens();
  };

  return (
    <div className="p-6 text-text max-w-3xl mx-auto space-y-6">
      <h1 className="text-2xl font-bold">üîê Report Access Tokens</h1>

      <div className="space-y-2">
        <label className="block text-sm">File Name (e.g. summary_2025-06-05.pdf)</label>
        <input
          type="text"
          value={fileName}
          onChange={(e) => setFileName(e.target.value)}
          className="w-full px-2 py-1 rounded bg-zinc-900 border border-zinc-700"
        />

        <label className="block text-sm mt-2">Expires At</label>
        <input
          type="datetime-local"
          value={expiresAt}
          onChange={(e) => setExpiresAt(e.target.value)}
          className="w-full px-2 py-1 rounded bg-zinc-900 border border-zinc-700"
        />

        <button
          onClick={createToken}
          className="mt-3 px-4 py-2 bg-emerald-600 text-white rounded hover:bg-emerald-700"
        >
          Generate Token
        </button>
      </div>

      <div className="pt-6">
        <h2 className="font-semibold mb-2">üßæ Recent Tokens</h2>
        <ul className="text-sm bg-zinc-900 rounded p-4 space-y-2">
          {tokens.map((t, i) => (
            <li key={i} className="flex justify-between">
              <span>{t.file_name}</span>
              <span className="text-zinc-400">{new Date(t.expires_at).toLocaleString()}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { AdminAnalyticsToolbar } from '@/components/admin/analytics/admin-analytics-toolbar';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from 'recharts';
import { useDateRange } from '@/hooks/useDateRange';

type SignalDataPoint = {
  date: string;
  views: number;
  feedback: number;
};

export default function AnalyticsChartView() {
  const [view, setView] = useState<'daily' | 'weekly' | 'monthly'>('daily');
  const [stats, setStats] = useState<{ label: string; value: number }[]>([]);
  const [data, setData] = useState<SignalDataPoint[]>([]);
  const { start, end } = useDateRange();

  const fetchData = async () => {
    if (!start || !end) return;

    const res = await fetch(`/api/analytics/signals?start=${start}&end=${end}`);
    const rows: SignalDataPoint[] = await res.json();
    setData(rows);

    const totals = rows.reduce(
      (acc, row) => {
        acc.views += row.views;
        acc.feedback += row.feedback;
        return acc;
      },
      { views: 0, feedback: 0 }
    );

    setStats([
      { label: 'Views', value: totals.views },
      { label: 'Feedback', value: totals.feedback },
    ]);
  };

  const downloadCSV = () => {
    const rows = ['Date,Views,Feedback', ...data.map((r) => `${r.date},${r.views},${r.feedback}`)];
    const blob = new Blob([rows.join('\n')], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'signals.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  useEffect(() => {
    fetchData();
  }, [view, start, end]);

  return (
    <div className="p-6 max-w-6xl mx-auto text-white">
      <AdminAnalyticsToolbar
        title="Odessa Daily Signals"
        view={view}
        setView={setView}
        onRefresh={fetchData}
        stats={stats}
      >
        <button
          onClick={downloadCSV}
          className="px-3 py-1 bg-blue-700 hover:bg-blue-600 rounded text-white"
        >
          ‚¨áÔ∏è Export CSV
        </button>
      </AdminAnalyticsToolbar>

      <div className="bg-zinc-800 rounded p-6 mt-6">
        <ResponsiveContainer width="100%" height={300}>
          <LineChart
            data={data}
            onClick={(e) => {
              const clicked = e?.activeLabel;
              if (clicked) alert(`Drilldown for ${clicked}`);
            }}
          >
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis dataKey="date" stroke="#999" />
            <YAxis stroke="#999" />
            <Tooltip
              contentStyle={{ backgroundColor: '#222', borderColor: '#555' }}
              labelStyle={{ color: '#fff' }}
              formatter={(val, key) => [val, key === 'views' ? 'Views' : 'Feedback']}
            />
            <Line type="monotone" dataKey="views" stroke="#8884d8" strokeWidth={2} />
            <Line type="monotone" dataKey="feedback" stroke="#82ca9d" strokeWidth={2} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

