import { createClient } from '@supabase/supabase-js';
import { chromium } from 'playwright';
import { NextRequest } from 'next/server';
import { Readable } from 'stream';

export const runtime = 'nodejs'; // üëà important: edge runtimes can't use fs or playwright

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

export async function POST(req: NextRequest) {
  const { handle } = await req.json();

  if (!handle || typeof handle !== 'string') {
    return new Response('Missing or invalid handle', { status: 400 });
  }

  const { data: user } = await supabase
    .from('users')
    .select('id')
    .eq('user_metadata->>handle', handle)
    .single();

  if (!user) {
    return Response.json({ error: 'User not found' }, { status: 404 });
  }

  const { data: blocks } = await supabase.from('blocks').select('*').eq('owner_id', user.id);

  const html = `
    <html>
      <head>
        <style>
          body { font-family: sans-serif; font-size: 12px; }
          .grid { display: grid; grid-template-columns: repeat(3, 1fr); gap: 10px; }
          .block { padding: 12px; border: 1px solid #ccc; border-radius: 4px; text-align: center; }
          img { width: 90px; height: 90px; }
        </style>
      </head>
      <body>
        <h1>Block QR Labels: @${handle}</h1>
        <div class="grid">
          ${blocks
            ?.map(
              (b) => `
            <div class="block">
              <div style="font-size: 18px">${b.emoji} ${b.title}</div>
              <div style="margin: 6px 0;">${b.message}</div>
              <img src="https://quicksites.ai/api/block-qr?handle=${handle}&blockId=${b.id}" />
              <div style="font-size: 10px;">/world/${handle}#block-${b.id}</div>
            </div>
          `
            )
            .join('')}
        </div>
      </body>
    </html>
  `;

  const browser = await chromium.launch();
  const page = await browser.newPage();
  await page.setContent(html, { waitUntil: 'networkidle' });

  const pdfBuffer = await page.pdf({
    format: 'A4',
    printBackground: true,
    margin: { top: '20mm', bottom: '20mm', left: '10mm', right: '10mm' },
  });

  await browser.close();

  return new Response(pdfBuffer, {
    headers: {
      'Content-Type': 'application/pdf',
      'Content-Disposition': `inline; filename="${handle}-qr-labels.pdf"`,
    },
  });
}

// app/api/reflect/route.ts
// Use reflect() when you need to reflect on a block
// Use getUserFromRequest() when you need the user context

export const runtime = 'nodejs';

import { createClient } from '@supabase/supabase-js';
import { json } from '@/lib/api/json';

function getSupabaseClient() {
  return createClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.SUPABASE_SERVICE_ROLE_KEY!
  );
}

export async function POST(req: Request): Promise<Response> {
  const supabase = getSupabaseClient();

  const token = req.headers.get('authorization')?.replace(/^Bearer\s+/i, '') || '';
  const ip =
    req.headers.get('x-forwarded-for') ||
    req.headers.get('x-real-ip') ||
    'unknown';

  const { block_id, message } = await req.json();

  if (!token || !block_id) {
    return json({ error: 'Missing token or block_id' }, { status: 400 });
  }

  const {
    data: { user },
    error: authError,
  } = await supabase.auth.getUser(token);

  if (authError || !user) {
    return json({ error: 'Unauthorized' }, { status: 403 });
  }

  // ‚è±Ô∏è Enforce rate limit: 1 reflection per user/block every 5 minutes
  const fiveMinutesAgo = new Date(Date.now() - 5 * 60 * 1000).toISOString();

  const { count: recentCount, error: rateError } = await supabase
    .from('block_feedback')
    .select('id', { count: 'exact', head: true })
    .eq('block_id', block_id)
    .eq('user_id', user.id)
    .eq('action', 'reflect')
    .gt('created_at', fiveMinutesAgo);

  if (rateError) {
    return json({ error: 'Rate check failed' }, { status: 500 });
  }

  if ((recentCount ?? 0) > 0) {
    return json({ error: 'Rate limit: Please wait before reflecting again.' }, { status: 429 });
  }

  const { error: insertError } = await supabase.from('block_feedback').insert({
    block_id,
    user_id: user.id,
    action: 'reflect',
    message: message || null,
    metadata: {
      ip,
      timestamp: new Date().toISOString(),
    },
  });

  if (insertError) {
    return json({ error: insertError.message }, { status: 500 });
  }

  return json({ success: true });
}

import { serviceClient as supabase } from '@/lib/supabase/service';
export const runtime = 'nodejs';

import { createClient } from '@supabase/supabase-js';
import crypto from 'node:crypto';
import fs from 'node:fs';
import path from 'node:path';
import { Readable } from 'node:stream';
import { NextRequest } from 'next/server';

export async function GET(req: NextRequest) {
  const { searchParams } = new URL(req.url);
  const file = searchParams.get('file');
  const token = searchParams.get('token');

  if (!file || !token) {
    return Response.json({ error: 'Missing file or token' }, { status: 400 });
  }

  const hash = crypto.createHash('sha256').update(token).digest('hex');

  const { data, error } = await supabase
    .from('report_tokens')
    .select('*')
    .eq('file_name', file)
    .eq('token_hash', hash)
    .lte('expires_at', new Date().toISOString())
    .maybeSingle();

  if (error || !data) {
    return Response.json({ error: 'Invalid or expired token' }, { status: 403 });
  }

  const filePath = path.resolve(`./reports/analytics/${file}`);
  if (!fs.existsSync(filePath)) {
    return Response.json({ error: 'File not found' }, { status: 404 });
  }

  const fileStream = fs.createReadStream(filePath);
  const readable = new Readable().wrap(fileStream);

  return new Response(readable as any, {
    headers: {
      'Content-Disposition': `attachment; filename="${file}"`,
      'Content-Type': 'application/pdf',
    },
  });
}

