import { NavBadge } from '../../ui/nav-badge';
import { useLiveAdminStats } from '../../../hooks/useLiveAdminStats';

export function NavSections() {
  const pathname = usePathname();
  const { nav, loading } = useSmartNav();
  const { unclaimed, errors } = useLiveAdminStats();

  const matches = (prefix: string) => pathname?.startsWith(prefix);

  if (loading) return null;

  return (
    <nav className="flex flex-col gap-6 text-sm">
      {/* üñ• Desktop */}
      <div className="hidden md:flex flex-wrap gap-4 items-center">
        {nav.map((section) => (
          <details
            key={section.label}
            className="group"
            open={section.routes.some((r) => matches(r.href))}
          >
            <summary
              className={`cursor-pointer font-semibold ${section.color} group-open:underline`}
            >
              {section.label}
            </summary>
            <div className="ml-2 flex flex-wrap gap-2 mt-1">
              {section.routes.map((r) => {
                const isDynamic = r.href.includes('[slug]') || r.href.includes(':');
                const labelWithCount =
                  r.href === '/admin/leads' && unclaimed && unclaimed > 0
                    ? `${r.label} (${unclaimed})`
                    : r.href === '/admin/drafts' && unclaimed && unclaimed > 0
                      ? `${r.label} (${unclaimed})`
                      : r.label;

                return (
                  <SafeLink
                    key={r.href}
                    href={r.href}
                    target={r.external ? '_blank' : undefined}
                    title={r.title}
                    onClick={() => {
                      fetch('/api/log-event', {
                        method: 'POST',
                        body: JSON.stringify({ href: r.href, type: 'nav_click' }),
                      });
                    }}
                  >
                    {labelWithCount}
                    <NavBadge flag={r.flags?.[0]} />
                  </SafeLink>
                );
              })}
            </div>
          </details>
        ))}
      </div>

      {/* üì± Mobile */}
      <div className="md:hidden">
        <label htmlFor="mobile-nav" className="block mb-1 font-medium text-muted-foreground">
          Navigate:
        </label>
        <select
          id="mobile-nav"
          className="w-full p-2 border rounded text-sm"
          onChange={(e) => {
            if (e.target.value) {
              fetch('/api/log-event', {
                method: 'POST',
                body: JSON.stringify({ href: e.target.value, type: 'nav_click' }),
              });
              window.location.href = e.target.value;
            }
          }}
        >
          <option value="">Select a page</option>
          {nav.flatMap((section) => [
            <optgroup key={section.label} label={section.label}>
              {section.routes.map((r) => (
                <option key={r.href} value={r.href}>
                  {r.label.replace(/^[^\w\s]+ /, '')}
                </option>
              ))}
            </optgroup>,
          ])}
        </select>
      </div>
    </nav>
  );
}

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';

export default function ActivityFeed({
  domain_id,
  lead_id,
}: {
  domain_id?: string;
  lead_id?: string;
}) {
  const [logs, setLogs] = useState<any[]>([]);

  useEffect(() => {
    const query = supabase
      .from('user_action_logs')
      .select('*')
      .order('timestamp', { ascending: false });
    if (domain_id) {
      query.eq('domain_id', domain_id).then(({ data }) => setLogs(data || []));
    } else if (lead_id) {
      query.eq('lead_id', lead_id).then(({ data }) => setLogs(data || []));
    }
  }, [domain_id, lead_id]);

  return (
    <div className="text-sm text-gray-300 mt-4">
      <h2 className="text-sm font-bold mb-2 text-gray-400">Activity Feed</h2>
      {logs.length === 0 && <p className="text-xs text-gray-500">No activity yet.</p>}
      <ul className="space-y-2">
        {logs.map((log, i) => (
          <li key={log.id} className="bg-gray-900 px-3 py-2 rounded">
            <div className="text-xs text-green-400">{new Date(log.timestamp).toLocaleString()}</div>
            <div className="text-xs">
              {log.action_type.replace(/_/g, ' ')} by <strong>{log.triggered_by || '‚Äî'}</strong>
            </div>
            {log.notes && <div className="text-xs italic text-gray-400 mt-1">{log.notes}</div>}
          </li>
        ))}
      </ul>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import clsx from 'clsx';
import { AdminNavSections } from '@/components/admin/AppHeader/AdminNavSections';

export default function AdminSidebar({
  onToggle,
}: {
  onToggle?: (collapsed: boolean) => void;
}) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  // Load stored collapsed state and auto-collapse if screen is small
  useEffect(() => {
    const stored = localStorage.getItem('admin-sidebar-collapsed');
    if (stored === 'true') setIsCollapsed(true);

    const handleResize = () => {
      if (window.innerWidth < 768) {
        setIsCollapsed(true);
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Persist collapsed state and notify parent
  useEffect(() => {
    localStorage.setItem('admin-sidebar-collapsed', String(isCollapsed));
    onToggle?.(isCollapsed);
  }, [isCollapsed, onToggle]);

  return (
    <aside
      className={clsx(
        'group fixed top-0 left-0 z-40 h-screen bg-zinc-900 border-r border-zinc-800 text-white overflow-y-auto transition-all duration-300 flex flex-col',
        isCollapsed ? 'w-14 group-hover:w-72' : 'w-72'
      )}
    >
      <div className="relative">
        <button
          onClick={() => setIsCollapsed(!isCollapsed)}
          className="p-2 m-2 hover:bg-zinc-800 rounded transition relative"
          title={isCollapsed ? 'Expand Sidebar' : 'Collapse Sidebar'}
        >
          {isCollapsed ? <ChevronRight size={20} /> : <ChevronLeft size={20} />}
          {isCollapsed && (
            <span className="absolute left-14 top-1/2 -translate-y-1/2 whitespace-nowrap rounded bg-zinc-800 px-2 py-1 text-xs text-white opacity-0 group-hover:opacity-100 transition-all z-20 shadow pointer-events-none">
              Expand Sidebar
            </span>
          )}
        </button>

        {/* Optional handle zone for hover rescue */}
        {isCollapsed && (
          <div className="absolute top-0 right-0 w-2 h-full bg-transparent group-hover:bg-zinc-700 rounded-r cursor-pointer transition-all duration-200" />
        )}
      </div>

      <div className={clsx('flex-1', isCollapsed && 'overflow-hidden')}>
        <h1>hello</h1>
        <AdminNavSections collapsed={isCollapsed} />
      </div>
    </aside>
  );
}

'use client';

import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { FiGrid, FiFileText, FiGlobe, FiUsers, FiMenu } from 'react-icons/fi';
import { useState } from 'react';
import { useCanonicalRole } from '@/hooks/useCanonicalRole';

const allTabs = [
  { label: 'Dashboard', href: '/admin/sites/dashboard', icon: FiGrid },
  {
    label: 'Logs',
    href: '/admin/logs/dashboard',
    icon: FiFileText,
    roles: ['admin'],
  },
  { label: 'Sites', href: '/admin/sites', icon: FiGlobe },
  { label: 'Users', href: '/admin/users', icon: FiUsers, roles: ['admin'] },
];

export default function AdminTabs() {
  const pathname = usePathname();
  const { role } = useCanonicalRole();
  const [open, setOpen] = useState(false);

  const isActive = (href: string) => pathname === href || (pathname && pathname.startsWith(href + '/'));

  const tabs = allTabs.filter((tab) => !tab.roles || tab.roles.includes(role || ''));

  return (
    <div className="border-b border-gray-300 mb-4 px-4">
      <div className="flex justify-between items-center py-2">
        <span className="text-sm font-semibold text-gray-600">Admin Panel</span>
        <button onClick={() => setOpen(!open)} className="md:hidden">
          <FiMenu className="w-5 h-5" />
        </button>
      </div>
      <nav
        className={`flex flex-col md:flex-row gap-3 md:gap-4 text-sm transition-all ${
          open ? 'block' : 'hidden md:flex'
        }`}
      >
        {tabs.map(({ label, href, icon: Icon }) => (
          <Link
            key={href}
            href={href}
            className={`flex items-center gap-1 pb-1 border-b-2 ${
              isActive(href)
                ? 'text-blue-600 border-blue-600 font-medium'
                : 'text-gray-600 border-transparent hover:text-blue-500'
            }`}
          >
            <Icon className="w-4 h-4" />
            {label}
          </Link>
        ))}
      </nav>
    </div>
  );
}

'use client';

import { useDropzone } from 'react-dropzone';
import { useImageUploader } from '@/hooks/useImageUploader';

interface ImageUploaderProps {
  siteId: string;
  templateId: string;
  dbField: string;
  folder?: string;
  label: string;
  initialUrl?: string;
  onUpload?: (url: string | null) => void;
}

export default function ImageUploader(props: ImageUploaderProps) {
  const { preview, uploading, uploadImage, removeImage } = useImageUploader({
    siteId: props.siteId,
    templateId: props.templateId,
    dbField: props.dbField,
    folder: props.folder,
    label: props.label,
    initialUrl: props.initialUrl,
    onChange: props.onUpload,
  });

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: { 'image/*': [] },
    onDrop: (acceptedFiles) => {
      const file = acceptedFiles[0];
      if (file) uploadImage(file);
    },
  });

  const getDropzoneSizeClass = (label?: string) => {
    if (!label) return 'h-40 w-full';

    const lower = label.toLowerCase();

    if (lower.includes('banner')) return 'h-32 w-full aspect-[4/1]';
    if (lower.includes('hero')) return 'h-64 w-full aspect-[2.5/1]';
    if (lower.includes('logo')) return 'h-32 w-32 aspect-square';
    if (lower.includes('team')) return 'h-48 w-full aspect-[3/2]';

    return 'h-40 w-full';
  };

  return (
    <div className="space-y-2">
      {props.label && <p className="text-xs text-white/70 font-medium">{props.label}</p>}

      <div
        {...getRootProps()}
        className={`border-2 border-dashed rounded-lg text-center cursor-pointer transition flex items-center justify-center
          ${isDragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300'}
          ${getDropzoneSizeClass(props.label)}
        `}
      >
        <input {...getInputProps()} />
        <p className="text-sm text-gray-400">
          {isDragActive ? 'Drop the image here‚Ä¶' : 'Drag & drop an image, or click to select one'}
        </p>
      </div>

      {uploading && <p className="text-sm text-gray-500">Uploading...</p>}

      {preview && (
        <div className="relative max-w-xs">
          <img src={preview} alt="Uploaded" className="rounded border max-h-48" />
          <button
            type="button"
            onClick={removeImage}
            className="absolute top-1 right-1 bg-red-500 text-white px-2 py-1 text-xs rounded"
          >
            Remove
          </button>
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';

export default function TemplateImageGallery({ templateId }: { templateId: string }) {
  const [images, setImages] = useState<any[]>([]);

  useEffect(() => {
    if (!templateId) return;
    supabase
      .from('template_images')
      .select('*')
      .eq('template_id', templateId)
      .order('uploaded_at', { ascending: false })
      .then(({ data, error }) => {
        if (error) console.error('Failed to fetch image history:', error);
        else setImages(data);
      });
  }, [templateId]);

  if (!images.length) return null;

  return (
    <div className="mt-4 space-y-2">
      <h3 className="text-sm font-semibold">Upload History</h3>
      <div className="grid grid-cols-2 gap-2">
        {images.map((img) => (
          <div key={img.id} className="relative">
            <img
              src={img.url}
              alt={img.original_name}
              className="rounded border max-h-32 object-contain"
            />
            <div className="absolute bottom-1 left-1 bg-black bg-opacity-50 text-white text-xs px-1 rounded">
              {img.type}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, Button, Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui';
import { format, formatDistance } from 'date-fns';

interface TimelineEvent {
  type: string;
  source: 'guest' | 'log';
  created_at: string;
  trigger_reason?: string;
  referrer?: string;
  page_url?: string;
  platform?: string | null;
  device_type?: string | null;
  ip_address?: string | null;
  user_agent?: string | null;
  [key: string]: any; // optional fallback for dynamic props
}

interface Props {
  open: boolean;
  onClose: () => void;
  userId: string;
  userLabel: string;
  guestEvents: TimelineEvent[];
  userLogs: TimelineEvent[];
}

export function UserTimelineDialog({
  open,
  onClose,
  userId,
  userLabel,
  guestEvents,
  userLogs,
}: Props) {
  const [showAbsolute, setShowAbsolute] = useState(false);

  const allEvents = [...guestEvents, ...userLogs]
    .map((e) => ({
      ...e,
      timestamp: new Date(e.created_at),
    }))
    .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

  const origin = allEvents[0]?.timestamp ?? new Date();

  const emojiMap: Record<string, string> = {
    view: 'üëÅÔ∏è',
    click: 'üöÄ',
    signup_complete: '‚úÖ',
    site_published: 'üì§',
  };

  const descriptions: Record<string, string> = {
    view: 'User opened the upgrade modal',
    click: 'User clicked ‚ÄúSign Up‚Äù button',
    signup_complete: 'User completed sign up',
    site_published: 'User published a site',
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-lg">
        <DialogHeader>
          <DialogTitle className="text-lg font-bold">Timeline for {userLabel}</DialogTitle>
          {guestEvents.length > 0 &&
            (() => {
              const firstView = guestEvents.find((e) => e.type === 'view');
              if (!firstView) return null;

              const ref = (firstView as any).referrer || '';
              const host = typeof window !== 'undefined' ? window.location.origin : '';
              const isInternal = ref.startsWith(host) || ref === '';

              const refColor = ref
                ? isInternal
                  ? 'text-green-600'
                  : 'text-red-600'
                : 'text-muted-foreground';

              const utmFields = [
                'utm_source',
                'utm_medium',
                'utm_campaign',
                'utm_term',
                'utm_content',
              ];

              return (
                <div className="text-sm text-muted-foreground space-y-1 border p-2 rounded bg-muted">
                  {firstView.trigger_reason && (
                    <div>
                      <strong>Trigger:</strong> {firstView.trigger_reason}
                    </div>
                  )}
                  <div>
                    <strong>Referrer:</strong> <span className={refColor}>{ref || 'none'}</span>
                  </div>
                  {(firstView as any).page_url && (
                    <div>
                      <strong>Page:</strong>{' '}
                      <a
                        href={(firstView as any).page_url}
                        target="_blank"
                        rel="noreferrer"
                        className="underline"
                      >
                        {(firstView as any).page_url}
                      </a>
                    </div>
                  )}
                  {utmFields
                    .filter((key) => (firstView as any)[key])
                    .map((key) => (
                      <div key={key}>
                        <strong>{key.replace('utm_', '').toUpperCase()}:</strong>{' '}
                        {(firstView as any)[key]}
                      </div>
                    ))}
                  {(firstView as any).page_url && (
                    <Button
                      size="sm"
                      variant="secondary"
                      className="mt-2"
                      onClick={() => window.open((firstView as any).page_url, '_blank')}
                    >
                      Open Page Context ‚Üó
                    </Button>
                  )}
                </div>
              );
            })()}
        </DialogHeader>

        <div className="mb-2">
          <Button variant="outline" size="sm" onClick={() => setShowAbsolute(!showAbsolute)}>
            Toggle {showAbsolute ? 'Relative' : 'Absolute'} Timestamps
          </Button>
        </div>

        <div className="space-y-2 text-sm max-h-[400px] overflow-y-auto">
          <TooltipProvider>
            {allEvents.map((e, i) => {
              const prev = allEvents[i - 1]?.timestamp;
              const delta =
                prev && e.timestamp
                  ? formatDistance(e.timestamp, prev, { addSuffix: false })
                  : null;

              const deviceInfo =
                e.source === 'guest' && (e.user_agent || e.platform || e.device_type)
                  ? [e.platform, e.device_type].filter(Boolean).join(' ‚Ä¢ ') +
                    (e.user_agent ? `\n${e.user_agent}` : '')
                  : null;

              return (
                <div key={i} className="border-b pb-1">
                  <div className="flex justify-between items-start">
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <span>
                          {emojiMap[e.type] || 'üîπ'} {descriptions[e.type] || e.type}
                        </span>
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>{descriptions[e.type] || 'Event in user journey'}</p>
                      </TooltipContent>
                    </Tooltip>

                    <span className="text-muted-foreground text-xs">
                      {showAbsolute
                        ? format(e.timestamp, 'MMM d, yyyy HH:mm:ss')
                        : formatDistance(e.timestamp, origin, {
                            addSuffix: true,
                          })}
                    </span>
                  </div>

                  {delta && (
                    <div className="text-xs text-muted-foreground pl-4">
                      +{delta} since previous
                    </div>
                  )}

                  {deviceInfo && (
                    <div className="text-xs text-muted-foreground mt-1 pl-4 whitespace-pre-wrap">
                      {deviceInfo}
                    </div>
                  )}
                </div>
              );
            })}
          </TooltipProvider>

          {allEvents.length === 0 && (
            <div className="text-muted-foreground italic">No events found for this user.</div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState, useMemo, useEffect } from 'react';
import { blockMeta } from '@/admin/lib/zod/blockSchema';
import type { Block } from '@/types/blocks';
import { createDefaultBlock } from '@/lib/createDefaultBlock';
import RenderBlockMini from '@/components/admin/templates/render-block-mini';
import SafeTriggerButton from '@/components/ui/safe-trigger-button';

const blockGroups: Record<string, { label: string; types: Block['type'][] }> = {
  content: {
    label: 'Content Blocks',
    types: ['text', 'quote', 'faq', 'testimonial', 'video', 'audio'],
  },
  layout: {
    label: 'Layout',
    types: ['grid', 'footer', 'header'],
  },
  callToAction: {
    label: 'Calls to Action',
    types: ['hero', 'cta', 'contact_form', 'button'],
  },
  services: {
    label: 'Business Features',
    types: ['services', 'service_areas'],
  },
};

function loadCollapsedGroups(): Record<string, boolean> {
  if (typeof window === 'undefined') return {};
  try {
    const saved = localStorage.getItem('collapsedBlockGroups');
    return saved ? JSON.parse(saved) : {};
  } catch {
    return {};
  }
}

function saveCollapsedGroups(groups: Record<string, boolean>) {
  if (typeof window !== 'undefined') {
    localStorage.setItem('collapsedBlockGroups', JSON.stringify(groups));
  }
}

type Props = {
  onAdd: (type: Block['type']) => void;
  existingBlocks?: Block[];
  disallowDuplicates?: Block['type'][];
  label?: string;
  triggerElement?: React.ReactNode;
};

export default function BlockAdderGrouped({
  onAdd,
  existingBlocks = [],
  disallowDuplicates = ['footer'],
  label = 'Add Block',
  triggerElement,
}: Props) {
  const [open, setOpen] = useState(false);
  const [search, setSearch] = useState('');
  const [collapsedGroups, setCollapsedGroups] = useState<Record<string, boolean>>(() =>
    loadCollapsedGroups()
  );

  useEffect(() => {
    const onKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Escape') setOpen(false);
    };
    document.addEventListener('keydown', onKeyDown);
    return () => document.removeEventListener('keydown', onKeyDown);
  }, []);

  const blocked = new Set(
    existingBlocks.filter((b) => disallowDuplicates.includes(b.type)).map((b) => b.type)
  );

  const filtered = useMemo(() => {
    return Object.entries(blockGroups).map(([key, group]) => {
      const matches = group.types.filter((type) => {
        return (
          !blocked.has(type) &&
          blockMeta[type as keyof typeof blockMeta]?.label.toLowerCase().includes(search.toLowerCase())
        );
      });
      return { key, label: group.label, types: matches };
    });
  }, [search, existingBlocks]);

  return (
    <>
      <div onClick={() => setOpen(true)}>
        {triggerElement ? (
          triggerElement
        ) : (
          <SafeTriggerButton
            onClick={() => setOpen(true)}
            className="w-full bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 text-center"
          >
            {label}
          </SafeTriggerButton>
        )}
      </div>

      {open && (
        <div className="fixed inset-0 z-50 bg-black/40 backdrop-blur-sm flex items-center justify-center p-4">
          <div className="bg-white dark:bg-neutral-900 rounded-lg shadow-2xl max-w-3xl w-full max-h-[90vh] overflow-y-auto border border-black/10 dark:border-white/10">
            <div className="p-4 border-b border-gray-200 dark:border-neutral-700 flex justify-between items-center">
              <h2 className="text-lg font-semibold text-gray-800 dark:text-white">
                {label}
              </h2>
              <button
                onClick={() => setOpen(false)}
                className="text-sm text-gray-500 hover:text-red-500 dark:text-gray-400"
              >
                ‚úï
              </button>
            </div>

            <div className="p-4 border-b border-gray-200 dark:border-neutral-700">
              <input
                autoFocus
                type="text"
                placeholder="Search block types..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="w-full px-3 py-2 rounded-md border border-gray-300 dark:border-neutral-700 bg-gray-50 dark:bg-neutral-800 text-black dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>

            <div className="p-4 space-y-6">
              {filtered.map(({ key, label, types }) => (
                <div key={key}>
                  <button
                    className="w-full text-left px-4 py-2 font-medium text-sm text-gray-700 dark:text-gray-300 bg-gray-50 dark:bg-neutral-800 hover:bg-gray-100 dark:hover:bg-neutral-700 rounded"
                    onClick={() => {
                      const next = { ...collapsedGroups, [key]: !collapsedGroups[key] };
                      setCollapsedGroups(next);
                      saveCollapsedGroups(next);
                    }}
                  >
                    {label}
                  </button>

                  {!collapsedGroups[key] && (
                    <div className="pt-2 flex flex-col gap-4 px-4">
                      {types.length > 0 ? (
                        types.map((type) => (
                          <button
                            key={type}
                            onClick={() => {
                              onAdd(type);
                              setSearch('');
                              setOpen(false);
                            }}
                            className="text-left px-4 py-4 rounded-md border border-gray-300 dark:border-neutral-700 bg-white dark:bg-neutral-900 hover:bg-gray-50 dark:hover:bg-neutral-800 space-y-3 text-sm"
                          >
                            <div className="flex items-center gap-2">
                              <div className="text-lg">
                                {blockMeta[type as keyof typeof blockMeta].icon}
                              </div>
                              <div>
                                <div className="font-medium text-gray-800 dark:text-white">
                                  {blockMeta[type as keyof typeof blockMeta].label}
                                </div>
                                <div className="text-xs text-gray-500 dark:text-gray-400">
                                  Block type: {type}
                                </div>
                              </div>
                            </div>
                            <div className="w-full border rounded overflow-hidden">
                              <RenderBlockMini block={createDefaultBlock(type)} className="w-full h-32" />
                            </div>
                          </button>
                        ))
                      ) : (
                        <div className="px-2 py-1 text-sm text-gray-400 dark:text-gray-500">
                          None available
                        </div>
                      )}
                    </div>
                  )}
                </div>
              ))}

              {filtered.every((g) => g.types.length === 0) && (
                <div className="px-4 py-2 text-sm text-gray-500 dark:text-gray-400">
                  No matching block types found
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
}

'use client';

import { useState, useMemo } from 'react';
import { blockMeta } from '@/admin/lib/zod/blockSchema';
import type { Block } from '@/types/blocks';

type Props = {
  onAdd: (type: Block['type']) => void;
  label?: string;
  disallowDuplicates?: Block['type'][];
  existingBlocks?: Block[];
};

export default function BlockAdderWithSearch({
  onAdd,
  label = 'Add Block',
  disallowDuplicates = ['footer'],
  existingBlocks = [],
}: Props) {
  const [open, setOpen] = useState(false);
  const [search, setSearch] = useState('');

  const availableTypes = useMemo(() => {
    const blocked = new Set(
      existingBlocks
        .filter((b) => disallowDuplicates.includes(b.type))
        .map((b) => b.type)
    );

    return Object.entries(blockMeta).filter(
      ([type, meta]) =>
        type.toLowerCase().includes(search.toLowerCase()) &&
        !blocked.has(type as Block['type'])
    );
  }, [search, existingBlocks]);

  return (
    <div className="relative inline-block text-left mb-4 w-full max-w-sm">
      <button
        onClick={() => setOpen((o) => !o)}
        className="w-full bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
      >
        {label}
      </button>

      {open && (
        <div className="absolute z-20 mt-2 w-full rounded-md bg-white dark:bg-neutral-900 shadow-lg ring-1 ring-black ring-opacity-5">
          <div className="p-2 border-b border-gray-200 dark:border-neutral-700">
            <input
              autoFocus
              type="text"
              placeholder="Search block types..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="w-full px-3 py-2 rounded bg-gray-100 dark:bg-neutral-800 text-black dark:text-white"
            />
          </div>
          <div className="py-1 max-h-80 overflow-y-auto">
            {availableTypes.map(([type, meta]) => (
              <button
                key={type}
                onClick={() => {
                  onAdd(type as Block['type']);
                  setSearch('');
                  setOpen(false);
                }}
                className="w-full text-left px-4 py-2 text-sm text-gray-800 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-neutral-800"
              >
                {meta.icon} {meta.label}
              </button>
            ))}
            {availableTypes.length === 0 && (
              <div className="px-4 py-2 text-sm text-gray-500 dark:text-gray-400">No blocks found</div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

