'use client';

import { Lead } from '@/types/lead.types';
import { Dispatch, SetStateAction, useEffect, useMemo, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import dayjs from 'dayjs';
import LeadSelectorWithRadius from '@/components/admin/campaigns/lead-selector-with-radius';
import { getDistanceMiles } from '@/lib/utils/distance';
import { CampaignType } from '@/types/campaign.types';
import { getLatLonForCityState } from '@/lib/utils/geocode';

const US_STATES = [
  'AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA', 'HI', 'ID', 'IL', 'IN', 'IA',
  'KS', 'KY', 'LA', 'ME', 'MD', 'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ',
  'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC', 'SD', 'TN', 'TX', 'UT', 'VT',
  'VA', 'WA', 'WV', 'WI', 'WY'
];

export type Props = {
  campaign: CampaignType;
  allLeads: Lead[];
  selectedLeadIds: string[];
  setSelectedLeadIds: Dispatch<SetStateAction<string[]>>;
  leadsByCampaign: Record<string, Lead[]>;
  setEditingCampaign: (c: CampaignType | null) => void;
  geoCenter?: { lat: number; lon: number } | null;
  currentCampaignId?: string | null;
  currentCampaignExpiresAt?: string | null;
};

export default function EditCampaignModal(props: Props) {
  const {
    campaign,
    allLeads,
    selectedLeadIds,
    setSelectedLeadIds,
    leadsByCampaign,
    setEditingCampaign,
    geoCenter,
    currentCampaignId,
    currentCampaignExpiresAt,
  } = props;

  useEffect(() => {
    const handleKey = (e: KeyboardEvent) => {
      if (e.key === 'Escape') setEditingCampaign(null);
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, []);
  const [radius, setRadius] = useState(50);
  const [availableIndustries, setAvailableIndustries] = useState<string[]>([]);
  const [campaignIndustry, setCampaignIndustry] = useState(campaign.industry || '');
  const [campaignState, setCampaignState] = useState(campaign.state || '');
  const [campaignCity, setCampaignCity] = useState(campaign.city || '');
  const [campaignStatus, setCampaignStatus] = useState<CampaignType['status']>(campaign.status || 'draft');
  const [cityLat, setCityLat] = useState<number | null>(campaign.city_lat ?? null);
  const [cityLon, setCityLon] = useState<number | null>(campaign.city_lon ?? null);

  // fetch industries
  useEffect(() => {
    async function fetchIndustries() {
      try {
        const res = await fetch('/api/industries');
        const data = await res.json();
        if (Array.isArray(data)) {
          setAvailableIndustries(data.sort());
        }
      } catch (err) {
        console.error('Failed to load industries:', err);
      }
    }
    fetchIndustries();
  }, []);

  // sync lat lon
  useEffect(() => {
    async function syncLatLon() {
      if (campaignCity && campaignState) {
        const coords = await getLatLonForCityState(campaignCity, campaignState);
        if (coords) {
          setCityLat(coords.lat);
          setCityLon(coords.lon);
        }
      }
    }
    syncLatLon();
  }, [campaignCity, campaignState]);

  // sync campaign state
  useEffect(() => {
    setCampaignState(campaign.state || '');
    setCampaignIndustry(campaign.industry || '');
    setCampaignCity(campaign.city || '');
    setCampaignStatus(campaign.status || 'draft');
    setCityLat(campaign.city_lat ?? null);
    setCityLon(campaign.city_lon ?? null);
  }, [campaign]);

  // fetch linked leads
  useEffect(() => {
    async function fetchLinkedLeads() {
      if (!campaign?.id) return;

      const res = await fetch(`/api/campaigns/${campaign.id}/leads`);
      const json = await res.json();

      if (res.ok && Array.isArray(json.lead_ids)) {
        setSelectedLeadIds(json.lead_ids);
      } else {
        console.warn('Failed to fetch campaign-linked leads:', json.error || json);
      }
    }

    fetchLinkedLeads();
  }, [campaign?.id]);

  // filter leads by industry
  const filteredLeads = useMemo(() => {
    const leads = campaignIndustry
      ? allLeads.filter((l) => l.industry?.toLowerCase().trim() === campaignIndustry.toLowerCase().trim())
      : allLeads;
    return leads;
  }, [allLeads, campaignIndustry]);

  // count leads by radius
  const leadCountByRadius = useMemo(() => {
    if (!cityLat || !cityLon) return {};
    return {
      '0‚Äì10 mi': filteredLeads.filter((l) => getDistanceMiles(l.address_lat!, l.address_lon!, cityLat, cityLon) < 10).length,
      '10‚Äì25 mi': filteredLeads.filter((l) => {
        const d = getDistanceMiles(l.address_lat!, l.address_lon!, cityLat, cityLon);
        return d >= 10 && d < 25;
      }).length,
      '25‚Äì50 mi': filteredLeads.filter((l) => {
        const d = getDistanceMiles(l.address_lat!, l.address_lon!, cityLat, cityLon);
        return d >= 25 && d < 50;
      }).length,
    };
  }, [filteredLeads, cityLat, cityLon]);

  return (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-zinc-900 text-white p-6 rounded shadow-xl w-full max-w-md max-h-[90vh] overflow-y-auto relative">
        <h2 className="text-lg font-bold mb-4">Edit Campaign</h2>
        <form
          onSubmit={async (e) => {
            e.preventDefault();
            try {
              const res = await fetch('/api/campaigns/update', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                id: campaign.id,
                name: campaign.name,
                city: campaignCity,
                state: campaignState,
                city_lat: cityLat,
                city_lon: cityLon,
                starts_at: campaign.starts_at,
                ends_at: campaign.ends_at,
                lead_ids: selectedLeadIds,
                industry: campaignIndustry,
                status: campaignStatus,
              }),
              });

              const payload = {
                id: campaign.id,
                name: campaign.name,
                city: campaignCity,
                state: campaignState,
                city_lat: cityLat,
                city_lon: cityLon,
                starts_at: campaign.starts_at,
                ends_at: campaign.ends_at,
                lead_ids: selectedLeadIds,
                industry: campaignIndustry,
                status: campaignStatus,
              };

              console.log('[üì§ Saving Campaign]', payload);
              const json = await res.json();
              if (json.updated) {
                console.log('[‚úÖ Updated from server]', json.updated);
                setCampaignState(json.updated.state || '');
                setCampaignIndustry(json.updated.industry || '');
                setCampaignCity(json.updated.city || '');
                setCampaignStatus(json.updated.status || 'draft');
                setCityLat(json.updated.city_lat ?? null);
                setCityLon(json.updated.city_lon ?? null);
              }

              if (!res.ok || json.error) {
                console.error('‚ùå Update failed:', json.error || 'Unknown error');
                return;
              }

              setEditingCampaign(null);
              location.reload();
            } catch (err) {
              console.error('‚ùå Network error while updating campaign:', err);
            }
          }}
        >
          <label className="block mb-3">
            Name:
            <input
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaign.name}
              onChange={(e) => (campaign.name = e.target.value)}
            />
          </label>
          <label className="block mb-3">
            City:
            <input
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignCity}
              onChange={(e) => setCampaignCity(e.target.value)}
              autoComplete="off"
            />
          </label>
          <label className="block mb-3">
            State:
            <span className="ml-2 text-xs text-zinc-400">[DB: {campaign.state}]</span>
            <select
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignState}
              onChange={(e) => setCampaignState(e.target.value)}
            >
              <option value="">Select State</option>
              {US_STATES.map((abbr) => (
                <option key={abbr} value={abbr}>
                  {abbr}
                </option>
              ))}
            </select>
          </label>
          <label className="block mb-3">
            Status:
            <select
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignStatus}
              onChange={(e) => setCampaignStatus(e.target.value as CampaignType['status'])}
            >
              <option value="draft">Draft</option>
              <option value="published">Published</option>
            </select>
          </label>

          <label className="block mb-3">
            Industry:
            <span className="ml-2 text-xs text-zinc-400">[DB: {campaign.industry}]</span>
            <select
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignIndustry}
              onChange={(e) => setCampaignIndustry(e.target.value)}
            >
              <option value="">Select Industry</option>
              {availableIndustries.map((ind) => (
                <option key={ind} value={ind}>
                  {ind}
                </option>
              ))}
            </select>
          </label>
          <label className="block mb-3">
            Starts At:
            <DatePicker
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              selected={dayjs(campaign.starts_at).toDate()}
              onChange={(date) => (campaign.starts_at = dayjs(date).toISOString())}
              showTimeSelect
              dateFormat="Pp"
            />
          </label>
          <label className="block mb-3">
            Ends At:
            <DatePicker
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              selected={dayjs(campaign.ends_at).toDate()}
              onChange={(date) => (campaign.ends_at = dayjs(date).toISOString())}
              showTimeSelect
              dateFormat="Pp"
            />
          </label>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">
              Link Leads:
              <div className="text-xs text-zinc-400 mt-1">
                0‚Äì10 mi: {leadCountByRadius['0‚Äì10 mi']} ‚Ä¢ 10‚Äì25 mi: {leadCountByRadius['10‚Äì25 mi']} ‚Ä¢ 25‚Äì50 mi: {leadCountByRadius['25‚Äì50 mi']}
              </div>
            </label>
            <LeadSelectorWithRadius
              leads={filteredLeads}
              selectedLeadIds={selectedLeadIds}
              setSelectedLeadIds={setSelectedLeadIds}
              cityLat={cityLat}
              cityLon={cityLon}
              radius={radius}
              setRadius={setRadius}
              industry={campaignIndustry}
              currentCampaignId={campaign.id}
              currentCampaignExpiresAt={campaign.ends_at}
            />
          </div>

          <div className="flex justify-end gap-3 sticky bottom-0 bg-zinc-900 pt-3 pb-4 z-10">
            <button
              type="button"
              className="text-sm text-zinc-400 hover:underline"
              onClick={() => setEditingCampaign(null)}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-1 text-sm rounded bg-blue-600 hover:bg-blue-700 text-white"
            >
              Save
            </button>
          </div>
        <details className="mt-4 text-xs text-zinc-400 bg-zinc-800 p-2 rounded border border-zinc-700">
  <summary className="cursor-pointer select-none">Debug: Loaded Campaign Object</summary>
  <pre className="whitespace-pre-wrap text-xs mt-2">
    {JSON.stringify(campaign, null, 2)}
  </pre>
</details>
</form>
      </div>
    </div>
  );
}

// components/admin/campaigns/lead-selector-with-radius.tsx
'use client';

import { useEffect, useState } from 'react';
import { getDistanceMiles } from '@/lib/utils/distance';

type Lead = {
    id: string;
    business_name: string;
    address_lat?: number | null;
    address_lon?: number | null;
    address_city?: string | null;
    address_state?: string | null;
    industry?: string | null;
    current_campaign_id?: string | null;
    current_campaign_expires_at?: string | null;
  };


type Props = {
  leads: Lead[];
  selectedLeadIds: string[];
  setSelectedLeadIds: (ids: string[]) => void;
  cityLat: number | null | undefined;
  cityLon: number | null | undefined;
  radius: number;
  setRadius: (radius: number) => void;
  geoCenter?: { lat: number; lon: number } | null;
  industry?: string | null;
  state?: string | null;
  currentCampaignId?: string | null;
  currentCampaignExpiresAt?: string | null;
};

export default function LeadSelectorWithRadius({
  leads,
  selectedLeadIds,
  setSelectedLeadIds,
  cityLat,
  cityLon,
  radius,
  setRadius,
  geoCenter,
  industry,
  currentCampaignId,
  currentCampaignExpiresAt,
}: Props) {
    const isLeadDisabled = (lead: Lead): boolean => {
        if (!lead.current_campaign_id) return false;

        const isExpired =
          !lead.current_campaign_expires_at ||
          new Date(lead.current_campaign_expires_at) < new Date();

        const isSameCampaign = lead.current_campaign_id === currentCampaignId;

        return !(isExpired || isSameCampaign);
      };

    const toggle = (id: string) => {
      setSelectedLeadIds(
        selectedLeadIds.includes(id)
          ? selectedLeadIds.filter((x) => x !== id)
          : [...selectedLeadIds, id]
    );
  };

  const resolvedLat = cityLat ?? geoCenter?.lat ?? null;
  const resolvedLon = cityLon ?? geoCenter?.lon ?? null;

  const getDist = (l: Lead) =>
    l.address_lat && l.address_lon && resolvedLat && resolvedLon
      ? getDistanceMiles(l.address_lat, l.address_lon, resolvedLat, resolvedLon)
      : Infinity;

  const filteredLeads = leads.filter((l) =>
    !industry || l.industry?.trim().toLowerCase() === industry.trim().toLowerCase()
  );

  const sortedLeads = filteredLeads
    .filter((l) => getDist(l) <= radius)
    .sort((a, b) => getDist(a) - getDist(b));

  const groupByRange = [
    { label: '0‚Äì10 mi', min: 0, max: 10 },
    { label: '10‚Äì25 mi', min: 10, max: 25 },
    { label: '25‚Äì50 mi', min: 25, max: 50 },
  ];

  const renderLeadLine = (lead: Lead, distanceLabel: string) => {
    const disabled = isLeadDisabled(lead);

    return (
      <div key={lead.id} className="flex flex-col gap-1 mb-2">
        <label
          className={`flex items-center gap-2 ${disabled ? 'opacity-50 cursor-not-allowed' : ''}`}
          title={disabled ? 'Already part of an active campaign' : ''}
        >
          <input
            type="checkbox"
            disabled={disabled}
            checked={selectedLeadIds.includes(lead.id)}
            onChange={() => toggle(lead.id)}
          />
          <div className="text-sm text-white">
            <span className="font-semibold">{lead.business_name}</span>{' '}
            <span className="text-xs text-zinc-400">
              ({lead.address_city}, {lead.address_state} ‚Ä¢ {lead.industry}) ‚Äî {distanceLabel}
            </span>
          </div>
        </label>
      </div>
    );
  };


  const renderGroup = (label: string, min: number, max: number) => {
    const group = sortedLeads.filter((l) => {
      const dist = getDist(l);
      return dist >= min && dist < max;
    });
    if (!group.length) return null;

    return (
      <div key={label} className="pt-2">
        <div className="text-xs font-semibold text-zinc-400 mb-1">{label}</div>
        {group.map((lead) => renderLeadLine(lead, `${getDist(lead).toFixed(1)} mi`))}
      </div>
    );
  };

  const outsideRadius = filteredLeads.filter((l) => getDist(l) > radius);

  return (
    <div className="mb-4">
      <label className="text-sm font-semibold block mb-1">Radius Filter:</label>
      <select
        className="bg-zinc-800 text-white border border-zinc-700 rounded px-2 py-1 mb-3"
        value={radius}
        onChange={(e) => setRadius(Number(e.target.value))}
      >
        {[10, 25, 50, 100].map((r) => (
          <option key={r} value={r}>
            Within {r} miles
          </option>
        ))}
      </select>

      <div className="rounded border border-zinc-700 p-3 bg-zinc-800 divide-y divide-zinc-700 max-h-[320px] overflow-y-auto">
        {groupByRange.map(({ label, min, max }) => renderGroup(label, min, max))}

        {outsideRadius.length > 0 && (
          <div className="pt-4">
            <div className="text-xs font-semibold text-zinc-400 mb-1">Other Leads</div>
            {outsideRadius.map((lead) => renderLeadLine(lead, 'outside radius'))}
          </div>
        )}
      </div>
    </div>
  );
}

// components/admin/city-marker.tsx

'use client';

import { CircleMarker, Popup } from 'react-leaflet';
import { useMemo } from 'react';
import { useRouter } from 'next/navigation';

interface CityMarkerProps {
  point: {
    lat: number;
    lon: number;
    city: string;
    state: string;
    leads: number;
    leadNames: string[];
    leadIds: string[];
    domains: number;
    domainNames: string[];
    industry?: string;
  };
  zoom: number;
  getColor: (point: any) => string;
  router: ReturnType<typeof useRouter>;
  onClick?: () => void;
}

export default function CityMarker({
  point,
  zoom,
  getColor,
  router,
  onClick,
}: CityMarkerProps) {
  const shouldRender = useMemo(() => {
    return !(zoom < 4 && point.leads + point.domains < 3);
  }, [zoom, point]);

  if (!shouldRender) return null;

  return (
    <CircleMarker
      center={[point.lat, point.lon]}
      radius={6 + (point.leads + point.domains) * 0.5}
      pathOptions={{
        color: getColor(point),
        fillColor: getColor(point),
        fillOpacity: 0.7,
      }}
      eventHandlers={{
        click: () => onClick?.(),
      }}
    >
      <Popup className="w-64 p-4 bg-gray-800 text-white rounded-lg shadow-lg">
        <div className="space-y-2">
          <div className="text-lg font-bold">
            <div className="flex justify-between">
              {point.city}, {point.state}
            </div>
            <div className="flex justify-between">
              {point.leads} leads, {point.domains} domains
            </div>
          </div>
          <div className="text-sm">Leads: {point.leads}</div>
          {point.leadNames.length > 0 && (
            <div className="text-xs text-gray-300">
              {point.leadNames.join(', ')}
            </div>
          )}
          <div className="text-sm mt-1">Domains: {point.domains}</div>
          {point.domainNames.length > 0 && (
            <div className="text-xs text-gray-300">
              {point.domainNames.join(', ')}
            </div>
          )}
          {point.industry && (
            <div className="text-xs text-amber-300 italic">
              {point.industry}
            </div>
          )}

          {point.leads >= 0 && (
            <>
            <button
              onClick={() => {
                const query = new URLSearchParams({
                  city: point.city,
                  state: point.state,
                  leadIds: point.leadIds.join(','),
                  industry: point.industry || '',
                });
                router.push(`/admin/start-campaign?${query}`);
              }}
              className="mt-3 px-3 py-2 bg-blue-600 text-white rounded-md text-sm hover:bg-blue-700 w-full"
              >
                üöÄ Start Campaignz
              </button>
            </>
          )}
        </div>
      </Popup>
    </CircleMarker>
  );
}

import { useEffect, useState } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';

type Comment = {
  id: string;
  message: string;
  author_email: string;
  created_at: string;
};

export default function CommentThread({ snapshotId }: { snapshotId: string }) {
  const [comments, setComments] = useState<Comment[]>([]);
  const [newComment, setNewComment] = useState('');
  const [author, setAuthor] = useState('');

  const loadComments = async () => {
    const res = await fetch(`/api/comments?snapshot_id=${snapshotId}`);
    const data = await res.json();
    setComments(data);
  };

  useEffect(() => {
    if (snapshotId) loadComments();
  }, [snapshotId]);

  const submitComment = async () => {
    if (!newComment.trim()) return;
    const res = await fetch('/api/comments', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        snapshot_id: snapshotId,
        author_email: author || 'anonymous',
        message: newComment,
      }),
    });
    if (res.ok) {
      setNewComment('');
      loadComments();
    }
  };

  return (
    <div className="p-4 border-t mt-4 bg-muted/20 rounded">
      <h3 className="text-lg font-semibold mb-2">Comments</h3>

      {comments.length === 0 && (
        <p className="text-sm text-muted-foreground mb-4">No comments yet.</p>
      )}

      <ul className="space-y-3 mb-4">
        {comments.map((c) => (
          <li key={c.id} className="bg-white p-3 rounded shadow-sm">
            <p className="text-sm">{c.message}</p>
            <p className="text-xs text-muted-foreground mt-1">
              {c.author_email} ‚Äì {new Date(c.created_at).toLocaleString()}
            </p>
          </li>
        ))}
      </ul>

      <div className="space-y-2">
        <Input
          placeholder="Your email (optional)"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
        <Textarea
          placeholder="Leave a comment..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <Button onClick={submitComment} size="sm">
          Post Comment
        </Button>
      </div>
    </div>
  );
}

'use client';

import { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { supabase } from '@/lib/supabase/client';

import { getSupabase } from '@/lib/supabase/server';

type CurrentUser = {
  id: string;
  email: string;
  name?: string;
  avatar_url?: string;
  role: string;
  bio?: string;
  plan?: string;
};

export type CurrentUserContextType = {
  user: CurrentUser | null;
  ready: boolean;
  role: 'admin' | 'editor' | 'viewer' | 'owner';
  hasRole: (roles: string[]) => boolean;
  refetch: () => void;
};

export const CurrentUserContext = createContext<CurrentUserContextType>({
  user: null,
  ready: false,
  role: 'viewer',
  hasRole: () => false,
  refetch: () => {},
});

export function CurrentUserProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<CurrentUser | null>(null);
  const [ready, setReady] = useState<boolean>(false);

  const load = async () => {
    const { data: sessionData } = await supabase.auth.getSession();
    const u = sessionData?.session?.user;

    if (!u?.id) {
      setUser(null);
      setReady(true);
      return;
    }

    const { data: profile, error } = await supabase
      .from('user_profiles')
      .select('*')
      .eq('user_id', u.id)
      .single();

    if (error) {
      console.error('‚ùå Failed to fetch user_profiles:', error.message);
      setUser(null);
      setReady(true);
      return;
    }

    const normalizedRole = (profile.role || 'viewer').toLowerCase() as CurrentUser['role'];

    const user: CurrentUser = {
      id: u.id,
      email: u.email ?? profile.email,
      name: profile.name ?? '',
      avatar_url: profile.avatar_url ?? '',
      role: normalizedRole,
      bio: profile.bio ?? '',
      plan: profile.plan ?? 'free',
    };

    console.log('‚úÖ [User loaded]', {
      email: user.email,
      role: user.role,
      plan: user.plan,
    });

    setUser(user);
    setReady(true);
  };

  useEffect(() => {
    load();
  }, []);

  const hasRole = (roles: string[]) =>
    user?.role ? roles.map((r) => r.toLowerCase()).includes(user.role.toLowerCase()) : false;

  return (
    <CurrentUserContext.Provider
      value={{
        user,
        ready,
        role: (user?.role as 'admin' | 'editor' | 'viewer' | 'owner') || 'viewer',
        hasRole,
        refetch: load,
      }}
    >
      {children}
    </CurrentUserContext.Provider>
  );
}

