'use client';

import { useState } from 'react';
import { supabase } from '@/lib/supabase/client';
import { Button } from '@/components/ui/button';
import Image from 'next/image';
import { Label } from '@/components/ui/label';

export default function FaviconUploader({
  templateId,
  currentUrl,
  onUpload,
}: {
  templateId: string;
  currentUrl?: string;
  onUpload: (url: string) => void;
}) {
  const [uploading, setUploading] = useState(false);
  const [preview, setPreview] = useState(currentUrl || '');

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    if (file.size > 1 * 1024 * 1024) {
      alert('Favicon file must be less than 1MB.');
      return;
    }

    const ext = 'png';
    const path = `favicons/${templateId}/favicon.${ext}`;

    setUploading(true);

    const optimizedRes = await fetch('/api/favicon-optimize', {
      method: 'POST',
      body: file,
    });

    if (!optimizedRes.ok) {
      alert('Optimization failed.');
      setUploading(false);
      return;
    }

    const blob = await optimizedRes.blob();

    const { error } = await supabase.storage
      .from('public')
      .upload(path, blob, {
        contentType: 'image/png',
        upsert: true,
      });

    if (error) {
      alert('Upload failed.');
      setUploading(false);
      return;
    }

    const { data } = supabase.storage.from('public').getPublicUrl(path);
    setPreview(data.publicUrl);
    onUpload(data.publicUrl);
    setUploading(false);
  };

  return (
    <div className="space-y-2">
      <Label>Favicon (.png only)</Label>
      <input type="file" accept=".png" onChange={handleFileChange} disabled={uploading} />
      {preview && (
        <div className="mt-2">
          <Image src={preview} alt="Favicon Preview" width={48} height={48} />
        </div>
      )}
    </div>
  );
}

'use client';

import { useRef, useEffect } from 'react';
import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { GripVertical, Trash2, PlusCircle } from 'lucide-react';
import type { Template } from '@/types/template';

type Link = { label: string; href: string };

function SortableLinkItem({
  link,
  index,
  id,
  onChange,
  onRemove,
  internalSlugs,
  createNewPage,
  focusOnMount,
}: {
  link: Link;
  index: number;
  id: string;
  onChange: (updated: Link) => void;
  onRemove: () => void;
  internalSlugs: string[];
  createNewPage: () => string;
  focusOnMount?: boolean;
}) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const labelRef = useRef<HTMLInputElement>(null);
  const blockRef = useRef<HTMLDivElement>(null);
  const isLabelMissing = !link.label.trim();
  const isHrefMissing = !link.href.trim();

  useEffect(() => {
    if (focusOnMount && isLabelMissing && labelRef.current) {
      labelRef.current.focus();
    }
  }, [focusOnMount, isLabelMissing]);

  return (
    <div
      ref={(el) => {
        setNodeRef(el);
        blockRef.current = el;
      }}
      style={style}
      className="relative bg-neutral-800 border border-neutral-700 rounded p-4 space-y-2"
    >
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <GripVertical
            {...attributes}
            {...listeners}
            className="w-4 h-4 cursor-move text-gray-500"
          />
          <span className="text-sm text-gray-300">Label #{index + 1}</span>
        </div>
        <button
          onClick={onRemove}
          className="text-red-500 hover:text-red-300"
          title="Remove"
        >
          <Trash2 size={16} />
        </button>
      </div>

      <input
        ref={labelRef}
        aria-invalid={isLabelMissing}
        type="text"
        className={`w-full bg-neutral-900 border rounded px-2 py-1 text-sm ${
          isLabelMissing ? 'border-red-500' : 'border-neutral-700'
        }`}
        placeholder="Link label"
        value={link.label}
        onChange={(e) => onChange({ ...link, label: e.target.value })}
      />
      {isLabelMissing && (
        <p className="text-xs text-red-400">Label is required</p>
      )}

      <div className="flex items-center gap-2 mt-1">
        <select
          className="bg-neutral-900 border border-neutral-700 rounded px-2 py-1 text-sm"
          value={internalSlugs.includes(link.href?.replace(/^\//, '')) ? link.href : ''}
          onChange={(e) => onChange({ ...link, href: e.target.value })}
        >
          <option value="">‚Äì Internal Page ‚Äì</option>
          {internalSlugs.map((slug) => (
            <option key={slug} value={`/${slug}`}>
              /{slug}
            </option>
          ))}
        </select>

        <span className="text-gray-400 text-xs">or URL:</span>

        <input
          aria-invalid={isHrefMissing}
          type="text"
          className={`flex-1 bg-neutral-900 border rounded px-2 py-1 text-sm ${
            isHrefMissing ? 'border-red-500' : 'border-neutral-700'
          }`}
          placeholder="https://..."
          value={link.href}
          onChange={(e) => onChange({ ...link, href: e.target.value })}
        />
      </div>

      {isHrefMissing && (
        <p className="text-xs text-red-400">URL or internal page is required</p>
      )}

      {!internalSlugs.includes(link.href?.replace(/^\//, '')) && (
        <button
          className="text-xs text-green-400 underline mt-1"
          onClick={() => onChange({ ...link, href: createNewPage() })}
        >
          Create internal page for this link
        </button>
      )}
    </div>
  );
}

export default function QuickLinksEditor({
  links,
  onChange,
  template,
}: {
  links: Link[];
  onChange: (updated: Link[]) => void;
  template: Template;
}) {
  const sensors = useSensors(useSensor(PointerSensor));
  const internalSlugs = template?.data.pages.map((p) => p.slug) || [];
  const linkRefs = useRef<(HTMLDivElement | null)[]>([]);

  const createNewPage = (label: string): string => {
    const slug = label.toLowerCase().replace(/\s+/g, '-');
    const newPage = {
      id: crypto.randomUUID(),
      slug,
      title: label,
      content_blocks: [],
    };
    template?.data.pages.push(newPage);
    return `/${slug}`;
  };

  // Scroll to first invalid field on mount or update
  useEffect(() => {
    const firstInvalidIndex = links.findIndex(
      (link) => !link.label.trim() || !link.href.trim()
    );
    if (firstInvalidIndex !== -1) {
      const el = linkRefs.current[firstInvalidIndex];
      if (el) el.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  }, [links]);

  linkRefs.current = links.map((_, i) => linkRefs.current[i] ?? null);

  return (
    <div className="space-y-4">
      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={({ active, over }) => {
          if (!over || active.id === over.id) return;
          const oldIndex = links.findIndex((l) => l.label === active.id);
          const newIndex = links.findIndex((l) => l.label === over.id);
          const updated = arrayMove(links, oldIndex, newIndex);
          onChange(updated);
        }}
      >
        <SortableContext
          items={links.map((l) => l.label || `link-${l.href}`)}
          strategy={verticalListSortingStrategy}
        >
          {links.map((link, i) => (
            <div key={i} ref={(el) => {
              linkRefs.current[i] = el;
            }}>
              <SortableLinkItem
                id={link.label || `link-${i}`}
                index={i}
                link={link}
                onChange={(updatedLink) => {
                  const updated = [...links];
                  updated[i] = updatedLink;
                  onChange(updated);
                }}
                onRemove={() => {
                  const updated = [...links];
                  updated.splice(i, 1);
                  onChange(updated);
                }}
                internalSlugs={internalSlugs}
                createNewPage={() =>
                  createNewPage(link.label || `Page ${i + 1}`)
                }
                focusOnMount={i === 0}
              />
            </div>
          ))}
        </SortableContext>
      </DndContext>

      <button
        onClick={() => onChange([...links, { label: '', href: '' }])}
        className="flex items-center gap-2 text-purple-400 hover:text-purple-200 mt-2"
      >
        <PlusCircle size={16} /> Add New Link
      </button>
    </div>
  );
}

// components/admin/grid-map.tsx
'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import dynamic from 'next/dynamic';

import { supabase } from '@/admin/lib/supabaseClient';
import GridSidebar from './grid-sidebar';
import { resolveGeo } from '@/lib/resolveGeo';
import type { CityPoint } from '@/types/grid';

// Dynamically load the Leaflet map so it's only rendered on the client
const SafeLeafletMap = dynamic(() => import('./safe-leaflet-map'), {
  ssr: false,
});

const _cache: { points?: CityPoint[]; lastFetched?: number } = {};

export default function GridMap() {
  const [industry, setIndustry] = useState('');
  const [points, setPoints] = useState<CityPoint[]>([]);
  const [zoom, setZoom] = useState(4);
  const [showHotspotsOnly, setShowHotspotsOnly] = useState(false);
  const router = useRouter();

  useEffect(() => {
    const load = async () => {
      const now = Date.now();
      if (_cache.points && _cache.lastFetched && now - _cache.lastFetched < 60_000) {
        setPoints(_cache.points);
        return;
      }
      const { data: campaignLinks } = await supabase
        .from('campaign_leads')
        .select('campaign_id, lead_id');

      const { data: campaigns } = await supabase
        .from('campaigns')
        .select('id, name, city, state');
      const { data: leads } = await supabase
        .from('leads')
        .select('id, business_name, address_city, address_state, industry');

      const { data: domains } = await supabase
        .from('domains')
        .select('city, state, domain');

      const geo: Record<string, CityPoint> = {};

      for (const l of leads || []) {
        const key = `${l.address_city}, ${l.address_state}`;
        geo[key] = geo[key] || {
          city: l.address_city,
          state: l.address_state,
          leadsQty: 0,
          domains: 0,
          leads: [],
          domainNames: [],
          leadIds: [],
          industryCounts: {},
        };
        geo[key].leadsQty += 1;
        const isUnclaimed = !campaignLinks?.some((cl) => cl.lead_id === l.id);
        if (l.business_name) geo[key].leads.push({ id: l.id, name: l.business_name, isClaimed: !isUnclaimed, campaignId: '', industry: l.industry || '' });
        geo[key].leadIds.push(l.id);
        const indKey = (l.industry || '').trim().toLowerCase();
        geo[key].industryCounts![indKey] = (geo[key].industryCounts![indKey] || 0) + 1;
      }

      for (const d of domains || []) {
        const key = `${d.city}, ${d.state}`;
        geo[key] = geo[key] || {
          city: d.city,
          state: d.state,
          leads: 0,
          domains: 0,
          leadNames: [],
          domainNames: [],
          leadIds: [],
          industryCounts: {},
        };
        geo[key].domains += 1;
        if (d.domain) geo[key].domainNames.push(d.domain);
      }

      const enriched = await Promise.all(
        Object.values(geo).map(async (entry) => {
          const { lat, lon } = await resolveGeo(entry.city, entry.state);
          const primaryIndustry = Object.entries(entry.industryCounts || {}).reduce(
            (acc: [string, number], [ind, count]) =>
              typeof count === 'number' && count > acc[1] ? [ind, count] : acc,
            ['', 0]
          )[0];

          const campaignIds = new Set(
            campaignLinks
              ?.filter((cl) => entry.leadIds.includes(cl.lead_id))
              .map((cl) => cl.campaign_id)
          );

          const campaignNames = campaigns
            ?.filter((c) => campaignIds.has(c.id))
            .map((c) => c.name) ?? [];

          const unclaimed = entry.leadIds.filter(
            (id) => !campaignLinks?.some((cl) => cl.lead_id === id)
          ).length;
          const unclaimedByIndustry: Record<string, number> = {};

          for (const lead of entry.leads) {
            const ind = (lead.industry || '').trim().toLowerCase();
            if (!lead.isClaimed) {
              unclaimedByIndustry[ind] = (unclaimedByIndustry[ind] || 0) + 1;
            }
          }

          const has2PlusUnclaimedInSameIndustry = Object.values(unclaimedByIndustry).some(
            (count) => count >= 2
          );

          return {
            ...entry,
            lat,
            lon,
            industry: primaryIndustry,
            campaigns: campaignNames,
            unclaimedLeadCount: unclaimed,
            has2PlusUnclaimedInSameIndustry,
          };
        })
      );

      setPoints(enriched);
      _cache.points = enriched;
      _cache.lastFetched = now;
    };

    load();
  }, []);

  const getColor = (p: CityPoint) => {
    if (p.has2PlusUnclaimedInSameIndustry) return 'red';
    if (p.unclaimedLeadCount && p.unclaimedLeadCount >= 1) return 'yellow';
    if (p.leadsQty >= 2 && p.domains > 0) return 'green';
    if (p.leadsQty >= 2) return 'orange';
    if (p.domains > 0) return 'blue';
    if (p.leadsQty > 0) return 'yellow';
    return 'gray';
  };

  const getUnclaimedLeadCount = (p: CityPoint) => {
    return p.leads.filter((l) => !l.isClaimed).length;
  };

  const filteredPoints = points.filter((p) => {
    if (!industry || industry === '') return true;
    if (!p.industry) return false;
    return p.industry.trim().toLowerCase() === industry.trim().toLowerCase();
  });

  const visiblePoints = showHotspotsOnly
    ? filteredPoints.filter((p) => p.has2PlusUnclaimedInSameIndustry)
    : filteredPoints;

  return (
    <div className="p-6 text-white">
      <div className="flex items-center justify-between mb-6">

        <h1 className="text-3xl font-bold">üåç The Grid</h1>
        <button
          className="text-xs bg-zinc-800 hover:bg-zinc-700 px-3 py-1 rounded border border-zinc-600"
          onClick={() => {
            _cache.points = undefined;
            _cache.lastFetched = 0;
            location.reload();
          }}
        >
          üîÑ Refresh
        </button>
        <div className="md:w-2/5 w-full">
          <GridSidebar
            industry={industry}
            setIndustry={setIndustry}
            filteredPoints={filteredPoints}
            showHotspotsOnly={showHotspotsOnly}
            setShowHotspotsOnly={setShowHotspotsOnly}
            // visiblePoints={visiblePoints}
          />
        </div>
      </div>
      <div className="flex flex-col md:flex-row gap-6">
        <div className="w-full h-[600px] border border-gray-700 rounded overflow-hidden">
          <SafeLeafletMap
            points={visiblePoints}
            zoom={zoom}
            setZoom={setZoom}
            router={router}
            getColor={getColor}
          />
        </div>
      </div>
      <div className="text-xs text-zinc-400 mt-2">
          <div className="flex gap-4 items-center text-sm mt-2">
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-yellow-500 rounded-full"></span> 1 Lead</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-orange-500 rounded-full"></span> 2+ Leads</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-blue-500 rounded-full"></span> 1+ Domain</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-green-500 rounded-full"></span> Lead + Domain</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-red-600 rounded-full"></span> 2+ Unclaimed in 1 Industry</div>
          </div>
        </div>
    </div>
  );
}

