'use client';

import { useState } from 'react';
import type { Block } from '@/types/blocks';
import type { BlockEditorProps } from './index';
import BlockField from './block-field';
import CtaBlockRender from '@/components/admin/templates/render-blocks/cta'; // Live preview
import { cn } from '@/admin/lib/utils';
import { extractFieldErrors } from '../utils/extractFieldErrors';
import { BlockValidationError } from '@/hooks/validateTemplateBlocks';

type CtaBlock = Extract<Block, { type: 'cta' }>;
type CtaContent = CtaBlock['content'];

const SUGGESTED_URLS = ['/contact', '/get-quote', '/services', '/book-now'];

export default function CtaEditor({
  block,
  onSave,
  onClose,
  errors = {},
  template,
}: BlockEditorProps) {
  const ctaBlock = block as CtaBlock;
  const [content, setContent] = useState<CtaContent>(ctaBlock.content || {});
  const [errorsContent, setErrorsContent] = useState<{ label?: BlockValidationError[]; link?: BlockValidationError[] }>({});
  const fieldErrors = extractFieldErrors(errors as unknown as string[]);
  const update = <K extends keyof CtaContent>(key: K, value: CtaContent[K]) => {
    setContent((prev) => ({ ...prev, [key]: value }));
    setErrorsContent((prev) => ({ ...prev, [key]: undefined }));
  };

  const validate = (): boolean => {
    const nextErrors: typeof errorsContent = {};
    if (!content.label?.trim()) nextErrors.label = [{ message: 'Label is required', field: 'label' }];
    if (!content.link?.trim()) {
      nextErrors.link = [{ message: 'Link URL is required', field: 'link' }];
    } else if (!/^\/|^https?:\/\//.test(content.link)) {
      nextErrors.link = [{ message: 'Must start with / or http(s)://', field: 'link' }];
    }
    setErrorsContent(nextErrors);
    return Object.keys(nextErrors).length === 0;
  };

  const handleSave = () => {
    if (!validate()) return;
    onSave({ ...ctaBlock, content });
    onClose();
  };

  const isExternalLink = content.link?.startsWith('http');

  return (
    <div className="space-y-4 bg-black text-white border border-black p-4 rounded">
      <h3 className="text-lg font-semibold">Edit Call to Action</h3>

      <BlockField
        type="text"
        label="Label"
        value={content.label || ''}
        onChange={(v) => update('label', v)}
        error={errorsContent.label}
      />

      <BlockField
        type="text"
        label="Link URL"
        value={content.link || ''}
        onChange={(v) => update('link', v)}
        error={errorsContent.link}
        description={
          isExternalLink
            ? 'üîó External link (will open in new tab)'
            : '‚Ü©Ô∏è Internal link (on-site navigation)'
        }
      />

      {/* üí° Suggested links */}
      <div className="text-xs text-white/70">
        Suggested Links:{' '}
        {SUGGESTED_URLS.map((url) => (
          <button
            key={url}
            onClick={() => update('link', url)}
            className="px-2 py-0.5 bg-white text-black rounded-full text-xs font-mono mr-2 mb-1 hover:bg-gray-200"
          >
            {url}
          </button>
        ))}
      </div>

      {/* üîç Live Preview */}
      <div className="mt-6 border-t border-white/10 pt-4">
        <p className="text-sm text-white/70 mb-1">Live Preview:</p>
        <div className={cn('p-4 rounded bg-neutral-900 border border-white/10')}>
          <CtaBlockRender block={{ ...block, type: 'cta', content }} />
        </div>
      </div>

      <div className="flex justify-end gap-2 pt-4">
        <button
          onClick={onClose}
          className="text-sm px-4 py-2 border border-gray-500 rounded hover:bg-neutral-800"
        >
          Cancel
        </button>
        <button
          onClick={handleSave}
          className="text-sm px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
        >
          Save
        </button>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import type { BlockEditorProps } from './index';
import type { Block } from '@/types/blocks';
import BlockField from './block-field';
import { extractFieldErrors } from '../utils/extractFieldErrors';

type FAQBlock = Extract<Block, { type: 'faq' }>;

export default function FaqEditor({ block, onSave, onClose, errors = {}, template }: BlockEditorProps) {
  const faqBlock = block as FAQBlock;
  const [title, setTitle] = useState(faqBlock.content.title || 'Frequently Asked Questions');
  const [items, setItems] = useState(faqBlock.content.items || []);
  const fieldErrors = extractFieldErrors(errors as unknown as string[]);

  const updateItem = (i: number, key: 'question' | 'answer', value: string) => {
    const updated = [...items];
    updated[i] = { ...updated[i], [key]: value };
    setItems(updated);
  };

  return (
    <div className="p-4 space-y-4">
      <h3 className="text-lg font-semibold">Edit FAQ</h3>

      <BlockField
        type="text"
        label="Section Title"
        value={title}
        onChange={(v) => setTitle(v)}
        error={fieldErrors?.title}
      />

      {items.map((item, i) => (
        <div key={i} className="border border-white/10 p-3 rounded space-y-2">
          <BlockField
            type="text"
            label={`Question ${i + 1}`}
            value={item.question}
            onChange={(v) => updateItem(i, 'question', v)}
          />
          <BlockField
            type="text"
            label={`Answer ${i + 1}`}
            value={item.answer}
            onChange={(v) => updateItem(i, 'answer', v)}
          />
        </div>
      ))}

      <div className="flex gap-2">
        <button
          onClick={() => setItems([...items, { question: 'New Question', answer: 'New Answer' }])}
          className="text-sm text-green-400 underline"
        >
          + Add Q&A
        </button>
        {items.length > 1 && (
          <button
            onClick={() => setItems(items.slice(0, -1))}
            className="text-sm text-red-400 underline"
          >
            ‚àí Remove Last
          </button>
        )}
      </div>

      <div className="flex justify-end gap-2 pt-4">
        <button onClick={onClose} className="px-4 py-2 bg-gray-700 text-white rounded">
          Cancel
        </button>
        <button
          onClick={() => onSave({ ...faqBlock, content: { title, items } })}
          className="px-4 py-2 bg-blue-600 text-white rounded"
        >
          Save
        </button>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import type { FooterBlock } from '@/types/blocks';
import BlockField from './block-field';
import type { BlockEditorProps } from './index';
import { extractFieldErrors } from '../utils/extractFieldErrors';
import { getYear } from 'date-fns';
import QuickLinksEditor from '@/components/admin/fields/quick-links-editor';
import { Template } from '@/types/template';

export default function FooterEditor({
  block,
  onSave,
  onClose,
  errors = {},
  template,
}: BlockEditorProps) {
  const footerBlock = block as FooterBlock;
  const [content, setContent] = useState(footerBlock.content);
  const fieldErrors = extractFieldErrors(errors as unknown as string[]);

  const update = <K extends keyof typeof content>(
    key: K,
    value: typeof content[K]
  ) => setContent({ ...content, [key]: value });

  const areLinksValid = (links: { label: string; href: string }[]) =>
