            onChange={(v) => update('address', v)}
          />
          <BlockField
            type="text"
            label="City/State"
            value={content.cityState}
            onChange={(v) => update('cityState', v)}
          />
          <BlockField
            type="text"
            label="Phone"
            value={content.phone}
            onChange={(v) => update('phone', v)}
          />
          <BlockField
            type="text"
            label="Copyright"
            value={content.copyright || `${getYear(new Date())} ${content.businessName}`}
            onChange={(v) => update('copyright', v)}
          />
