import type { Block } from '@/types/blocks';
import type { Template } from '@/types/template';
import type { BlockValidationError } from '@/hooks/validateTemplateBlocks';

export type BlockEditorProps = {
  block: Block;
  onSave: (updated: Block) => void;
  onClose: () => void;
  errors?: Record<string, BlockValidationError[]>;
  template?: Template;
  fullBleed?: boolean;
};

// üåê Mapping of block type ‚Üí dynamic React component import
export const BLOCK_EDITORS: Record<
  Block['type'],
  () => Promise<{ default: React.FC<BlockEditorProps> }>
> = {
  text: () => import('./text-editor'),
  image: () => import('./image-editor'),
  video: () => import('./video-editor'),
  audio: () => import('./audio-editor'),
  quote: () => import('./quote-editor'),
  button: () => import('./button-editor'),
  grid: () => import('./json-fallback-editor'), // uses JSON fallback
  hero: () => import('./hero-editor'),
  services: () => import('./services-editor'),
  testimonial: () => import('./testimonial-editor'),
  cta: () => import('./cta-editor'),
  footer: () => import('./footer-editor'),
  service_areas: () => import('./service-areas-editor'),
  header: () => import('./header-editor'),
  faq: () => import('./faq-editor'),
  contact_form: () => import('./contact-form-editor').then(mod => ({ default: mod.ContactFormEditor })),
};

// üß† Optional preloader for a single block type (for speed optimization)
export function preloadBlockEditor(type: Block['type']) {
  if (type in BLOCK_EDITORS) {
    void BLOCK_EDITORS[type]();
  }
}

// ‚ö° Preload most-used editors (optional: call early in app)
export function preloadCommonEditors() {
  preloadBlockEditor('hero');
  preloadBlockEditor('text');
  preloadBlockEditor('cta');
  preloadBlockEditor('faq');
}

'use client';

import { useEffect, useState } from 'react';
import type { BlockEditorProps } from './index'; // ‚úÖ Reuse the shared type
import { extractFieldErrors } from '../utils/extractFieldErrors';

export default function JsonFallbackEditor({ block, onSave, onClose, errors = {}, template }: BlockEditorProps) {
  const [value, setValue] = useState<string>('');
  const [error, setError] = useState<string | null>(null);
  const fieldErrors = extractFieldErrors(errors as unknown as string[]); // now accepts Record<string, BlockValidationError[]>
  useEffect(() => {
    setValue(JSON.stringify(block, null, 2));
    setError(null);
  }, [block]);

  const handleSave = () => {
    try {
      const parsed = JSON.parse(value);
      onSave(parsed);
      onClose();
    } catch (e: any) {
      setError(e.message || 'Invalid JSON');
    }
  };

  return (
    <div className="space-y-4">
      <textarea
        className="w-full h-80 p-2 text-sm font-mono bg-neutral-800 text-white border border-gray-700 rounded"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      {error && <p className="text-sm text-red-400">{error}</p>}

      <div className="flex justify-end gap-2">
        <button onClick={onClose} className="px-4 py-2 bg-gray-700 text-white rounded">
          Cancel
        </button>
        <button onClick={handleSave} className="px-4 py-2 bg-blue-600 text-white rounded">
          Save
        </button>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useRef } from 'react';
import {
  MapContainer,
  TileLayer,
  Marker,
  Popup,
  useMap
} from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

type City = {
  name: string;
  lat: number;
  lng: number;
};

function FitBounds({ markers }: { markers: [number, number][] }) {
  const map = useMap();
  useEffect(() => {
    if (markers.length > 0) {
      const bounds = markers.length === 1
        ? L.latLng(markers[0])
        : L.latLngBounds(markers);
      map.fitBounds(bounds as any, { padding: [30, 30] });
    }
  }, [markers, map]);
  return null;
}

export function MapSection({
  lat,
  lng,
  selectedMarkers,
  unselectedMarkers,
  showAllPins
}: {
  lat: number;
  lng: number;
  selectedMarkers: City[];
  unselectedMarkers: City[];
  showAllPins: boolean;
}) {
  const mapRef = useRef<any>(null);

  useEffect(() => {
    return () => {
      if (mapRef.current) {
        mapRef.current.remove(); // üí• Explicit unmount
      }
    };
  }, []);

  const center = [lat, lng] as [number, number];
  const bounds = selectedMarkers.map(c => [c.lat, c.lng] as [number, number]);

  return (
    <MapContainer
      ref={mapRef}
      center={center}
      zoom={10}
      scrollWheelZoom={false}
      className="w-full h-full rounded z-0"
    >
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <FitBounds markers={bounds.length ? bounds : [center]} />
      {selectedMarkers.map((city, i) => (
        <Marker key={city.name + '-selected'} position={[city.lat, city.lng]}>
          <Popup>{city.name}</Popup>
        </Marker>
      ))}
      {showAllPins &&
        unselectedMarkers.map((city, i) => (
          <Marker
            key={city.name + '-unselected'}
            position={[city.lat, city.lng]}
            opacity={0.4}
          >
            <Popup>{city.name} (not selected)</Popup>
          </Marker>
        ))}
    </MapContainer>
  );
}

