import type { Block } from '@/types/blocks';
import { Dialog, DialogContent, DialogTitle } from '@/components/ui/dialog';
import BlockEditor from './block-editor';
import type { BlockValidationError } from '@/hooks/validateTemplateBlocks';

type Props = {
  block: Block | null;
  errors?: BlockValidationError[];
  onSave: (updated: Block) => void;
  onClose: () => void;
  onOpen: boolean;
  onReplaceWithAI?: (index: number) => void;
  onClone?: (index: number) => void;
  onShowPrompt?: (prompt: string, index: number) => void;
  onUndo?: (index: number) => void;
  onViewDiff?: (index: number) => void;
  undoAvailable?: boolean;
};

export default function BlockSidebar({
  block,
  errors = [],
  onSave,
  onClose,
  onOpen,
  onReplaceWithAI,
  onClone,
  onShowPrompt,
  onUndo,
  onViewDiff,
  undoAvailable = false,
}: Props) {
  if (!block || typeof block._id !== 'string') return null;

  return (
    <Dialog open={onOpen} onOpenChange={(open) => !open && onClose()}>
      <DialogContent className="max-w-2xl dark:bg-neutral-900 text-black dark:text-white overflow-y-auto max-h-screen p-4">
        <DialogTitle>
          Edit Block: <code className="text-sm lowercase">{block.type}</code>
        </DialogTitle>

        {errors.length > 0 && (
          <div className="mb-4 p-3 bg-red-100 text-red-800 rounded border border-red-300 dark:bg-red-900 dark:text-red-100 dark:border-red-700">
            <div className="font-semibold mb-1">Validation issues:</div>
            <ul className="list-disc list-inside space-y-1 text-sm">
              {errors.map((err, i) => (
                <li key={i}>
                  <code className="text-red-600 dark:text-red-300">{err.field}</code>: {err.message}
                </li>
              ))}
            </ul>
          </div>
        )}

        <BlockEditor
          block={block}
          onSave={(updated) => {
            onSave(updated);
            onClose();
          }}
          onClose={onClose}
        />

        {(onReplaceWithAI || onClone || onUndo || onViewDiff) && (
          <div className="mt-4 flex flex-wrap gap-2 text-sm">
            {onReplaceWithAI && (
              <button
                onClick={() => onReplaceWithAI(0)}
                className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-1 rounded"
              >
                ü§ñ Replace with AI
              </button>
            )}
            {onClone && (
              <button
                onClick={() => onClone(0)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
              >
                üìã Clone Block
              </button>
            )}
            {onUndo && (
              <button
                disabled={!undoAvailable}
                onClick={() => onUndo(0)}
                className="bg-yellow-500 hover:bg-yellow-600 disabled:opacity-50 text-black px-3 py-1 rounded"
              >
                ‚Ü© Undo
              </button>
            )}
            {onViewDiff && (
              <button
                onClick={() => onViewDiff(0)}
                className="bg-gray-700 hover:bg-gray-800 text-white px-3 py-1 rounded"
              >
                ü™û View Diff
              </button>
            )}
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import {
  GripVertical,
  Pencil,
  AlertTriangle,
  Sparkles,
  Trash2,
} from 'lucide-react';

import { Button, Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui';
import BlockSidebar from './block-sidebar';
import type { Block, BlockWithId, BlocksEditorProps } from '@/types/blocks';
import { normalizeBlock } from '@/types/blocks';
import type { BlockValidationError } from '@/hooks/validateTemplateBlocks';

interface BlocksEditorPropsExtended extends BlocksEditorProps {
  onReplaceWithAI?: (index: number) => void;
  onEdit?: (index: number) => void;
}

interface SortableBlockProps {
  block: BlockWithId;
  index: number;
  errors?: BlockValidationError[];
  onEdit: (index: number) => void;
  onReplaceWithAI?: (index: number) => void;
  onDelete: (index: number) => void;
}

function SortableBlock({
  block,
  index,
  errors = [],
  onEdit,
  onReplaceWithAI,
  onDelete,
}: SortableBlockProps) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: block._id });
  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const invalid = errors.length > 0;
  const isAI = block.tags?.includes('ai');
  const wasAutofixed = block.tags?.includes('autofixed');

  return (
    <div
      ref={setNodeRef}
      style={style}
      id={`block-${block._id}`}
      className={`flex flex-col gap-2 border rounded p-3 ${
        invalid
          ? 'border-red-500 bg-red-500/10 animate-pulse shadow-red-500/30 shadow-md'
          : 'bg-white/5 border-white/10'
      }`}
    >
      <div className="flex items-center justify-between gap-2">
        <div className="flex items-center gap-2">
          <GripVertical
            className="w-4 h-4 cursor-move text-muted-foreground"
            {...attributes}
            {...listeners}
          />
          <span className="text-sm font-medium text-white">{block.type}</span>
          {wasAutofixed && (
            <Tooltip>
              <TooltipTrigger>
                <span className="text-xs text-yellow-300 ml-1 italic">üõ† Autofixed</span>
              </TooltipTrigger>
              <TooltipContent>This block was automatically repaired to pass validation.</TooltipContent>
            </Tooltip>
          )}
          {isAI && <span className="text-xs text-purple-400 ml-2">üîÆ AI</span>}
          {invalid && (
            <span className="flex items-center gap-1 text-xs text-red-400 ml-2">
              <AlertTriangle className="w-3 h-3" />
              Invalid block
            </span>
          )}
        </div>

        <div className="flex gap-1">
          <Button size="sm" variant="ghost" onClick={() => onEdit(index)}>
            <Pencil className="w-4 h-4" />
          </Button>
          {onReplaceWithAI && (
            <Button
              size="sm"
              variant="ghost"
              onClick={() => {
                if (confirm('Replace this block with an AI-generated version?')) {
                  onReplaceWithAI(index);
                }
              }}
            >
              <Sparkles className="w-4 h-4 text-purple-400" />
            </Button>
          )}
          <Button
            size="sm"
            variant="ghost"
            className="text-red-500 hover:text-red-700"
            onClick={() => {
              if (confirm('Delete this block?')) {
                onDelete(index);
              }
            }}
          >
            <Trash2 className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {invalid && (
        <ul className="text-xs text-red-300 list-disc list-inside pl-1 pt-1 space-y-1">
          {errors.map((err, i) => (
            <li key={i}>
              <code>{err.field}</code>: {err.message}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export const BlocksEditor = ({
  blocks,
  onChange,
  industry = 'default',
  onReplaceWithAI,
  onEdit,
}: BlocksEditorPropsExtended) => {
  const [selectedIndex, setSelectedIndex] = useState<number | null>(null);

  const ensureIds = (blocks: Block[]): BlockWithId[] =>
    blocks.map((block) => ({
      ...block,
      _id: block._id ?? crypto.randomUUID(),
    }));

  const blocksWithIds = ensureIds(blocks);

  const handleUpdate = (index: number, updatedBlock: Block) => {
    const updatedBlocks = [...blocksWithIds];
    updatedBlocks[index] = normalizeBlock(updatedBlock);
    onChange(updatedBlocks);
  };

  useEffect(() => {
    const first = blocksWithIds.find((b) => (b as any)._meta?.errorMessages?.length > 0);
    if (first) {
      const el = document.getElementById(`block-${first._id}`);
      if (el) el.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  }, [blocksWithIds]);

  return (
    <div className="space-y-4 rounded p-3">
      <DndContext
        collisionDetection={closestCenter}
        onDragEnd={({ active, over }) => {
          if (active.id !== over?.id) {
            const oldIndex = blocksWithIds.findIndex((b) => b._id === active.id);
            const newIndex = blocksWithIds.findIndex((b) => b._id === over?.id);
            const reordered = arrayMove(blocksWithIds, oldIndex, newIndex);
            onChange(reordered);
          }
        }}
      >
        <SortableContext items={blocksWithIds.map((b) => b._id)} strategy={verticalListSortingStrategy}>
          {blocksWithIds.map((block, index) => (
            <SortableBlock
              key={block._id}
              block={block}
              index={index}
              errors={(block as any)._meta?.errorMessages as BlockValidationError[] || []}
              onReplaceWithAI={onReplaceWithAI}
              onEdit={setSelectedIndex}
              onDelete={(deleteIndex) => {
                const updated = [...blocksWithIds];
                updated.splice(deleteIndex, 1);
                onChange(updated);
              }}
            />
          ))}
        </SortableContext>
      </DndContext>

      {selectedIndex !== null && (
        <BlockSidebar
          block={blocksWithIds[selectedIndex]}
          errors={(blocksWithIds[selectedIndex] as any)._meta?.errorMessages || []}
          onSave={(updatedBlock) => {
            handleUpdate(selectedIndex, updatedBlock);
            setSelectedIndex(null);
          }}
          onClose={() => setSelectedIndex(null)}
          onOpen={true}
          onReplaceWithAI={() => {}}
          onClone={() => {}}
          onShowPrompt={() => {}}
          onUndo={() => {}}
          onViewDiff={() => {}}
          undoAvailable={false}
        />
      )}
    </div>
  );
};

