export default function EditTemplatePage() {
  const { slug } = useParams() as { slug: string };
  const [template, setTemplate] = useState<Snapshot | null>(null);
  const supabase = useMemo(() => createBrowserClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
  ), []);

  useEffect(() => {
    if (!slug) return;

    supabase
      .from('templates')
      .select('*')
      .eq('slug', slug)
      .single()
      .then(({ data, error }) => {
        if (error) console.error(error);
        else setTemplate(data);
      });
  }, [slug]);

  if (!template) {
    return <div className="p-6 text-muted-foreground text-sm italic">Loading template‚Ä¶</div>;
  }

  return (
    <TemplateEditor
      templateName={template.template_name}
      initialData={template}
    />
  );
}

