  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.6 : 1,
    zIndex: isDragging ? 10 : 1,
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      className="group relative border rounded mb-6"
    >
      <div
        {...listeners}
        className="absolute -left-6 top-2 text-gray-300 group-hover:text-gray-600 cursor-grab"
      >
        ‚ò∞
      </div>
      {children}
    </div>
  );
}

function getPageTitle(page: Page): string {
  const blocks = page.content_blocks || [];
  const hero = blocks.find((b) => b.type === 'hero') as any;
  if (hero?.content?.title) return hero.content.title;

  const text = blocks.find((b) => b.type === 'text') as any;
  if (text?.content?.value) return text.content.value.slice(0, 40) + '...';

  return page.slug;
}

export default function ReorderablePageList({
  pages,
  colorScheme,
  onBlockClick,
  onReorder,
}: ReorderablePageListProps) {
  const sensors = useSensors(useSensor(PointerSensor));
  const [settingsPage, setSettingsPage] = useState<Page | null>(null);
  const [search, setSearch] = useState('');

  const getId = (page: Page, index: number) => page.slug || page.id || `page-${index}`;

  const filteredPages = pages.filter(
    (p) =>
      p.slug?.toLowerCase().includes(search.toLowerCase()) ||
      getPageTitle(p).toLowerCase().includes(search.toLowerCase())
  );

  const handleSlugChange = (index: number, newSlug: string) => {
    const updated = [...pages];
    updated[index].slug = newSlug;
    onReorder(updated);
  };

  const handleDelete = (index: number) => {
    const confirmed = confirm('Delete this page?');
    if (!confirmed) return;
    const updated = pages.filter((_, i) => i !== index);
    onReorder(updated);
  };

  const handleDuplicate = (index: number) => {
    const original = pages[index];
    const clone: Page = {
      ...original,
      slug: original.slug + '-copy',
      id: `${original.id || original.slug || 'page'}-${Date.now()}`,
    };
    const updated = [...pages.slice(0, index + 1), clone, ...pages.slice(index + 1)];
    onReorder(updated);
  };

  const handleAddPage = () => {
    const newPage: Page = {
      id: `page-${Date.now()}`,
      slug: `new-page-${pages.length + 1}`,
      content_blocks: [],
      title: `New Page ${pages.length + 1}`,
    };
    onReorder([...pages, newPage]);
  };

  const handlePageDrag = ({ active, over }: { active: any; over: any }) => {
    if (!over || active.id === over.id) return;
    const oldIndex = pages.findIndex((p, i) => getId(p, i) === active.id);
    const newIndex = pages.findIndex((p, i) => getId(p, i) === over.id);
    const updated = arrayMove(pages, oldIndex, newIndex);
    onReorder(updated);
  };

  const handlePageSave = (updatedPage: Page) => {
    const updatedPages = pages.map((p) =>
      p.id === updatedPage.id ? updatedPage : p
    );
    onReorder(updatedPages);
  };

  useEffect(() => {
    const saved = localStorage.getItem('page-order');
    if (saved) {
      const order = JSON.parse(saved) as string[];
      const reordered = order
        .map((id) => pages.find((p) => p.id === id))
        .filter(Boolean) as Page[];
      const missing = pages.filter((p) => !order.includes(p.id!));
      if (reordered.length > 0) onReorder([...reordered, ...missing]);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('page-order', JSON.stringify(pages.map((p) => p.id)));
  }, [pages]);

  return (
    <>
      <div className="mb-4 flex justify-between items-center">
        <Input
          placeholder="Search by slug or title..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-full max-w-md"
        />
      </div>

      <DndContext sensors={sensors} collisionDetection={closestCenter} onDragEnd={handlePageDrag}>
        <SortableContext items={filteredPages.map(getId)} strategy={verticalListSortingStrategy}>
          {filteredPages.map((page, index) => (
            <SortablePage key={getId(page, index)} id={getId(page, index)}>
              <div className="px-4 py-3 bg-muted/50 border-b flex items-center justify-between gap-3">
                <div className="flex items-center gap-2 w-full max-w-sm">
                  <Input
                    value={page.slug}
                    onChange={(e) => handleSlugChange(index, e.target.value)}
                    className="w-full text-sm"
                  />
                  {page.meta?.visible === false ? (
                    <span className="px-2 py-0.5 text-xs rounded bg-yellow-200 text-yellow-800">
                      Hidden
                    </span>
                  ) : (
                    <span className="px-2 py-0.5 text-xs rounded bg-green-200 text-green-800">
                      Visible
                    </span>
                  )}
                </div>
                <div className="flex gap-2">
                  <Button size="sm" variant="ghost" onClick={() => setSettingsPage(page)}>
                    ‚öôÔ∏è
                  </Button>
                  <Button size="sm" variant="ghost" onClick={() => handleDuplicate(index)}>
                    üìÑ
                  </Button>
                  <Button size="sm" variant="destructive" onClick={() => handleDelete(index)}>
                    üóëÔ∏è
                  </Button>
                </div>
              </div>

              <ReorderableBlockList
                data={{ pages: [page] }}
                colorScheme={colorScheme}
                onBlockClick={onBlockClick}
                onReorder={(updatedPageData) => {
                  const updatedPages = [...pages];
                  updatedPages[index] = updatedPageData.pages[0];
                  onReorder(updatedPages);
                }}
              />
            </SortablePage>
          ))}
        </SortableContext>
      </DndContext>

      <div className="text-center my-6">
        <Button onClick={handleAddPage}>‚ûï Add New Page</Button>
      </div>

      <PageSettingsModal
        open={!!settingsPage}
        page={settingsPage}
        onClose={() => setSettingsPage(null)}
        onSave={handlePageSave}
      />
    </>
  );
}

// components/admin/templates/sortable-block-board.tsx
'use client';

import {
  DndContext,
  closestCenter,
  useSensor,
  useSensors,
  PointerSensor,
} from '@dnd-kit/core';
import {
  SortableContext,
  arrayMove,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';

import { motion } from 'framer-motion';
import { useState } from 'react';
import type { Template, Page } from '@/types/template';
import { BlockSchema } from '@/admin/lib/zod/blockSchema';
import { GripVertical } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useSortable } from '@dnd-kit/sortable';

type Props = {
  template: Template;
  onChange: (t: Template) => void;
  onLivePreviewUpdate: (d: Template['data']) => void;
  maxBlocksPerPage?: number;
};

export function SortableBlockBoard({ template, onChange, onLivePreviewUpdate, maxBlocksPerPage = 6 }: Props) {
  const sensors = useSensors(useSensor(PointerSensor));
  const [draggingBlockId, setDraggingBlockId] = useState<string | null>(null);

  const handleDragStart = (event: any) => {
    setDraggingBlockId(event.active.id);
  };

  const handleDragEnd = (event: any) => {
    const { active, over } = event;
    setDraggingBlockId(null);

    if (!over || active.id === over.id) return;

    const fromPage = template.data.pages.find((p) =>
      p.content_blocks.some((b) => b._id === active.id)
    );
    const toPage = template.data.pages.find((p) =>
      p.content_blocks.some((b) => b._id === over.id)
    );

    if (!fromPage || !toPage) return;

    const fromIndex = fromPage.content_blocks.findIndex((b) => b._id === active.id);
    const toIndex = toPage.content_blocks.findIndex((b) => b._id === over.id);

    // Prevent moving to a page over max limit
    if (
      fromPage.id !== toPage.id &&
      toPage.content_blocks.length >= maxBlocksPerPage
    ) {
      return alert('This page already has the max number of blocks');
    }

    const fromBlocks = [...fromPage.content_blocks];
    const [moved] = fromBlocks.splice(fromIndex, 1);

    const toBlocks = [...toPage.content_blocks];
    toBlocks.splice(toIndex, 0, moved);

    const updatedPages = template.data.pages.map((p) => {
      if (p.id === fromPage.id) return { ...p, content_blocks: fromBlocks };
      if (p.id === toPage.id) return { ...p, content_blocks: toBlocks };
      return p;
    });

    const updated = { ...template, data: { ...template.data, pages: updatedPages } };
    onChange(updated);
    onLivePreviewUpdate(updated.data);
  };

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCenter}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
    >
      <div className="space-y-6">
        {template.data.pages.map((page, index) => (
          <div key={page.id} className="border rounded p-4 bg-muted/50 space-y-2 group">
            <div className="flex justify-between items-center">
              <div className="font-semibold text-sm">{page.title || `Page ${index + 1}`}</div>
              <span className="text-xs text-muted-foreground">
                {page.content_blocks.length} / {maxBlocksPerPage}
              </span>
            </div>

            <SortableContext
              items={page.content_blocks.map((b) => b._id).filter((id): id is string => id !== undefined)}
              strategy={verticalListSortingStrategy}
            >
              <div className="space-y-2 min-h-[40px]">
                {page.content_blocks.map((block) => (
                  <SortableBlock key={block._id} block={block} />
                ))}
                {page.content_blocks.length === 0 && (
                  <div className="text-xs text-muted-foreground italic">Drop a block here</div>
                )}
              </div>
            </SortableContext>
          </div>
        ))}
      </div>
    </DndContext>
  );
}

function SortableBlock({ block }: { block: any }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: block._id });

  const style = {
    transform: transform ? `translate(${transform.x}px, ${transform.y}px)` : undefined,
    transition,
  };

  const isValid = BlockSchema.safeParse(block).success;

  return (
    <motion.div
      ref={setNodeRef}
      layout
      style={style}
      className={`flex items-center justify-between p-3 rounded bg-white/5 border ${
        isDragging ? 'border-blue-500' : isValid ? 'border-gray-700' : 'border-red-500'
      } group relative`}
    >
      <div
        {...listeners}
        {...attributes}
        className="absolute -left-6 top-2 cursor-grab text-gray-500 group-hover:text-white opacity-0 group-hover:opacity-100"
      >
        <GripVertical size={16} />
      </div>
      <div className="text-sm">{block?.type || '(unknown)'}</div>
      {!isValid && (
        <Button size="icon" variant="outline" className="text-xs">
          Fix
        </Button>
      )}
    </motion.div>
  );
}

'use client';

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  SortableContext,
  verticalListSortingStrategy,
  arrayMove,
} from '@dnd-kit/sortable';

import SortableBlock from './sortable-block';
import { BlockSchema } from '@/admin/lib/zod/blockSchema';
import { motion, AnimatePresence } from 'framer-motion';
import { Button } from '@/components/ui/button';
import { Pencil, Trash2, Plus } from 'lucide-react';
import { BlockWithId } from '@/types/blocks';

type Props = {
  blocks: BlockWithId[];
  onChange: (next: BlockWithId[]) => void;
  onBlockEdit?: (block: BlockWithId, index: number) => void;
  onInsertBlock?: (atIndex: number) => void;
};

export function SortableBlockList({
  blocks,
  onChange,
  onBlockEdit,
  onInsertBlock,
}: Props) {
  const sensors = useSensors(useSensor(PointerSensor));

  const handleDragEnd = (event: any) => {
    const { active, over } = event;
    if (!over || active.id === over.id) return;

    const oldIndex = blocks.findIndex((b) => b._id === active.id);
    const newIndex = blocks.findIndex((b) => b._id === over.id);
    if (oldIndex < 0 || newIndex < 0) return;

    const reordered = arrayMove(blocks, oldIndex, newIndex);
    onChange(reordered);
  };

  const handleDelete = (index: number) => {
    if (!confirm('Remove this block?')) return;
    const next = blocks.filter((_, i) => i !== index);
    onChange(next);
  };

  const handleInsert = (atIndex: number) => {
    if (onInsertBlock) onInsertBlock(atIndex);
  };

  return (
    <DndContext
      collisionDetection={closestCenter}
      sensors={sensors}
      onDragEnd={handleDragEnd}
    >      
      <SortableContext items={blocks.map((b) => b._id)} strategy={verticalListSortingStrategy}>
        <div className="space-y-2">
          <AnimatePresence>
            {blocks.map((block, i) => {
              const isValid = BlockSchema.safeParse(block).success;

              return (
                <SortableBlock block={block} key={block._id} index={i} onEdit={() => {}}>
                  <motion.div
                    layout
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    className={`rounded border px-3 py-2 text-sm flex justify-between items-center ${
                      isValid
                        ? 'border-gray-700 bg-white/5'
                        : 'border-red-500 bg-red-500/10'
                    }`}
                  >
                    <div>{block?.type || '(unknown)'}</div>
                    <div className="flex gap-1">
                      {onBlockEdit && (
                        <Button
                          size="icon"
                          variant="ghost"
                          onClick={() => onBlockEdit(block, i)}
                        >
                          <Pencil className="w-3 h-3" />
                        </Button>
                      )}
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => handleDelete(i)}
                      >
                        <Trash2 className="w-3 h-3" />
                      </Button>
                    </div>
                  </motion.div>
                </SortableBlock>
              );
            })}

            {blocks.length === 0 && (
              <motion.div
                key="empty-state"
                layout
                initial={{ opacity: 0 }}
                animate={{ opacity: 0.5 }}
                exit={{ opacity: 0 }}
                className="text-xs text-muted-foreground italic px-2 py-4 border border-dashed border-gray-600 rounded text-center"
              >
                No blocks yet. Drag or insert to begin.
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </SortableContext>
    </DndContext>
  );
}

'use client';

import { CSS } from '@dnd-kit/utilities';
import { useSortable } from '@dnd-kit/sortable';
import { GripVertical, Pencil, AlertTriangle, Sparkles } from 'lucide-react';
import { Button, Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui';
import type { Block } from '@/types/blocks';
import type { BlockValidationError } from '@/hooks/validateTemplateBlocks';

type Props = {
  block: Block;
  index: number;
  errors?: BlockValidationError[];
  onEdit: (index: number) => void;
  onReplaceWithAI?: (index: number) => void;
  onUndo?: (index: number) => void;
  onViewDiff?: (index: number) => void;
  undoAvailable?: boolean;
  children: React.ReactNode;
};

export default function SortableBlock({
  block,
  index,
  errors = [],
  onEdit,
  onReplaceWithAI,
  onUndo,
  onViewDiff,
  undoAvailable,
}: Props) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({
    id: block._id as string,
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const invalid = errors.length > 0;
  const isAI = block.tags?.includes('ai');
  const wasAutofixed = block.tags?.includes('autofixed');

  return (
    <div
      ref={setNodeRef}
      style={style}
      id={`block-${block._id}`}
      className={`flex flex-col gap-2 border rounded p-3 ${
        invalid
          ? 'border-red-500 bg-red-500/10 animate-pulse shadow-red-500/30 shadow-md'
          : 'bg-white/5 border-white/10'
      }`}
    >
      {/* Block Header */}
      <div className="flex items-center justify-between gap-2">
        <div className="flex items-center gap-2">
          <GripVertical
            className="w-4 h-4 cursor-move text-muted-foreground"
            {...attributes}
            {...listeners}
          />
          <span className="text-sm font-medium text-white">{block.type}</span>
          {wasAutofixed && (
            <Tooltip>
              <TooltipTrigger>
                <span className="text-xs text-yellow-300 ml-1 italic">üõ† Autofixed</span>
              </TooltipTrigger>
              <TooltipContent>
                This block was automatically repaired to pass validation.
              </TooltipContent>
            </Tooltip>
          )}
          {isAI && <span className="text-xs text-purple-400 ml-2">üîÆ AI</span>}
          {invalid && (
            <span className="flex items-center gap-1 text-xs text-red-400 ml-2">
              <AlertTriangle className="w-3 h-3" />
              Invalid block
            </span>
          )}
        </div>

        <div className="flex gap-1">
          <Button size="sm" variant="ghost" onClick={() => onEdit(index)}>
            <Pencil className="w-4 h-4" />
          </Button>
          {onReplaceWithAI && (
            <Button
              size="sm"
              variant="ghost"
              onClick={() => {
                if (confirm('Replace this block with an AI-generated version?')) {
                  onReplaceWithAI(index);
                }
              }}
            >
              <Sparkles className="w-4 h-4 text-purple-400" />
            </Button>
          )}
        </div>
      </div>

      {/* Validation Errors */}
      {invalid && (
        <ul className="text-xs text-red-300 list-disc list-inside pl-1 pt-1 space-y-1">
          {errors.map((err, i) => (
            <li key={i}>
              <code>{err.field}</code>: {err.message}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

