              toast.success('Applied "Show Header: true" to all pages');
            }}
            className="px-3 py-1 text-xs bg-green-600 hover:bg-green-500 rounded text-white"
          >
            Apply Header: true
          </button>
          <button
            onClick={() => {
              const updatedPages = template.data.pages.map((p) => ({ ...p, show_header: false }));
              onChange({ ...template, data: { ...template.data, pages: updatedPages } });
              toast.success('Applied "Show Header: false" to all pages');
            }}
            className="px-3 py-1 text-xs bg-red-600 hover:bg-red-500 rounded text-white"
          >
            Apply Header: false
          </button>
          <button
            onClick={() => {
              const updatedPages = template.data.pages.map((p) => ({ ...p, show_footer: true }));
              onChange({ ...template, data: { ...template.data, pages: updatedPages } });
              toast.success('Applied "Show Footer: true" to all pages');
            }}
            className="px-3 py-1 text-xs bg-green-600 hover:bg-green-500 rounded text-white"
          >
            Apply Footer: true
          </button>
          <button
            onClick={() => {
              const updatedPages = template.data.pages.map((p) => ({ ...p, show_footer: false }));
              onChange({ ...template, data: { ...template.data, pages: updatedPages } });
              toast.success('Applied "Show Footer: false" to all pages');
            }}
            className="px-3 py-1 text-xs bg-red-600 hover:bg-red-500 rounded text-white"
          >
            Apply Footer: false
          </button>
        </div>

        <button
          onClick={() => {
            if (confirm('Reset all per-page header/footer overrides?')) {
              const resetPages = template.data.pages.map((p) => ({
                ...p,
                show_header: undefined,
                show_footer: undefined,
              }));
              onChange({ ...template, data: { ...template.data, pages: resetPages } });
              toast.success('Cleared all overrides (falling back to template default)');
            }
          }}
          className="mt-3 text-sm bg-yellow-700 hover:bg-yellow-600 text-white px-4 py-2 rounded"
        >
          ‚ü≤ Reset All Page Overrides
        </button>
      </div>
    </div>
  );
}

import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { toast } from 'react-hot-toast';
import SeoPreviewThumbnail from '@/components/admin/seo-preview-thumbnail';
import SeoPreviewTestLinks from '@/components/admin/seo-preview-test-links';
import SeoShareCardPanel from '@/components/admin/seo-share-card-panel';
import OGBulkRebuild from '@/components/admin/og-bulk-rebuild';
import OGPreviewModal from '@/components/admin/og-preview-modal';
import type { Template } from '@/types/template';

type Props = {
  template: Template;
  onChange: (updated: Template) => void;
  showOGPreview: boolean;
  setShowOGPreview: (open: boolean) => void;
};

const MAX_TITLE_LENGTH = 60;
const MAX_DESC_LENGTH = 160;

export default function SeoSettingsTab({ template, onChange, showOGPreview, setShowOGPreview }: Props) {
  const handleMetaChange = (key: 'title' | 'description', value: string) => {
    const meta = { ...template.meta, [key]: value };
    onChange({ ...template, meta });
  };

  return (
    <div className="space-y-4">
      <div>
        <Label>SEO Title</Label>
        <Input
          value={template.meta?.title || ''}
          onChange={(e) => handleMetaChange('title', e.target.value)}
          placeholder="e.g. Best Tow Trucks in Phoenix"
        />
        <p className={`text-xs mt-1 ${template.meta?.title?.length && template.meta.title.length > MAX_TITLE_LENGTH ? 'text-red-400' : 'text-muted-foreground'}`}>
          {template.meta?.title?.length || 0} / {MAX_TITLE_LENGTH} characters
        </p>
      </div>

      <div>
        <Label>SEO Description</Label>
        <Input
          value={template.meta?.description || ''}
          onChange={(e) => handleMetaChange('description', e.target.value)}
          placeholder="e.g. QuickSites helps you launch a towing site fast."
        />
        <p className={`text-xs mt-1 ${template.meta?.description?.length && template.meta.description.length > MAX_DESC_LENGTH ? 'text-red-400' : 'text-muted-foreground'}`}>
          {template.meta?.description?.length || 0} / {MAX_DESC_LENGTH} characters
        </p>
      </div>

      <SeoPreviewThumbnail pageUrl={template.slug} ogImageUrl={template.meta?.ogImage || ''} />
      <SeoPreviewTestLinks url={template.slug} />
      <SeoShareCardPanel url={template.slug} />

      <OGBulkRebuild
        slug={template.slug}
        endpoint="/api/og/rebuild-all"
        onResult={(results: Record<string, string>) => {
          const failures = Object.entries(results).filter(([_, result]) => result.startsWith('‚ùå'));
          if (failures.length > 0) {
            toast.error(`OG rebuild failed on ${failures.length} page(s): ${failures.join(', ')}`);
          } else {
            toast.success('‚úÖ All OG images rebuilt successfully');
          }
        }}
      />

      <button
        onClick={() => setShowOGPreview(true)}
        className="text-sm underline text-indigo-400 hover:text-indigo-300"
      >
        View Current OG Image
      </button>

      <OGPreviewModal
        open={showOGPreview}
        onOpenChange={setShowOGPreview}
        ogImageUrl={template.meta?.ogImage || ''}
      />
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { RotateCcw, RotateCw } from 'lucide-react';
import { Button } from '@/components/ui';
import { saveAsTemplate } from '@/admin/lib/saveAsTemplate';
import { createSharedPreview } from '@/admin/lib/createSharedPreview';
import { supabase } from '@/admin/lib/supabaseClient';
import toast from 'react-hot-toast';
import type { Template } from '@/types/template';

export function TemplateActionToolbar({
  template,
  autosaveStatus,
  onSaveDraft,
  onUndo,
  onRedo,
}: {
  template: Template;
  autosaveStatus?: string;
  onSaveDraft?: () => void;
  onUndo: () => void;
  onRedo: () => void;
}) {
  const router = useRouter();
  const [status, setStatus] = useState('Draft');
  const [versions, setVersions] = useState<any[]>([]);

  useEffect(() => {
    if (template?.template_name) {
      supabase
        .from('template_versions')
        .select('id, commit_message, created_at')
        .eq('template_name', template.template_name)
        .order('created_at', { ascending: false })
        .then(({ data }) => {
          if (data) setVersions(data);
        });

      setStatus(template?.published ? 'Published' : 'Draft');
    }
  }, [template?.template_name]);

  useEffect(() => {
    const handleHotkey = (e: KeyboardEvent) => {
      if (e.metaKey && e.key === 's') {
        e.preventDefault();
        onSaveDraft?.();
      }
      if (e.metaKey && e.shiftKey && e.key.toLowerCase() === 's') {
        e.preventDefault();
        handleShare();
      }
    };
    window.addEventListener('keydown', handleHotkey);
    return () => window.removeEventListener('keydown', handleHotkey);
  }, [template]);

  const handleSaveAs = async (type: 'template' | 'site') => {
    const newId = await saveAsTemplate(template, type);
    if (newId) {
      toast.success('Template copied');
      router.push(`/admin/templates?selected=${newId}`);
    } else {
      toast.error('Failed to copy template');
    }
  };

  const handleShare = async () => {
    const id = await createSharedPreview({
      templateId: template.id,
      templateName: template.template_name,
      templateData: template.data,
    });
    if (id) {
      toast.success('Preview shared!');
      router.push(`/shared/${id}`);
    } else {
      toast.error('Share failed');
    }
  };

  return (
    <div className="fixed bottom-4 left-1/2 -translate-x-1/2 z-40 rounded-lg bg-gray-900 border border-gray-700 px-6 py-3 shadow-xl max-w-5xl w-[95%] flex justify-between items-center text-white opacity-80">
      <div className="text-sm font-medium flex gap-4 items-center">
        <span>üìÑ {template.template_name}</span>
        <span
          className={`text-xs px-2 py-1 rounded ${
            status === 'Published' ? 'bg-green-600' : 'bg-yellow-600'
          }`}
        >
          {status}
        </span>
        {autosaveStatus && (
          <span className="text-xs text-gray-400 italic">üíæ {autosaveStatus}</span>
        )}
        <Button size="icon" variant="ghost" onClick={onUndo} title="Undo (‚åòZ)">
          <RotateCcw className="w-4 h-4" />
        </Button>
        <Button size="icon" variant="ghost" onClick={onRedo} title="Redo (‚áß‚åòZ)">
          <RotateCw className="w-4 h-4" />
        </Button>
      </div>

      <div className="flex items-center gap-3">
        <select
          className="bg-gray-800 border border-gray-600 text-sm text-white rounded px-2 py-1"
          onChange={async (e) => {
          const versionId = e.target.value;
          if (!versionId || versionId === 'View Version') return;

          const { data, error } = await supabase
            .from('template_versions')
            .select('snapshot')
            .eq('id', versionId)
            .single();

          if (error || !data?.snapshot) {
            toast.error('Failed to load version');
            return;
          }

          if (confirm('Restore this version? This will overwrite the current draft.')) {
            const restored = {
              ...template,
              data: data.snapshot,
            };
            toast.success('Version restored!');
            location.reload();
          }
        }}
        >
          <option>View Version</option>
          {versions.map((v) => (
            <option key={v.id} value={v.id}>
              {v.commit_message || 'Untitled'} ‚Äî {new Date(v.created_at).toLocaleDateString()}
            </option>
          ))}
        </select>

        <Button size="sm" variant="secondary" onClick={onSaveDraft}>
          Save
        </Button>
        <Button size="sm" variant="secondary" onClick={() => handleSaveAs('site')}>
          Duplicate as a Site
        </Button>
        <Button size="sm" variant="secondary" onClick={() => handleSaveAs('template')}>
          Duplicate as a Template
        </Button>
        <Button size="sm" variant="secondary" onClick={handleShare}>
          Share Snapshot
        </Button>
      </div>
    </div>
  );
}

