import { useEffect, useState } from 'react';
import { toast } from 'react-hot-toast';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export function useEditorLoader(slug: string) {
  const [record, setRecord] = useState<any | null>(null);
  const [mode, setMode] = useState<'template' | 'site' | null>(null);

  useEffect(() => {
    const load = async () => {
      const { data: site, error: siteError } = await supabase
        .from('sites')
        .select('*')
        .eq('slug', slug)
        .single();

      if (site) {
        setMode('site');
        setRecord(site);
        return;
      }

      const { data: template, error: templateError } = await supabase
        .from('templates')
        .select('*')
        .eq('slug', slug)
        .single();

      if (template) {
        const draftKey = `draft-${template.id}`;
        const savedDraft = localStorage.getItem(draftKey);

        if (savedDraft) {
          try {
            const parsed = JSON.parse(savedDraft);
            if (parsed && parsed.pages && parsed.pages.length > 0) {
              toast((t) => (
                <span>
                  üìù A draft was found.
                  <button
                    className="ml-2 underline text-blue-400"
                    onClick={() => {
                      toast.dismiss(t.id);
                      setRecord({ ...template, data: parsed });
                    }}
                  >
                    Restore it?
                  </button>
                </span>
              ));
              setRecord({ ...template }); // Don't restore by default
              setMode('template');
              return;
            }
          } catch (err) {
            console.warn('Failed to parse draft JSON');
          }
        }

        setRecord(template);
        setMode('template');
      } else {
        toast.error('Failed to load template or site from DB');
      }
    };

    load();
  }, [slug]);

  return { record, mode };
}

'use client';

import { useRouter } from 'next/navigation';
import { useState } from 'react';
import { ScrollArea } from '@/components/ui/scroll-area';
import { TemplateEditorToolbar } from './template-editor-toolbar';
import { useTemplateEditorState } from './use-template-editor-state';
import { EditorContent } from '@/components/admin/templates/template-editor-content';
import { Drawer } from '@/components/ui/drawer';
import { Modal } from '@/components/ui/modal';
import VectorQueryPage from '@/app/admin/vector-query/page';
import type { Snapshot, Template } from '@/types/template';
import { Dispatch, SetStateAction } from 'react';
import { Button } from '@/components/ui/button';
import { useTemplateInsert } from '@/hooks/useTemplateInsert';
import { BlocksEditor } from './blocks-editor';
import BlockSidebar from './block-sidebar';
import { Block, BlockSchema } from '@/admin/lib/zod/blockSchema';
import { z } from 'zod';
import { BlockValidationError } from '@/hooks/validateTemplateBlocks';

export default function TemplateEditor({
  templateName,
  initialData,
  onRename,
  initialMode = 'template',
}: {
  templateName: string;
  initialData?: Snapshot;
  onRename?: (newName: string) => void;
  initialMode?: 'template' | 'site';
}) {
  const router = useRouter();
  const [showVectorDrawer, setShowVectorDrawer] = useState(false);
  const [pendingText, setPendingText] = useState<string | null>(null);
  const [pendingAction, setPendingAction] = useState<'insert' | 'replace'>('insert');
  const [targetBlockIndex, setTargetBlockIndex] = useState<number | null>(null);
  const [selectedIndex, setSelectedIndex] = useState<number | null>(null);

  const {
    template,
    rawJson,
    setRawJson,
    livePreviewData,
    setTemplate,
    autosave,
    isCreating,
    isRenaming,
    setIsRenaming,
    handleRename,
    inputValue,
    setInputValue,
    slugPreview,
    handleSaveDraft,
    nameExists,
    blockErrors,
    setBlockErrors
  } = useTemplateEditorState({ templateName, initialData, onRename });

  const {
    insertBlock,
    undoInsert,
    recentlyInsertedBlockId,
    hasUndo,
  } = useTemplateInsert(setTemplate as unknown as (updater: (prev: Template) => Template) => void);

  const handleUseBlock = (text: string, action: 'insert' | 'replace', index?: number) => {
    setPendingText(text);
    setPendingAction(action);
    setTargetBlockIndex(index ?? null);
    setShowVectorDrawer(false);
  };

  const selectedBlock = selectedIndex !== null ? template.data.pages[0].content_blocks[selectedIndex] : null;
  const selectedId = selectedBlock?._id ?? '';

  const validationSummary = Object.entries(blockErrors).map(([blockId, errors]) => (
    <div key={blockId} className="text-sm text-red-300 border-b border-zinc-700 pb-2 mb-2">
      <strong className="text-red-400">Block {blockId}:</strong>
      <ul className="list-disc list-inside">
        {errors.map((e, i) => (
          <li key={i}>
            <code>{e.field}</code>: {e.message}
          </li>
        ))}
      </ul>
    </div>
  ));

  return (
    <>
      {/* Template Editor Toolbar */}
      <ScrollArea className="h-screen w-full p-6">
        <TemplateEditorToolbar
          templateName={template.template_name}
          autosaveStatus={autosave.status}
          isRenaming={isRenaming}
          setIsRenaming={setIsRenaming}
          inputValue={inputValue}
          setInputValue={setInputValue}
          slugPreview={slugPreview}
          handleRename={handleRename}
          handleSaveDraft={handleSaveDraft}
          onBack={() => router.push(initialMode === 'site' ? '/admin/sites' : '/admin/templates')}
          nameExists={nameExists}
          setShowNameError={() => {}}
        />

        {/* Block Errors */}
        {Object.keys(blockErrors).length > 0 && (
          <div className="bg-red-900/40 text-red-300 p-4 rounded border border-red-500 mb-4">
            <div className="font-bold text-red-200 mb-2">Some block(s) have validation errors:</div>
            {validationSummary}
          </div>
        )}

        {/* Template Editor Content */}
        <EditorContent
          template={template as unknown as Template}
          rawJson={rawJson}
          setRawJson={setRawJson}
          livePreviewData={livePreviewData}
          setTemplate={setTemplate as unknown as Dispatch<SetStateAction<Template>>}
          autosaveStatus={autosave.status}
          setShowPublishModal={() => {}}
          recentlyInsertedBlockId={recentlyInsertedBlockId ?? null}
          setBlockErrors={setBlockErrors as unknown as (errors: Record<string, BlockValidationError[]>) => void}
          blockErrors={blockErrors as unknown as Record<string, BlockValidationError[]> | null  }
          mode="template"
        />

        {/* Suggest Block */}
        {/* <div className="mt-6 flex gap-4">
          <Button onClick={() => setShowVectorDrawer(true)}>
            Suggest Block
          </Button>
          {hasUndo && (
            <Button variant="ghost" onClick={undoInsert}>
              Undo Last Insert
            </Button>
          )}
        </div> */}

        {/* Block Editor */}
        {selectedBlock && selectedIndex !== null && (
          <BlockSidebar
            block={selectedBlock}
            errors={blockErrors[selectedId] as unknown as BlockValidationError[] || []}
            onSave={(updatedBlock: Block) => {
              setTemplate((prev) => {
                const updated = { ...prev };
                updated.data.pages[0].content_blocks[selectedIndex] = updatedBlock;
                return updated;
              });
            }}
            onClose={() => setSelectedIndex(null)}
            onOpen={selectedIndex !== null}
            onReplaceWithAI={() => {}}
            onClone={() => {}}
            onShowPrompt={() => {}}
            onUndo={() => {}}
            onViewDiff={() => {}}
            undoAvailable={false}
          />
        )}
      </ScrollArea>

      {/* AI Block Suggestions */}
      <Drawer open={showVectorDrawer} onClose={() => setShowVectorDrawer(false)}>
        <VectorQueryPage
          onUseBlock={(text, mode = 'insert', index) => handleUseBlock(text, mode as 'insert' | 'replace', index as number | undefined)}
        />
      </Drawer>

      {/* AI Block Preview */}
      <Modal show={!!pendingText} onClose={() => setPendingText(null)} title="üß† Preview AI Block">
        <textarea
          className="w-full border-gray-700 rounded bg-zinc-800 text-white p-2 text-sm mb-4"
          rows={5}
          value={pendingText || ''}
          onChange={(e) => setPendingText(e.target.value)}
        />
        <div className="flex justify-end gap-2">
          <Button variant="ghost" onClick={() => setPendingText(null)}>
            Cancel
          </Button>
          <Button
            onClick={() => {
              if (!pendingText) return;
              if (pendingAction === 'replace' && targetBlockIndex !== null) {
                setTemplate((prev) => {
                  const updated = { ...prev };
                  updated.data.pages[0].content_blocks[targetBlockIndex] = {
                    _id: updated.data.pages[0].content_blocks[targetBlockIndex]._id,
                    type: 'text',
                    content: { value: pendingText },
                    tags: ['ai'],
                    meta: {
                      ...updated.data.pages[0].content_blocks[targetBlockIndex].meta,
                      prompt: pendingText,
                    },
                  };
                  return updated;
                });
              } else {
                insertBlock(pendingText);
              }
              setPendingText(null);
            }}
          >
            {pendingAction === 'replace' ? 'Replace Block' : 'Insert Block'}
          </Button>
        </div>
      </Modal>
      <div>&nbsp;</div>
      <div>&nbsp;</div>
      <div>&nbsp;</div>
      <div>&nbsp;</div>
    </>
  );
}

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import TemplateVersionDiff from './template-version-diff';
import { Button } from '@/components/ui';
import toast from 'react-hot-toast';

export default function TemplateHistory({
  template,
  onRevert,
}: {
  template: any;
  onRevert: (data: any) => void;
}) {
  const [versions, setVersions] = useState<any[]>([]);
  const [selectedVersion, setSelectedVersion] = useState<any>(null);

  useEffect(() => {
    if (!template?.template_name) return;

    supabase
      .from('template_versions')
      .select('*')
      .eq('template_name', template.template_name)
      .order('created_at', { ascending: false })
      .then(({ data }) => {
        if (data) setVersions(data);
      });
  }, [template?.template_name]);

  const handleRevert = () => {
    if (!selectedVersion) return;
    onRevert({ ...template, data: selectedVersion.full_data });
    toast.success('Template reverted to selected version');
  };

  return (
    <div className="space-y-4">
      <h2 className="text-lg font-bold">Version History</h2>
      <ul className="space-y-2">
        {versions.map((v) => (
          <li key={v.id} className="border p-3 rounded bg-white dark:bg-gray-900">
            <div className="flex justify-between items-center">
              <div>
                <p className="text-sm font-medium">{v.commit_message || 'No message'}</p>
                <p className="text-xs text-gray-400">
                  {new Date(v.created_at || v.saved_at).toLocaleString()}
                </p>
                {v.thumbnail_url && (
                  <img
                    src={v.thumbnail_url}
                    alt="Preview thumbnail"
                    className="mt-2 w-32 rounded border border-gray-600 shadow-sm"
                  />
                )}
              </div>
              <button
                className="text-blue-500 underline text-xs"
                onClick={() => setSelectedVersion(v)}
              >
                View Diff
              </button>
            </div>
          </li>
        ))}
      </ul>

      {selectedVersion && (
        <div id="version-diff-container" className="mt-6">
          <h3 className="text-sm font-semibold mb-2 text-gray-300">Diff from selected version:</h3>
          <TemplateVersionDiff current={template?.data} previous={selectedVersion.full_data} />
          <div className="flex gap-4 mt-3">
            <Button size="sm" variant="secondary" onClick={handleRevert}>
              Revert to This
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

// components/admin/templates/template-json-editor.tsx
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui';
import { ChevronRight, ChevronDown } from 'lucide-react';
import { TemplateSaveSchema, ValidatedTemplate } from '@/admin/lib/zod/templateSaveSchema';
import type { JsonValue } from '@/types/json';
import { validateBlocksInTemplate } from '@/admin/lib/validateBlocksInTemplate';

type TemplateJsonEditorProps = {
  rawJson: string;
  setRawJson: (value: string) => void;
};

export default function TemplateJsonEditor({ rawJson, setRawJson }: TemplateJsonEditorProps) {
  const [isReadOnly, setIsReadOnly] = useState(true);
  const [parsedJson, setParsedJson] = useState<ValidatedTemplate | null>(null);
  const [validationError, setValidationError] = useState<string | null>(null);
  const [collapsed, setCollapsed] = useState(new Set<string>());

  useEffect(() => {
    try {
      const parsed = JSON.parse(rawJson);
      const result = TemplateSaveSchema.safeParse(parsed);
      if (result.success) {
        setParsedJson(result.data);

        // setValidationError(null);
        const blockErrors = validateBlocksInTemplate(result.data);
        if (blockErrors.length > 0) {
          setValidationError(blockErrors.join('\n'));
        } else {
          setValidationError(null);
        }

      } else {
        setParsedJson(null);
        const formErrors = result.error.flatten();
        setValidationError(
          formErrors.formErrors.length > 0
            ? formErrors.formErrors.join(', ')
            : 'Some field(s) inside blocks or pages are incorrectly formatted.'
        );
        console.warn('[‚ùå Zod Field Errors]', formErrors);
        console.warn('JSON failed schema validation:', result.error.flatten());
      }
    } catch {
      setParsedJson(null);
      setValidationError('Invalid JSON syntax');
    }
  }, [rawJson]);

  const toggleCollapse = (path: string) => {
    const newSet = new Set(collapsed);
    if (newSet.has(path)) {
      newSet.delete(path);
    } else {
      newSet.add(path);
    }
    setCollapsed(newSet);
  };

  const renderValue = (value: JsonValue, path = '') => {
    const type = typeof value;

    if (value === null) return <span className="text-pink-400">null</span>;
    if (Array.isArray(value)) {
      const isCollapsed = collapsed.has(path);
      return (
        <div>
          <span className="cursor-pointer select-none" onClick={() => toggleCollapse(path)}>
            {isCollapsed ? <ChevronRight size={12} /> : <ChevronDown size={12} />}
            <span className="text-yellow-400"> [Array]</span>
          </span>
          {!isCollapsed && (
            <div className="ml-4">
              {value.map((v, i) => (
                <div key={i}>{renderValue(v, `${path}[${i}]`)}</div>
              ))}
            </div>
          )}
        </div>
      );
    }

    if (type === 'object') {
      const isCollapsed = collapsed.has(path);
      return (
        <div>
          <span className="cursor-pointer select-none" onClick={() => toggleCollapse(path)}>
            {isCollapsed ? <ChevronRight size={12} /> : <ChevronDown size={12} />}
            <span className="text-blue-400"> {'{Object}'} </span>
          </span>
          {!isCollapsed && (
            <div className="ml-4">
              {Object.entries(value).map(([k, v]) => (
                <div key={k}>
                  <span className="text-green-400">&quot;{k}&quot;</span>:{' '}
                  {renderValue(v, `${path}.${k}`)}
                </div>
              ))}
            </div>
          )}
        </div>
      );
    }

    if (type === 'string')
      return <span className="text-emerald-400">&quot;{value as string}&quot;</span>;
    if (type === 'number') return <span className="text-cyan-400">{value as number}</span>;
    if (type === 'boolean')
      return <span className="text-orange-400">{(value as boolean).toString()}</span>;
    return <span className="text-white">{value as string}</span>;
  };

  const handlePrettify = () => {
    try {
      const parsed = JSON.parse(rawJson);
      setRawJson(JSON.stringify(parsed, null, 2));
    } catch {
      alert('Invalid JSON. Cannot format.');
    }
  };

  return (
    <div className="space-y-2">
      <div className="flex justify-between items-center">
        <h3 className="text-white text-sm font-semibold">JSON Editor</h3>
        <div className="flex gap-2">
          <Button variant="secondary" size="sm" onClick={handlePrettify}>
            Prettify
          </Button>
          <Button variant="secondary" size="sm" onClick={() => setIsReadOnly(!isReadOnly)}>
            {isReadOnly ? 'Unlock' : 'Read-Only'}
          </Button>
        </div>
      </div>

      {validationError && (
        <div className="text-red-400 text-sm font-mono px-1">
          ‚ö†Ô∏è {validationError}
        </div>
      )}

      <div className="overflow-auto rounded border border-gray-700 bg-gray-900 font-mono text-sm text-white max-h-[500px] p-4">
        {isReadOnly ? (
          parsedJson ? (
            renderValue(parsedJson)
          ) : (
            <pre>{rawJson}</pre>
          )
        ) : (
          <textarea
            value={rawJson}
            onChange={(e) => setRawJson(e.target.value)}
            className="w-full h-[600px] resize-none bg-gray-900 text-white font-mono text-sm p-2 border border-gray-700 rounded"
          />
        )}
      </div>
    </div>
  );
}

