export default function DevToolsWidget() {
  const [visible, setVisible] = useState(true);
  const [minimized, setMinimized] = useState(true);
  const [session, setSession] = useState<Session | null>(null);
  const [cookies, setCookies] = useState<{ [key: string]: string }>({});
  const [headers, setHeaders] = useState<{ [key: string]: string }>({});
  const [locationQuery, setLocationQuery] = useState('');
  const [locationLoading, setLocationLoading] = useState(false);
  const [showJwt, setShowJwt] = useState(false);
  const [showMetadata, setShowMetadata] = useState(false);
  const [jwtPayload, setJwtPayload] = useState<any>(null);

  const mockGeo = useMockGeolocation();
  const meta = useRequestMeta();

  useEffect(() => {
    if (process.env.NODE_ENV !== 'development') return;

    setVisible(true);
    const minimizedFlag = localStorage.getItem('devtools:minimized') === 'true';
    setMinimized(minimizedFlag);

    const supabase = createClient<Database>(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);

    supabase.auth.getSession().then(({ data }) => {
      setSession(data.session);
    });

    const { data: listener } = supabase.auth.onAuthStateChange((_event, newSession) => {
      setSession(newSession ?? null);
    });

    const handler = (e: KeyboardEvent) => {
      if (e.metaKey && e.key === '~') {
        e.preventDefault();
        setVisible((v) => !v);
      }
    };
    window.addEventListener('keydown', handler);

    return () => {
      window.removeEventListener('keydown', handler);
      listener?.subscription?.unsubscribe();
    };
  }, []);

  useEffect(() => {
    localStorage.setItem('devtools:minimized', String(minimized));
  }, [minimized]);

  useEffect(() => {
    const parsed: { [key: string]: string } = {};
    document.cookie.split(';').forEach((c) => {
      const [key, ...rest] = c.trim().split('=');
      parsed[key] = rest.join('=');
    });
    setCookies(parsed);

    const headerDump = (window as any).__NEXT_DATA__?.props?.headers || {};
    setHeaders(headerDump);
  }, []);

  useEffect(() => {
    if (!mockGeo?.lat || !mockGeo?.lon) return;

    const mock = {
      coords: {
        latitude: parseFloat(mockGeo.lat.toString()),
        longitude: parseFloat(mockGeo.lon.toString()),
        accuracy: 10,
      },
      timestamp: Date.now(),
    };

    navigator.geolocation.getCurrentPosition = (cb) => {
      cb(mock as GeolocationPosition);
    };
  }, [mockGeo]);

  useEffect(() => {
    if (!session?.access_token) return;
    try {
      const [, payload] = session.access_token.split('.');
      const decoded = JSON.parse(atob(payload));
      setJwtPayload(decoded);
    } catch (err) {
      console.warn('Failed to decode JWT', err);
    }
  }, [session?.access_token]);

  const handleCookieChange = (key: string, value: string) => {
    document.cookie = `${key}=${value}`;
    setCookies((prev) => ({ ...prev, [key]: value }));
  };

  const handleCookieDelete = (key: string) => {
    document.cookie = `${key}=; Max-Age=0`;
    const updated = { ...cookies };
    delete updated[key];
    setCookies(updated);
  };

  const copyDebugState = () => {
    const debug = {
      meta,
      session,
      cookies,
      headers,
    };
    navigator.clipboard.writeText(JSON.stringify(debug, null, 2));
    alert('Debug state copied!');
  };

  const abVariant = cookies['ab-variant'];
  const sessionId = cookies['session-id'];
  const hasMockUser = Boolean(cookies['mock-user-id']);

  if (!visible) return null;

  if (minimized) {
    return (
      <button
        onClick={() => setMinimized(false)}
        className="fixed bottom-4 left-1/2 -translate-x-1/2 z-50 px-4 py-2 bg-zinc-800 text-white text-xs rounded border border-zinc-700 shadow-md"
      >
        üõ†Ô∏è DevTools
      </button>
    );
  }

  return (
    <div className="fixed bottom-4 left-1/2 -translate-x-1/2 z-50 bg-black/80 text-white px-4 py-3 text-xs rounded shadow-md border border-zinc-700 backdrop-blur-md w-[420px] max-h-[80vh] overflow-y-auto">
      <DevToolsPanel minimized={minimized} />
      <TopMetadataSummary abVariant={abVariant} sessionId={sessionId} hasMockUser={hasMockUser} />
      <CookieEditor cookies={cookies} onChange={handleCookieChange} onDelete={handleCookieDelete} />
      <MockGeolocationEditor
        cookieValue={cookies['mock-geo'] || ''}
        onChange={handleCookieChange}
        onDelete={() => handleCookieDelete('mock-geo')}
        locationQuery={locationQuery}
        setLocationQuery={setLocationQuery}
        loading={locationLoading}
        setLoading={setLocationLoading}
        activeGeo={mockGeo ? { lat: mockGeo.lat.toString(), lon: mockGeo.lon.toString() } : { lat: '', lon: '' }}
      />
      <MockUserEditor
        cookies={cookies}
        onChange={handleCookieChange}
        onDelete={handleCookieDelete}
      />
      <HeaderViewer headers={headers} />
      <SessionViewer
        sessionInfo={session}
      />
      <div className="flex justify-between items-center pt-3">
        <button
          onClick={() => setMinimized(true)}
          className="text-xs text-zinc-400 underline"
        >
          Minimize
        </button>
        <button
          onClick={copyDebugState}
          className="text-xs text-blue-400 underline"
        >
          üìã Copy Debug Info
        </button>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { createPortal } from 'react-dom';
import clsx from 'clsx';

type HoverData = {
  id: string;
  type: string;
  rect: DOMRect;
};

export default function BlockInspectorOverlay() {
  const [hover, setHover] = useState<HoverData | null>(null);

  useEffect(() => {
    function handleMouseMove(e: MouseEvent) {
      if (!e.altKey) return setHover(null);

      const el = (e.target as HTMLElement)?.closest('[data-block-id]');
      if (!el) return setHover(null);

      const id = el.getAttribute('data-block-id') || 'unknown';
      const type = el.getAttribute('data-block-type') || 'unknown';
      const rect = el.getBoundingClientRect();

      setHover({ id, type, rect });
    }

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  if (!hover) return null;

  return createPortal(
    <>
      {/* üü™ Floating tooltip */}
      <div
        className="fixed z-50 bg-black text-white text-xs px-2 py-1 rounded shadow pointer-events-none"
        style={{ top: hover.rect.top + 8, left: hover.rect.left + 8 }}
      >
        üß± <strong>{hover.type}</strong> ‚Äî <code>{hover.id}</code>
      </div>

      {/* üü¶ Outline overlay */}
      <div
        className={clsx(
          'fixed z-40 pointer-events-none border-2 border-purple-500 rounded-md animate-pulse',
        )}
        style={{
          top: hover.rect.top - 2,
          left: hover.rect.left - 2,
          width: hover.rect.width + 4,
          height: hover.rect.height + 4,
        }}
      />
    </>,
    document.body
  );
}

'use client';

import { useEffect, useState } from 'react';
import { PanelLeftClose, Sparkles } from 'lucide-react';
import { AnimatePresence, motion } from 'framer-motion';
import clsx from 'clsx';

const quotes = [
  "Those margins? I've seen toddlers space better.",
  "Your layout slaps. Not in the good way.",
  "Hero block looking heroic ‚Äî I‚Äôll allow it.",
  "That's a lot of padding... compensating for something?",
  "Your colors are bold. Bold like Comic Sans.",
  "Fast site. Faster than your squat depth.",
  "This layout's tighter than my quad routine.",
  "CTA needs a glow-up. Or a nap.",
  "Your font hierarchy is a social hierarchy.",
  "I'm not saying it's bad... but I'm not not saying that.",
];

function getRandomQuote() {
  return quotes[Math.floor(Math.random() * quotes.length)];
}

export default function SquatBotOverlay() {
  const [open, setOpen] = useState(false);
  const [quote, setQuote] = useState(getRandomQuote());

  useEffect(() => {
    const key = (e: KeyboardEvent) => {
      if (e.key === '.' && e.metaKey && e.shiftKey) {
        setOpen((o) => !o);
      }
    };
    window.addEventListener('keydown', key);
    return () => window.removeEventListener('keydown', key);
  }, []);

  useEffect(() => {
    if (!open) return;
    const id = setInterval(() => {
      setQuote(getRandomQuote());
    }, 10000);
    return () => clearInterval(id);
  }, [open]);

  return (
    <>
      <button
        onClick={() => setOpen((o) => !o)}
        className="fixed bottom-4 right-4 z-50 bg-purple-600 text-white p-3 rounded-full shadow-lg hover:bg-purple-700 transition-all"
        aria-label="Toggle SquatBot"
      >
        <Sparkles className="w-5 h-5" />
      </button>

      <AnimatePresence>
        {open && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            transition={{ duration: 0.3 }}
            className="fixed bottom-20 right-4 z-50 w-[300px] bg-neutral-900 text-white rounded-xl shadow-lg border border-white/10"
          >
            <div className="p-4">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-sm font-semibold uppercase tracking-wide text-purple-300">
                  ü¶æ SquatBot v0.1
                </h2>
                <button
                  onClick={() => setOpen(false)}
                  className="text-white/70 hover:text-white"
                  aria-label="Close SquatBot"
                >
                  <PanelLeftClose className="w-4 h-4" />
                </button>
              </div>
              <p className="text-sm italic">{quote}</p>
              <div className="mt-4 text-xs text-zinc-400">
                Tip: <code className="bg-zinc-800 px-1 rounded">‚åò+‚áß+.</code> to toggle
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

'use client';

import { useEffect, useState } from 'react';
import clsx from 'clsx';
import { Sparkles, Trash2, CheckCircle } from 'lucide-react';
import { suggestFix } from '@/lib/roastbot/suggestFix';
import { useBlockFix } from '@/components/ui/block-fix-context';
import { useRouter } from 'next/navigation';
import toast from 'react-hot-toast';
import type { Block } from '@/types/blocks';
import DevToolsWidget from '../dev-tools-widget';

type BlockMeta = {
  id: string;
  type: string;
  el: HTMLElement;
};

type FeedbackMode = 'roast' | 'truth';
type RoastLevel = 'mild' | 'medium' | 'scorched';

export default function SquatBotPanel() {
  const [blocks, setBlocks] = useState<BlockMeta[]>([]);
  const [highlightedId, setHighlightedId] = useState<string | null>(null);
  const [visible, setVisible] = useState(false);
  const [tab, setTab] = useState<'blocks' | 'roasts'>('blocks');
  const [roastLevel, setRoastLevel] = useState<RoastLevel>('medium');
  const [feedbackMode, setFeedbackMode] = useState<FeedbackMode>('roast');
  const [roasts, setRoasts] = useState<Record<string, string>>({});
  const router = useRouter();

  const {
    draftFixes,
    committedFixes,
    setDraftFix,
    discardFix,
    commitFix,
    toggle: toggleFixPreview,
    enabled: previewingFixes,
    resetAll,
  } = useBlockFix();

  const loadBlocks = () => {
    const elements = Array.from(document.querySelectorAll('[data-block-id]')) as HTMLElement[];
    const metas: BlockMeta[] = elements.map((el) => ({
      id: el.dataset.blockId || '',
      type: el.dataset.blockType || 'unknown',
      el,
    }));
    setBlocks(metas);
    console.log(`ü¶æ SquatBot loaded ${metas.length} blocks`);
  };

  useEffect(() => {
    const key = (e: KeyboardEvent) => {
      if (e.metaKey && e.shiftKey && e.key.toLowerCase() === 'i') setVisible((v) => !v);
      if (e.key.toLowerCase() === 'r' && visible) {
        console.log('üîÅ Refreshing SquatBot block list...');
        loadBlocks();
      }
    };
    window.addEventListener('keydown', key);
    return () => window.removeEventListener('keydown', key);
  }, [visible]);

  useEffect(() => {
    if (!visible) return;
    setTimeout(() => {
      loadBlocks();
    }, 50);
  }, [visible]);

  useEffect(() => {
    blocks.forEach((block) => {
      block.el.style.outline =
        block.id === highlightedId
          ? '2px solid #9333ea'
          : draftFixes[block.id]
          ? '2px dashed #16a34a'
          : '';
    });
  }, [highlightedId, blocks, draftFixes]);

  const saveFixes = async () => {
    const slug = window.location.pathname.split('/')[2];
    const res = await fetch(`/api/fix-template?slug=${slug}`, {
      method: 'POST',
      body: JSON.stringify({ fixes: committedFixes }),
      headers: { 'Content-Type': 'application/json' },
    });

    if (res.ok) {
      toast.success('‚úÖ Fixes saved to Supabase!');
      router.refresh();
    } else {
      toast.error('‚ùå Failed to save fixes');
    }
  };

  return (
    <>
      <button
        onClick={() => setVisible((v) => !v)}
        className="fixed bottom-4 right-4 z-50 bg-purple-600 text-white p-3 rounded-full shadow-lg hover:bg-purple-700 transition-all"
        aria-label="Toggle SquatBot"
      >
        <Sparkles className="w-5 h-5" />
      </button>

      {visible && (
        <div className="fixed bottom-4 left-4 z-50 w-[360px] max-h-[65vh] overflow-y-auto bg-neutral-900 text-white rounded-lg shadow-xl border border-white/10 text-sm">
        <DevToolsWidget />
          <div className="p-3 border-b border-white/10 flex justify-between items-center">
            <span className="font-semibold">ü¶æ SquatBot Dev Panel</span>
            <div className="flex gap-2 text-xs">
              <button
                className={clsx('px-2 py-1 rounded', tab === 'blocks' && 'bg-white text-black')}
                onClick={() => setTab('blocks')}
              >
                Blocks
              </button>
              <button
                className={clsx('px-2 py-1 rounded', tab === 'roasts' && 'bg-white text-black')}
                onClick={() => setTab('roasts')}
              >
                RoastBot
              </button>
            </div>
          </div>

          {tab === 'blocks' && (
            <>
              <div className="p-3 border-b border-white/10 flex justify-between text-xs">
                <span>Preview Fixes</span>
                <button
                  onClick={toggleFixPreview}
                  className={clsx(
                    'px-2 py-1 rounded font-mono',
                    previewingFixes ? 'bg-green-600 text-white' : 'bg-white text-black'
                  )}
                >
                  {previewingFixes ? 'ON' : 'OFF'}
                </button>
              </div>

              {Object.keys(draftFixes).length > 0 && (
                <div className="px-3 py-2 flex justify-between text-xs border-b border-white/10">
                  <button
                    onClick={resetAll}
                    className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700"
                  >
                    üßπ Reset All
                  </button>
                </div>
              )}
            </>
          )}

          <div className="divide-y divide-white/5 px-2">
            {blocks.map((block) => {
              const draft = draftFixes[block.id];
              const committed = committedFixes[block.id];
              const hasFix = draft && Object.keys(draft).length > 0;

              return (
                <div
                  key={block.id}
                  className={clsx(
                    'px-2 py-2 transition group',
                    highlightedId === block.id && 'bg-purple-800/30'
                  )}
                  onMouseEnter={() => setHighlightedId(block.id)}
                  onMouseLeave={() => setHighlightedId(null)}
                >
                  <div className="flex justify-between items-center">
                    <div className="text-white font-mono text-xs flex items-center gap-2">
                      {block.type} <code>{block.id}</code>
                      {committed && <span className="text-green-400">‚úÖ</span>}
                      {!committed && draft && <span className="text-yellow-400">üü°</span>}
                    </div>

                    <div className="flex gap-1">
                      {!hasFix && (
                        <button
                          onClick={() => {
                            const el = block.el;
                            if (!el) return;
                            const content = (el as any).__squatterContent || {};
                            const patch = suggestFix({ type: block.type, content } as Block);
                            (el as any).__squatterContent = { ...content, ...patch };
                            setDraftFix(block.id, patch);
                            toast.success(`üõ† Fix preview applied`);
                          }}
                          className="text-xs bg-green-600 text-white px-2 py-0.5 rounded hover:bg-green-700"
                        >
                          Fix it
                        </button>
                      )}

                      {hasFix && (
                        <button
                          onClick={() => discardFix(block.id)}
                          className="text-xs bg-red-600 text-white px-2 py-0.5 rounded hover:bg-red-700 flex items-center gap-1"
                        >
                          <Trash2 className="w-3 h-3" /> Undo
                        </button>
                      )}

                      {hasFix && !committed && (
                        <button
                          onClick={() => {
                            commitFix(block.id);
                            toast.success('‚úÖ Fix committed');
                          }}
                          className="text-xs bg-blue-600 text-white px-2 py-0.5 rounded hover:bg-blue-700 flex items-center gap-1"
                        >
                          <CheckCircle className="w-3 h-3" /> Commit
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {Object.keys(committedFixes).length > 0 && (
            <div className="p-3 border-t border-white/10 text-center">
              <button
                onClick={saveFixes}
                className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded text-sm font-semibold"
              >
                üíæ Save Fixes to Supabase
              </button>
            </div>
          )}
        </div>
      )}
    </>
  );
}

