'use client';

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  SortableContext,
  useSortable,
  arrayMove,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { useState } from 'react';
import { GripVertical, Pencil, Trash2, PlusCircle } from 'lucide-react';
import { createDefaultBlock } from '@/lib/createDefaultBlock';
import RenderBlock from '@/components/admin/templates/render-block';
import { DynamicBlockEditor } from '@/components/editor/dynamic-block-editor';
import BlockAdderGrouped from '@/components/admin/block-adder-grouped';
import { Template } from '@/types/template';
import { BlockValidationError } from '@/hooks/validateTemplateBlocks';
import { FloatingPageSidebar } from '@/components/editor/floating-page-sidebar';
import SafeTriggerButton from '@/components/ui/safe-trigger-button';

function BlockWrapper({ block, children, onEdit, onDelete }: any) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: block._id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      className="group relative border border-white/10 rounded p-2"
    >
      <div className="flex justify-between items-center mb-1 opacity-80 text-xs text-white">
        <div className="flex items-center gap-1">
          <GripVertical
            className="w-3 h-3 text-gray-500 cursor-move"
            {...attributes}
            {...listeners}
          />
          <span className="uppercase tracking-wide">{block.type}</span>
        </div>
        <div className="hidden group-hover:flex gap-2">
          <button onClick={onEdit} className="text-xs text-blue-400 underline">
            <Pencil size={16} />
          </button>
          <button onClick={onDelete} className="text-xs text-red-400 underline">
            <Trash2 size={16} />
          </button>
        </div>
      </div>
      {children}
    </div>
  );
}

export function LiveEditorPreview({
  template,
  onChange,
  industry,
  errors,
}: {
  template: Template;
  onChange: (template: Template) => void;
  industry: string;
  errors: Record<string, BlockValidationError[]>;
}) {
  const sensors = useSensors(useSensor(PointerSensor));
  const [layoutMode, setLayoutMode] = useState<'stack' | 'grid'>('stack');
  const [editing, setEditing] = useState<any | null>(null);
  const [lastInsertedId, setLastInsertedId] = useState<string | null>(null);
  const [selectedPageIndex, setSelectedPageIndex] = useState(0);

  const updateAndSave = (updated: Template) => {
    onChange({ ...updated });
  };

  const selectedPage = template.data.pages[selectedPageIndex];

  const handleAddPage = () => {
    const updated = { ...template };
    updated.data.pages.push({
      id: crypto.randomUUID(),
      slug: `page-${Date.now()}`,
      title: `Untitled Page ${updated.data.pages.length + 1}`,
      content_blocks: [],
      show_header: true,
      show_footer: true,
    });
    setSelectedPageIndex(updated.data.pages.length - 1);
    updateAndSave(updated);
  };

  return (
    <div className="relative dark:bg-neutral-900 text-white min-h-screen">
      <FloatingPageSidebar
        pages={template.data.pages}
        selectedIndex={selectedPageIndex}
        onSelect={setSelectedPageIndex}
        onAdd={handleAddPage}
        onRename={(i, title) => {
          const updated = { ...template };
          updated.data.pages[i].title = title;
          updateAndSave(updated);
        }}
        onDelete={(i) => {
          const updated = { ...template };
          updated.data.pages.splice(i, 1);
          setSelectedPageIndex(Math.max(0, i - 1));
          updateAndSave(updated);
        }}
        onReorder={(oldIndex, newIndex) => {
          const updated = { ...template };
          updated.data.pages = arrayMove(updated.data.pages, oldIndex, newIndex);
          updateAndSave(updated);
        }}
        onToggleHeader={(i, val) => {
          const updated = { ...template };
          updated.data.pages[i].show_header = val;
          updateAndSave(updated);
        }}
        onToggleFooter={(i, val) => {
          const updated = { ...template };
          updated.data.pages[i].show_footer = val;
          updateAndSave(updated);
        }}
      />

      <div className="p-6 pb-40 space-y-6 max-w-screen-md mx-auto w-full">
        <div className="flex justify-between items-center">
          <h1 className="text-xl font-bold text-white">{template.template_name}</h1>
          <div className="text-right text-sm space-x-2">
            <button
              onClick={() => setLayoutMode((m) => (m === 'stack' ? 'grid' : 'stack'))}
              className="text-blue-400 underline"
            >
              View: {layoutMode === 'stack' ? 'Vertical' : 'Grid'}
            </button>
          </div>
        </div>

        {selectedPage?.show_header !== false && template.headerBlock && (
          <RenderBlock block={template.headerBlock} />
        )}

        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={({ active, over }) => {
            if (!over || active.id === over.id) return;
            const blocks = [...selectedPage.content_blocks];
            const oldIndex = blocks.findIndex((b) => b._id === active.id);
            const newIndex = blocks.findIndex((b) => b._id === over.id);
            const reordered = arrayMove(blocks, oldIndex, newIndex);
            const updated = { ...template };
            updated.data.pages[selectedPageIndex].content_blocks = reordered;
            updateAndSave(updated);
          }}
        >
          <SortableContext
            items={selectedPage.content_blocks.map((b) => b._id ?? '')}
            strategy={verticalListSortingStrategy}
          >
            <div className={layoutMode === 'grid' ? 'grid grid-cols-1 md:grid-cols-2 gap-4' : 'space-y-6'}>
              {selectedPage.content_blocks.map((block, blockIndex) => (
                <BlockWrapper
                  key={block._id}
                  block={block}
                  onEdit={() => setEditing(block)}
                  onDelete={() => {
                    const updated = { ...template };
                    updated.data.pages[selectedPageIndex].content_blocks.splice(blockIndex, 1);
                    updateAndSave(updated);
                  }}
                >
                  <RenderBlock block={block} />
                  <BlockAdderGrouped
                    onAdd={(type) => {
                      const newBlock = createDefaultBlock(type);
                      const updated = { ...template };
                      updated.data.pages[selectedPageIndex].content_blocks.splice(blockIndex + 1, 0, newBlock);
                      setLastInsertedId(newBlock._id ?? '');
                      updateAndSave(updated);
                    }}
                    existingBlocks={selectedPage.content_blocks}
                    triggerElement={
                      <SafeTriggerButton
                        onClick={() => {
                          const newBlock = createDefaultBlock('text');
                          const updated = { ...template };
                          updated.data.pages[selectedPageIndex].content_blocks.splice(blockIndex + 1, 0, newBlock);
                          setLastInsertedId(newBlock._id ?? '');
                          updateAndSave(updated);
                        }}
                        className="text-sm text-purple-500 hover:underline mt-2"
                      >
                        + Add Block Below
                      </SafeTriggerButton>
                    }
                  />
                </BlockWrapper>
              ))}
            </div>
          </SortableContext>

          <div className="relative z-10 bg-white dark:bg-neutral-900 p-4 border-t border-gray-200 dark:border-neutral-700 mt-6">
            <BlockAdderGrouped
              onAdd={(type) => {
                const newBlock = createDefaultBlock(type);
                const updated = { ...template };
                updated.data.pages[selectedPageIndex].content_blocks.push(newBlock);
                setLastInsertedId(newBlock._id ?? '');
                updateAndSave(updated);
              }}
              existingBlocks={selectedPage.content_blocks}
              triggerElement={
                <SafeTriggerButton
                  onClick={() => {
                    const newBlock = createDefaultBlock('text');
                    const updated = { ...template };
                    updated.data.pages[selectedPageIndex].content_blocks.push(newBlock);
                    setLastInsertedId(newBlock._id ?? '');
                    updateAndSave(updated);
                  }}
                  className="w-full flex items-center justify-center gap-2 border border-purple-600 text-purple-600 dark:text-purple-400 rounded px-4 py-2 text-sm hover:bg-purple-50 dark:hover:bg-purple-900 transition-colors"
                >
                  <PlusCircle className="w-4 h-4" />
                  <span>Add Block to End</span>
                </SafeTriggerButton>
              }
            />
          </div>
        </DndContext>

        {selectedPage?.show_footer !== false && template.footerBlock && (
          <RenderBlock block={template.footerBlock} />
        )}
      </div>

      {editing && (
        <div className="fixed inset-0 bg-black/90 z-[999] p-6 overflow-auto flex items-center justify-center">
          <div className="w-full max-w-4xl bg-neutral-900 border border-white/10 rounded-xl shadow-xl overflow-hidden">
            <DynamicBlockEditor
              block={editing}
              onSave={(updatedBlock) => {
                const updated = { ...template };
                for (const page of updated.data.pages) {
                  const index = page.content_blocks.findIndex((b) => b._id === updatedBlock._id);
                  if (index !== -1) {
                    page.content_blocks[index] = updatedBlock;
                    break;
                  }
                }
                updateAndSave(updated);
                setEditing(null);
              }}
              onClose={() => setEditing(null)}
              errors={errors}
              template={template}
            />
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { useState } from 'react';
import { GripVertical, PlusCircle, File, Home, Phone, Wrench, User } from 'lucide-react';
import { Template } from '@/types/template';
import { Switch } from '@radix-ui/react-switch';
import { Label } from '@/components/ui/label';

function getPageIcon(slug: string) {
  const lower = slug.toLowerCase();
  if (lower.includes('home')) return <Home className="w-4 h-4 text-green-400 shrink-0" />;
  if (lower.includes('contact')) return <Phone className="w-4 h-4 text-blue-400 shrink-0" />;
  if (lower.includes('services')) return <Wrench className="w-4 h-4 text-orange-400 shrink-0" />;
  if (lower.includes('about')) return <User className="w-4 h-4 text-pink-400 shrink-0" />;
  return <File className="w-4 h-4 text-purple-400 shrink-0" />;
}

export function PageManagerSidebar({
  pages,
  selectedIndex,
  onSelect,
  onAdd,
  onRename,
  onDelete,
  onReorder,
  compact = false,
  onToggleHeader,
  onToggleFooter,
  templateShowHeader,
  templateShowFooter,
}: {
  pages: Template['data']['pages'];
  selectedIndex: number;
  onSelect: (index: number) => void;
  onAdd: () => void;
  onRename: (index: number, title: string) => void;
  onDelete: (index: number) => void;
  onReorder: (oldIndex: number, newIndex: number) => void;
  compact?: boolean;
  onToggleHeader: (index: number, value: boolean) => void;
  onToggleFooter: (index: number, value: boolean) => void;
  templateShowHeader?: boolean;
  templateShowFooter?: boolean;
}) {
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [draftTitle, setDraftTitle] = useState('');

  const sensors = useSensors(useSensor(PointerSensor));

  return (
    <div className={`p-3 space-y-3 ${compact ? 'text-xs leading-tight' : 'text-sm'}`}>
      <div className="flex justify-between items-center">
        <h3 className="font-semibold text-white">Pages</h3>
        <button
          onClick={onAdd}
          className="text-green-400 hover:underline flex items-center gap-1"
        >
          <PlusCircle className="w-4 h-4" /> Add
        </button>
      </div>

      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={({ active, over }) => {
          if (!over || active.id === over.id) return;
          const oldIndex = pages.findIndex((p) => p.slug === active.id);
          const newIndex = pages.findIndex((p) => p.slug === over.id);
          if (oldIndex !== -1 && newIndex !== -1) {
            onReorder(oldIndex, newIndex);
          }
        }}
      >
        <SortableContext
          items={pages.map((p) => p.slug)}
          strategy={verticalListSortingStrategy}
        >
          <ul className="space-y-1">
            {pages.map((page, idx) => (
              <SidebarPageItem
                key={page.slug}
                page={page}
                index={idx}
                isSelected={idx === selectedIndex}
                onSelect={() => onSelect(idx)}
                onRename={(newTitle: string) => onRename(idx, newTitle)}
                onDelete={() => onDelete(idx)}
                editingIndex={editingIndex}
                setEditingIndex={setEditingIndex}
                draftTitle={draftTitle}
                setDraftTitle={setDraftTitle}
                onToggleHeader={onToggleHeader}
                onToggleFooter={onToggleFooter}
                templateShowHeader={templateShowHeader}
                templateShowFooter={templateShowFooter}
              />
            ))}
          </ul>
        </SortableContext>
      </DndContext>
    </div>
  );
}

function SidebarPageItem({
  page,
  index,
  isSelected,
  onSelect,
  onRename,
  onDelete,
  editingIndex,
  setEditingIndex,
  draftTitle,
  setDraftTitle,
  onToggleHeader,
  onToggleFooter,
  templateShowHeader,
  templateShowFooter,
}: {
  page: Template['data']['pages'][number];
  index: number;
  isSelected: boolean;
  onSelect: () => void;
  onRename: (newTitle: string) => void;
  onDelete: () => void;
  editingIndex: number | null;
  setEditingIndex: (index: number | null) => void;
  draftTitle: string;
  setDraftTitle: (title: string) => void;
  onToggleHeader: (index: number, value: boolean) => void;
  onToggleFooter: (index: number, value: boolean) => void;
  templateShowHeader?: boolean;
  templateShowFooter?: boolean;
}) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: page.slug });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const effectiveShowHeader = page.show_header ?? templateShowHeader ?? true;
  const effectiveShowFooter = page.show_footer ?? templateShowFooter ?? true;

  return (
    <li
      ref={setNodeRef}
      style={style}
      className={`flex items-center gap-2 px-2 py-1 rounded group transition ${
        isSelected ? 'bg-neutral-800' : 'hover:bg-neutral-800'
      }`}
      title={page.title}
    >
      <GripVertical
        className="w-4 h-4 text-gray-500 cursor-move"
        {...attributes}
        {...listeners}
      />
      {getPageIcon(page.slug)}

      {editingIndex === index ? (
        <input
          className="w-full bg-neutral-800 border border-neutral-600 px-2 py-1 rounded text-white"
          value={draftTitle}
          onChange={(e) => setDraftTitle(e.target.value)}
          onBlur={() => {
            onRename(draftTitle);
            setEditingIndex(null);
          }}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              onRename(draftTitle);
              setEditingIndex(null);
            }
          }}
          autoFocus
        />
      ) : (
        <div className="flex justify-between items-center w-full">
          <button
            onClick={onSelect}
            className="text-left flex-1 truncate text-white hover:underline"
          >
            {page.title}
          </button>

          <div className="mt-2 space-y-2 text-sm text-white/80">
            <Label className="flex items-center gap-2">
              <Switch
                checked={effectiveShowHeader}
                onCheckedChange={(val) => onToggleHeader(index, val)}
              />
              Show Header
              {page.show_header === undefined && (
                <span className="text-yellow-400 text-xs">(inherited)</span>
              )}
            </Label>

            <Label className="flex items-center gap-2">
              <Switch
                checked={effectiveShowFooter}
                onCheckedChange={(val) => onToggleFooter(index, val)}
              />
              Show Footer
              {page.show_footer === undefined && (
                <span className="text-yellow-400 text-xs">(inherited)</span>
              )}
            </Label>
          </div>

          <div className="flex gap-2 ml-2 text-xs opacity-80 group-hover:opacity-100">
            <button
              onClick={() => {
                setEditingIndex(index);
                setDraftTitle(page.title);
              }}
              className="text-blue-400 hover:underline"
            >
              Edit
            </button>
            <button
              onClick={() => {
                if (confirm(`Delete page "${page.title}"?`)) {
                  onDelete();
                }
              }}
              className="text-red-400 hover:underline"
            >
              ‚úï
            </button>
          </div>
        </div>
      )}
    </li>
  );
}

