// components/ProfileForm.tsx
'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import { Button, } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { toast } from 'react-hot-toast';
import Image from 'next/image';
import { useCurrentUser } from '@/hooks/useCurrentUser';
import Link from 'next/link';
import { RewardTally } from '@/components/reward-tally';

export default function ProfileForm() {
  const { user, role } = useCurrentUser();
  const [avatarUrl, setAvatarUrl] = useState(user?.avatar_url || '');
  const [displayName, setDisplayName] = useState(user?.name || '');
  const [bio, setBio] = useState(user?.bio || '');
  const [showUtc, setShowUtc] = useState(false);
  const [latestLog, setLatestLog] = useState<any | null>(null);
  const [requesting, setRequesting] = useState(false);
  const [accessRequests, setAccessRequests] = useState<any[]>([]);
  const [rewardPoints, setRewardPoints] = useState(0);
  const [refLink, setRefLink] = useState('');
  const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

  const email = user?.email ?? (user as any)?.user_metadata?.email ?? '';

  useEffect(() => {
    if (user?.id) {
      (async () => {
        try {
          const res = await fetch(`/api/reward-points?user_id=${user.id}`);
          const d = await res.json();
          setRewardPoints(d.total || 0);
          setRefLink(`${window.location.origin}/?ref=${user.id}`);
        } catch {
          setRewardPoints(0);
        }
      })();
    }
  }, [user?.id]);

  useEffect(() => {
    if (role === 'admin') {
      supabase
        .from('user_deletion_logs')
        .select('id, email, deleted_at, user_id, admin_actor, reason')
        .order('deleted_at', { ascending: false })
        .limit(1)
        .then(({ data }) => {
          if (data?.length) setLatestLog(data[0]);
        });

      supabase
        .from('access_requests')
        .select('id, email, requested_at, reason')
        .order('requested_at', { ascending: false })
        .limit(10)
        .then(({ data }) => {
          if (data?.length) setAccessRequests(data);
        });
    }
  }, [role]);

  const handleSave = async () => {
    const { error } = await supabase.auth.updateUser({
      data: {
        avatar_url: avatarUrl,
        name: displayName,
        bio,
        updated_at: new Date().toISOString(),
      },
    });
    if (error) {
      toast.error('Failed to update profile');
    } else {
      toast.success('Profile updated!');
    }
  };

  const requestAccess = async () => {
    setRequesting(true);
    const { error } = await supabase.from('access_requests').insert({
      user_id: user?.id,
      email,
      requested_at: new Date().toISOString(),
      reason: 'Wants to edit profile info',
    });
    if (error) {
      toast.error('Failed to submit access request');
    } else {
      toast.success('Access request sent!');
    }
    setRequesting(false);
  };

  const handleApprove = async (req: any) => {
    const { error } = await supabase.from('user_profiles').upsert({
      user_id: req.user_id,
      email: req.email,
      role: 'reseller',
      updated_at: new Date().toISOString(),
    });
    if (error) {
      toast.error(`Failed to approve ${req.email}`);
    } else {
      toast.success(`${req.email} approved as reseller`);
      setAccessRequests(accessRequests.filter((r) => r.id !== req.id));
    }
  };

  const handleDeny = async (req: any) => {
    const { error } = await supabase.from('access_requests').delete().eq('id', req.id);
    if (error) {
      toast.error(`Failed to deny ${req.email}`);
    } else {
      toast.success(`Denied request from ${req.email}`);
      setAccessRequests(accessRequests.filter((r) => r.id !== req.id));
    }
  };

  return (
    <div className="space-y-6">
      <div className="text-white">
        <h1 className="text-2xl font-bold mb-2">üë§ Profile</h1>
        {user && (
          <>
            <div className="mb-6">
              <RewardTally points={rewardPoints} />
            </div>
            <div className="bg-zinc-800 p-4 rounded text-sm">
              <div className="text-zinc-400 mb-1">Your referral link:</div>
              <code className="text-blue-400">{refLink}</code>
            </div>
          </>
        )}
      </div>

      <div className="space-y-4">
        <div>
          <Label>Display Name</Label>
          <Input
            id="displayName"
            value={displayName}
            onChange={(e) => setDisplayName(e.target.value)}
            placeholder="e.g. Taylor Swift"
          />
        </div>
        <div>
          <Label>Bio</Label>
          <Input
            id="bio"
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            placeholder="Short description..."
          />
        </div>
        <div>
          <Label>Avatar URL</Label>
          <Input
            id="avatarUrl"
            value={avatarUrl}
            onChange={(e) => setAvatarUrl(e.target.value)}
            placeholder="https://example.com/avatar.png"
          />
        </div>

        <div className="flex gap-2 items-center">
          <Image
            src={avatarUrl || '/default-avatar.png'}
            alt="preview"
            width={64}
            height={64}
            className="rounded-full border border-white object-cover"
          />
          <Button onClick={handleSave}>Save</Button>
        </div>
      </div>

      {role !== 'admin' && (
        <div className="pt-6">
          <Button
            variant="outline"
            disabled={requesting}
            onClick={requestAccess}
          >
            Request Elevated Access
          </Button>
        </div>
      )}

      {role === 'admin' && accessRequests.length > 0 && (
        <div className="pt-6">
          <h2 className="text-lg font-semibold text-white mb-3">Access Requests</h2>
          <div className="space-y-3">
            {accessRequests.map((req) => (
              <div key={req.id} className="bg-zinc-800 p-3 rounded">
                <div className="text-sm text-zinc-300">
                  <span className="font-bold text-white">{req.email}</span> wants access ‚Äî reason:
                  <span className="italic"> {req.reason}</span>
                </div>
                <div className="mt-2 flex gap-2">
                  <Button size="sm" onClick={() => handleApprove(req)}>Approve</Button>
                  <Button size="sm" variant="secondary" onClick={() => handleDeny(req)}>Deny</Button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {role === 'admin' && (
        <div className="pt-6">
          <h2 className="text-lg font-semibold text-yellow-400">Admin Log</h2>
          <button
            className="text-xs text-blue-400 hover:underline"
            onClick={() => setShowUtc((v) => !v)}
          >
            Toggle UTC ({showUtc ? 'Local' : 'UTC'})
          </button>
          <p className="text-sm text-zinc-400 mt-2">
            Most recent deletion:
            <code className="ml-2 bg-zinc-800 px-2 py-1 rounded text-xs text-yellow-300">
              {latestLog
                ? `${latestLog.email} ‚Ä¢ ${new Date(latestLog.deleted_at).toLocaleString('en-US', {
                    timeZone: showUtc ? 'UTC' : timeZone,
                  })}`
                : 'Loading...'}
            </code>
          </p>
          <p className="text-sm text-zinc-400">
            Admin deletions and access approvals are tracked in the database.
          </p>
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import useMediaQuery from '@/hooks/useMediaQuery';
import event from '@vercel/analytics';

const features = [
  'üöÄ AI-generated websites in seconds',
  'üß† Built-in SEO optimization',
  'üì± Mobile-ready, always',
  'üé® Fully customizable designs',
  'üîí Secure & privacy-respecting',
];

export default function PuppyWidget({ showOnMobile = true }: { showOnMobile?: boolean }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [tooltipVisible, setTooltipVisible] = useState(false);
  const [tooltipDismissed, setTooltipDismissed] = useState(false);
  const [currentFeatureIndex, setCurrentFeatureIndex] = useState(0);
  const isMobile = useMediaQuery('(max-width: 768px)');

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentFeatureIndex((i) => (i + 1) % features.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      setTooltipVisible(true);

      if (isMobile) {
        const autoHide = setTimeout(() => {
          setTooltipVisible(false);
          setTooltipDismissed(true);
          event.track('tooltip_auto_hidden', { isMobile: true });
        }, 7000);
        return () => clearTimeout(autoHide);
      }
    }, 5000);

    return () => clearTimeout(timer);
  }, [isMobile]);

  const handleClick = () => {
    event.track('puppy_clicked', { isMobile });
    setIsExpanded((prev) => !prev);
    setTooltipVisible(false);
    setTooltipDismissed(false);
  };

  return (
    <div className={`fixed bottom-2 right-4 z-50 md:block ${showOnMobile ? '' : 'hidden'}`}>
      <div className="relative w-[60px] h-[60px]">
        {/* Puppy Video pinned to bottom right */}
        <motion.div
          onClick={handleClick}
          whileTap={isMobile ? { scale: 1.15 } : undefined}
          className="absolute bottom-0 right-0 cursor-pointer w-16 h-16 md:w-24 md:h-24 rounded-full overflow-hidden border-2 border-white shadow-lg transition-all hover:scale-105"
          title="Click to learn more"
        >
          <video
            src="/videos/pet-dog-2-small.mp4"
            autoPlay
            muted
            loop
            playsInline
            className="w-full h-full object-cover"
          />
        </motion.div>

        {/* Tooltip - final position tweak */}
        <AnimatePresence>
          {!isExpanded && tooltipVisible && (
            <motion.div
              key="tooltip"
              initial={{ opacity: 0, x: isMobile ? 10 : -50, y: isMobile ? 30 : 0 }}
              animate={{ opacity: 1, x: isMobile ? 10 : -50, y: isMobile ? 30 : 0 }}
              exit={{ opacity: 0, x: isMobile ? 10 : -50, y: isMobile ? 30 : 0 }}
              transition={{ duration: 0.3 }}
              className="absolute left-[-200px] bottom-[100px] w-[220px] sm:w-[240px] bg-zinc-900 text-white text-xs rounded-lg shadow-xl p-3 border border-zinc-700
                relative pointer-events-none group-hover:pointer-events-auto group-hover:opacity-100"
            >
              <div className="font-semibold text-sm text-indigo-400 mb-1">Did you know?</div>
              <p className="text-zinc-300 leading-snug">{features[currentFeatureIndex]}</p>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Panel */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              key="panel"
              initial={{ opacity: 0, scale: 0.95, y: isMobile ? -50 : -50, x: isMobile ? 0 : -20 }}
              animate={{ opacity: 1, scale: 1, y: isMobile ? -50 : -50, x: isMobile ? 0 : -20 }}
              exit={{ opacity: 0, scale: 0.95, y: isMobile ? -50 : -50, x: isMobile ? 0 : -20 }}
              transition={{ duration: 0.3 }}
              className="absolute left-[-260px] bottom-[140px] w-[280px] bg-zinc-950 border border-zinc-700 rounded-lg shadow-2xl p-4 space-y-3 text-white text-sm
                relative pointer-events-auto"
            >
              <h3 className="font-semibold text-lg text-indigo-400">QuickSites Features</h3>
              <ul className="list-none pl-5 space-y-1 text-zinc-300">
                {features.map((f, i) => (
                  <li key={i}>{f.replace(/^.+? /, '')}</li>
                ))}
              </ul>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Show tip button */}
        {/* {tooltipDismissed && !isExpanded && (
          <button
            onClick={() => {
              setTooltipVisible(true);
              setTooltipDismissed(false);
              event.track('tooltip_manually_restored', { isMobile });
            }}
            className="absolute bottom-0 right-0 translate-y-full mt-2 text-xs text-zinc-400 hover:text-white underline"
          >
            Show tip
          </button>
        )} */}
      </div>
    </div>
  );
}

