        <Button onClick={handleSave} disabled={isSaving}>
          {isSaving ? 'Saving...' : 'Save Changes'}
        </Button>
      </div>

      <EditableSitePageEditor
        data={editingSite.data as unknown as Template['data']}
        onChange={() => {}}
