'use client';

import { useEffect, useState } from 'react';

export function useMockGeolocation() {
  const [mockGeo, setMockGeo] = useState<{ lat: number; lon: number } | null>(null);

  useEffect(() => {
    const cookie = document.cookie
      .split('; ')
      .find((row) => row.startsWith('mock-geo='));

    if (!cookie) {
      setMockGeo(null);
      return;
    }

    try {
      const value = decodeURIComponent(cookie.split('=')[1]);
      const [lat, lon] = value.split(',').map(Number);
      if (!isNaN(lat) && !isNaN(lon)) {
        setMockGeo({ lat, lon });
      }
    } catch {
      setMockGeo(null);
    }
  }, []);

  return mockGeo;
}

import { useState } from 'react';

function toTitleCase(name: string): string {
  return name.trim().toLowerCase().replace(/\b\w/g, (c) => c.toUpperCase());
}

export type CityInfo = {
  name: string;
  distance: number;
  lat: number;
  lng: number;
};

export function useNearbyCities() {
  const [loading, setLoading] = useState(false);
  const [cities, setCities] = useState<CityInfo[]>([]);
  const [error, setError] = useState<string | null>(null);

  const fetchCities = async (lat: number, lng: number, radiusMiles = 30) => {
    setLoading(true);
    setError(null);
    try {
      console.log('[useNearbyCities] Fetching nearby cities', { lat, lng, radiusMiles });

      const res = await fetch(
        `/api/nearby-cities?lat=${lat}&lng=${lng}&radius=${radiusMiles * 1609}`
      );
      const data = await res.json();
      console.log('[useNearbyCities] API response', data);

      if (res.ok) {
        const normalized: CityInfo[] = (data.cities || []).map((c: any) => ({
          name: toTitleCase(c.name),
          distance: c.distance,
          lat: c.lat,
          lng: c.lng,
        }));
        setCities(normalized);
      } else {
        throw new Error(data.error || 'Unknown error');
      }
    } catch (err: any) {
      console.error('[useNearbyCities] Error:', err);
      setError(err.message || 'Request failed');
      setCities([]);
    } finally {
      setLoading(false);
    }
  };

  return { cities, loading, error, fetchCities };
}

import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';

export function useQueryFlash(key: string): string | null {
  const searchParams = useSearchParams();
  const [value, setValue] = useState<string | null>(null);

  useEffect(() => {
    const param = searchParams?.get(key);
    if (param && typeof param === 'string') {
      setValue(param);

      // Strip it from the URL without full reload
      const rest = new URLSearchParams(searchParams?.toString() || '');
      rest.delete(key);
      window.location.href = `${window.location.pathname}?${rest.toString()}`;
    }
  }, [searchParams?.get(key)]); // re-run if query changes

  return value;
}

