import { supabase } from '@/lib/supabase';
import { toast } from 'react-hot-toast';
import type { ValidatedTemplate } from '@/admin/lib/zod/templateSaveSchema';
import { TemplateSaveSchema } from '@/admin/lib/zod/templateSaveSchema';

export async function useTemplateSave(template: ValidatedTemplate): Promise<boolean> {
  const validated = TemplateSaveSchema.safeParse(template);
  if (!validated.success) {
    toast.error('Template is invalid. Cannot save.');
    console.warn('[Validation Error]', validated.error.format());
    return false;
  }

  const { data, error } = await supabase
    .from('templates')
    .update(validated.data)
    .eq('id', template.id);

  if (error) {
    toast.error('Save failed');
    return false;
  }

  toast.success('Template saved!');
  return true;
}

'use client';

import { createContext, useContext, useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import type { Database } from '@/types/supabase';
import { industryPresets } from '@/lib/theme/industryPresets';
import { fontMap } from '@/lib/theme/fontMap';

export type GlowConfig = {
  size: 'sm' | 'md' | 'lg' | 'xl';
  intensity: number;
  colors: string[];
};

export type SiteTheme = {
  glow: GlowConfig[];
  fontFamily?: string;
  borderRadius?: string;
  accentColor?: string;
  darkMode?: 'light' | 'dark';
  fontUrl?: string; // optional reference
  name?: string;
  description?: string;
};

const defaultGlow: GlowConfig[] = [
  { size: 'xl', intensity: 0.2, colors: ['from-purple-600', 'via-blue-500', 'to-pink-500'] },
];

const defaultTheme: SiteTheme = {
  glow: defaultGlow,
  fontFamily: 'sans',
  borderRadius: 'lg',
  accentColor: 'indigo-600',
  darkMode: 'dark',
};

const ThemeContext = createContext<{
  theme: SiteTheme;
  setTheme: (t: SiteTheme) => void;
  toggleDark?: () => void;
  siteSlug: string;
}>({
  theme: defaultTheme,
  setTheme: () => {},
  toggleDark: () => {},
  siteSlug: 'default',
});

export function ThemeProvider({
  children,
  siteSlug = 'default',
}: {
  children: React.ReactNode;
  siteSlug?: string;
}) {
  const supabase = createClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
  );

  const [theme, setThemeState] = useState<SiteTheme>(defaultTheme);
  const [isClient, setIsClient] = useState(false);

  useEffect(() => {
    setIsClient(true);
  }, []);

  useEffect(() => {
    const loadTheme = async () => {
      const key = `theme-config::${siteSlug}`;
      const cached = localStorage.getItem(key);

      // 1. Local storage cache
      if (cached) {
        try {
          const parsed = JSON.parse(cached);
          document.documentElement.setAttribute('data-theme', parsed.darkMode ?? 'light');
          setThemeState(parsed);
          return;
        } catch {
          console.warn('‚ö†Ô∏è Failed to parse cached theme');
        }
      }

      // 2. Supabase
      const userId = (window as any).__DEV_MOCK_USER__?.id;
      let foundTheme: SiteTheme | null = null;

      if (userId) {
        const { data } = await supabase
          .from('user_site_settings')
          .select('glow_config, theme_font, theme_radius, theme_accent, theme_mode, theme_font_url, theme_name, theme_description')
          .eq('user_id', userId)
          .eq('site_slug', siteSlug)
          .single();

        if (data) {
          foundTheme = {
            glow: Array.isArray(data.glow_config) ? data.glow_config : [data.glow_config],
            fontFamily: data.theme_font || defaultTheme.fontFamily,
            borderRadius: data.theme_radius || defaultTheme.borderRadius,
            accentColor: data.theme_accent || defaultTheme.accentColor,
            darkMode: data.theme_mode || defaultTheme.darkMode,
            fontUrl: data.theme_font_url || fontMap[data.theme_font as keyof typeof fontMap]?.googleUrl,
            name: data.theme_name || '',
            description: data.theme_description || '',
          };
        }
      }

      // 3. Fallback from global site config (e.g. SSR injected)
      if (!foundTheme && typeof window !== 'undefined') {
        const siteConfigTheme = (window as any).__SITE_CONFIG__?.theme;
        if (siteConfigTheme) {
          console.log('üí° Using __SITE_CONFIG__ theme fallback');
          foundTheme = siteConfigTheme;
        }
      }

      // 4. Fallback to industry preset
      if (!foundTheme) {
        const preset = industryPresets[siteSlug];
        if (preset) {
          console.log(`‚öôÔ∏è Using industry preset for "${siteSlug}"`);
          foundTheme = preset;
        }
      }

      // 5. Final fallback
      if (!foundTheme) {
        foundTheme = defaultTheme;
      }

      // 6. Apply + persist
      document.documentElement.setAttribute('data-theme', foundTheme.darkMode ?? 'light');
      setThemeState(foundTheme);
      localStorage.setItem(key, JSON.stringify(foundTheme));

      const userIdAgain = (window as any).__DEV_MOCK_USER__?.id;
      if (userIdAgain && !cached) {
        const fontKey = foundTheme.fontFamily as keyof typeof fontMap;
        const fontUrl = fontMap[fontKey]?.googleUrl ?? '';

        await supabase.from('user_site_settings').upsert({
          user_id: userIdAgain,
          site_slug: siteSlug,
          glow_config: foundTheme.glow,
          theme_font: foundTheme.fontFamily,
          theme_radius: foundTheme.borderRadius,
          theme_accent: foundTheme.accentColor,
          theme_mode: foundTheme.darkMode,
          theme_font_url: fontUrl,
          theme_name: foundTheme.name,
          theme_description: foundTheme.description,
        });
      }
    };

    if (isClient) loadTheme();
  }, [siteSlug, isClient]);

  const setTheme = async (value: SiteTheme) => {
    setThemeState(value);
    if (value.darkMode) {
      document.documentElement.setAttribute('data-theme', value.darkMode);
    }

    const userId = (window as any).__DEV_MOCK_USER__?.id;
    const key = `theme-config::${siteSlug}`;
    const fontKey = value.fontFamily as keyof typeof fontMap;
    const fontUrl = fontMap[fontKey]?.googleUrl ?? '';

    try {
      localStorage.setItem(key, JSON.stringify(value));

      if (userId) {
        await supabase.from('user_site_settings').upsert({
          user_id: userId,
          site_slug: siteSlug,
          glow_config: value.glow,
          theme_font: value.fontFamily,
          theme_radius: value.borderRadius,
          theme_accent: value.accentColor,
          theme_mode: value.darkMode,
          theme_font_url: fontUrl,
          theme_name: value.name,
          theme_description: value.description,
        });
      }
    } catch (err) {
      console.warn('‚ö†Ô∏è Failed to persist theme', err);
    }
  };

  const toggleDark = () => {
    const next = theme.darkMode === 'dark' ? 'light' : 'dark';
    const updated = { ...theme, darkMode: next };
    setTheme(updated as SiteTheme);
  };

  if (!isClient) return null;

  return (
    <ThemeContext.Provider value={{ theme, setTheme, toggleDark, siteSlug }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  return useContext(ThemeContext);
}

'use client';

import { useEffect, useMemo } from 'react';
import { useRouter } from 'next/navigation';
import { z, ZodObject, ZodRawShape } from 'zod';
import { parseQueryParams } from '@/lib/parseQueryParams';
import { stringifyQueryParams } from '@/lib/stringifyQueryParams';

type ParamTools<T> = {
  params: T;
  setParam: (key: keyof T, value: any) => void;
  clearParam: (key: keyof T) => void;
};

export function useTypedQuery<T extends ZodRawShape>(
  schema: ZodObject<T>
): ParamTools<z.infer<typeof schema>> {
  const router = useRouter();
  const queryParams = useMemo(() => {
    return typeof window !== 'undefined'
      ? new URLSearchParams(window.location.search)
      : new URLSearchParams();
  }, []);

  const params = useMemo(() => parseQueryParams(schema, queryParams), [schema, queryParams]);

  const setParam = (key: keyof typeof params, value: any) => {
    const next = { ...params, [key]: value };
    const query = stringifyQueryParams(schema, next);
    const newUrl = `${window.location.pathname}?${query}`;
    router.replace(newUrl);
  };

  const clearParam = (key: keyof typeof params) => {
    const updated = new URLSearchParams(window.location.search);
    updated.delete(key as string);
    const newUrl = `${window.location.pathname}?${updated.toString()}`;
    router.replace(newUrl);
  };

  // Inject defaults only once on mount
  useEffect(() => {
    if (typeof window === 'undefined') return;
    const parsed = parseQueryParams(schema, queryParams);
    const urlParams = new URLSearchParams(window.location.search);
    let changed = false;

    for (const key in parsed) {
      const val = parsed[key];
      const raw = urlParams.get(key);
      const norm = Array.isArray(val) ? val.join(',') : String(val);
      if (!raw && val !== undefined && val !== null) {
        urlParams.set(key, norm);
        changed = true;
      }
    }

    if (changed) {
      router.replace(`${window.location.pathname}?${urlParams.toString()}`);
    }
  }, []);

  return { params, setParam, clearParam };
}

'use client';

import { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { z } from 'zod';
import { parseTypedQueryValue } from '@/admin/lib/query/parseTypedQueryValue';

type InferParamType<T> =
  T extends 'string[]' ? string[] :
  T extends 'number[]' ? number[] :
  T extends 'string' ? string :
  T extends 'number' ? number :
  T extends 'date' ? Date :
  T extends 'date[]' ? Date[] :
  T extends 'json' ? Record<string, any> :
  T extends 'json[]' ? Record<string, any>[] :
  T extends 'boolean' ? boolean :
  never;

type ParamReturn<T> = [
  InferParamType<T>,
  (value: any) => void,
  () => void
];

function getSearchParams(): URLSearchParams {
  return typeof window !== 'undefined'
    ? new URLSearchParams(window.location.search)
    : new URLSearchParams();
}

function isArrayType(type: string): boolean {
  return ['string[]', 'number[]', 'date[]', 'json[]'].includes(type);
}

export default function useTypedQueryParam<
  T extends
    | 'string'
    | 'number'
    | 'boolean'
    | 'string[]'
    | 'number[]'
    | 'date'
    | 'date[]'
    | 'json'
    | 'json[]'
>(
  key: string,
  fallback: any,
  type: T,
  schema?: z.ZodTypeAny
): ParamReturn<T> {
  const router = useRouter();
  const searchParams = getSearchParams();

  // Set default param if missing (only in browser)
  useEffect(() => {
    if (typeof window === 'undefined') return;

    if (!searchParams.has(key) && fallback !== undefined && type !== 'json') {
      const updated = new URLSearchParams(window.location.search);
      updated.set(key, String(fallback));
      const newUrl = `${window.location.pathname}?${updated.toString()}`;
      router.replace(newUrl);
    }
  }, [key, fallback, router, type]);

  const setParam = (value: any) => {
    const newParams = new URLSearchParams(window.location.search);

    if (Array.isArray(value)) {
      newParams.delete(key);
      value.forEach((v) => {
        const serialized =
          typeof v === 'object'
            ? encodeURIComponent(JSON.stringify(v))
            : String(v);
        newParams.append(key, serialized);
      });
    } else if (value instanceof Date) {
      newParams.set(key, value.toISOString());
    } else if (typeof value === 'object' && value !== null) {
      newParams.set(key, encodeURIComponent(JSON.stringify(value)));
    } else {
      newParams.set(key, String(value));
    }

    const newUrl = `${window.location.pathname}?${newParams.toString()}`;
    router.replace(newUrl);
  };

  const clearParam = () => {
    const newParams = new URLSearchParams(window.location.search);
    newParams.delete(key);
    const newUrl = `${window.location.pathname}?${newParams.toString()}`;
    router.replace(newUrl);
  };

  const values = searchParams.getAll(key);
  const raw = isArrayType(type) ? values : searchParams.get(key);

  const parsed: InferParamType<T> = parseTypedQueryValue(
    key,
    raw,
    fallback,
    type,
    schema as any,
    router as any
  );

  return [parsed, setParam, clearParam];
}

