import JSZip from 'jszip';
import { saveAs } from 'file-saver';

export async function downloadFaviconZip(sources: Record<string, Blob>) {
  const zip = new JSZip();

  for (const size in sources) {
    zip.file(`favicon-${size}x${size}.png`, sources[size]);
  }

  zip.file('manifest.json', JSON.stringify({
    icons: Object.keys(sources).map(size => ({
      src: `favicon-${size}x${size}.png`,
      sizes: `${size}x${size}`,
      type: 'image/png',
    })),
    name: 'QuickSite',
    short_name: 'QS',
    start_url: '/',
    display: 'standalone',
    background_color: '#000000',
    theme_color: '#111111',
  }, null, 2));

  const blob = await zip.generateAsync({ type: 'blob' });
  saveAs(blob, 'favicons.zip');
}

import sharp from 'sharp';

export async function generateFaviconBundle(file: File): Promise<{ ico: Blob; sizes: Record<string, Blob> } | null> {
  const buffer = await file.arrayBuffer();
  const base = sharp(Buffer.from(buffer));

  try {
    const sizes = [16, 32, 48, 64];
    const pngs: Buffer[] = [];

    const resized: Record<string, Blob> = {};
    for (const size of sizes) {
      const resizedBuf = await base.clone().resize(size, size).png().toBuffer();
      pngs.push(resizedBuf);
      resized[size] = new Blob([resizedBuf], { type: 'image/png' });
    }

    const icoBuffer = await sharp({ create: { width: 64, height: 64, channels: 4, background: 'transparent' } })
      .composite([{ input: pngs[2] }])
      .resize(64, 64)
      .toFormat('ico')
      .toBuffer();

    return {
      ico: new Blob([icoBuffer], { type: 'image/x-icon' }),
      sizes: resized,
    };
  } catch (err) {
    console.error('Error generating favicon bundle:', err);
    return null;
  }
}

import sharp from 'sharp';

export async function optimizeFaviconUpload(file: File): Promise<Blob | null> {
  const buffer = await file.arrayBuffer();
  try {
    const resized = await sharp(Buffer.from(buffer))
      .resize(64, 64)
      .png()
      .toBuffer();
    return new Blob([resized], { type: 'image/png' });
  } catch (err) {
    console.error('Favicon optimization failed:', err);
    return null;
  }
}

export function renderAppleIcons(urls: Partial<Record<string, string>>): string {
  return Object.entries(urls)
    .filter(([size]) => Number(size) >= 64)
    .map(
      ([size, url]) => 
        `<link rel="apple-touch-icon" sizes="${size}x${size}" href="${url}">`
    )
    .join('\n');
}

export function renderFaviconLinks(urls: Partial<Record<string, string>>): string {
  const lines: string[] = [];

  for (const size in urls) {
    lines.push(
      `<link rel="icon" type="image/png" sizes="${size}x${size}" href="${urls[size]}">`
    );
  }

  return lines.join('\n');
}

// lib/getFromDate.ts
export function getFromDate(label: string): Date | null {
    const now = new Date();

    switch (label) {
      case 'Today': {
        return new Date(now.getFullYear(), now.getMonth(), now.getDate());
      }

      case 'Yesterday': {
        const from = new Date(now);
        from.setDate(from.getDate() - 1);
        from.setHours(0, 0, 0, 0);
        return from;
      }

      case 'Last 7 days': {
        const from = new Date(now);
        from.setDate(from.getDate() - 7);
        return from;
      }

      case 'Last 30 days': {
        const from = new Date(now);
        from.setDate(from.getDate() - 30);
        return from;
      }

      case 'This month': {
        return new Date(now.getFullYear(), now.getMonth(), 1);
      }

      case 'Last month': {
        return new Date(now.getFullYear(), now.getMonth() - 1, 1);
      }

      case 'This year': {
        return new Date(now.getFullYear(), 0, 1);
      }

      case 'All time':
      default:
        return null;
    }
  }
  export const dateFilterOptions = [
    'Today',
    'Yesterday',
    'Last 7 days',
    'Last 30 days',
    'This month',
    'Last month',
    'This year',
    'All time',
  ] as const;

  export type DateFilterOption = (typeof dateFilterOptions)[number];

// lib/leads/distance.ts
import { Lead } from '@/types/lead.types';

export function getDistanceMiles(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 3958.8; // Earth radius in miles
    const toRad = (deg: number) => deg * (Math.PI / 180);

    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);

    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;

    return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  }

  export function sortLeadsByDistance(
    leads: Lead[],
    targetLat: number,
    targetLon: number
  ): Lead[] {
    return leads
      .filter((l) => l.address_lat !== null && l.address_lon !== null)
      .sort((a, b) => {
        const distA = getDistanceMiles(targetLat, targetLon, a.address_lat!, a.address_lon!);
        const distB = getDistanceMiles(targetLat, targetLon, b.address_lat!, b.address_lon!);
        return distA - distB;
      });
  }

import { z } from 'zod';
import type { Lead as BaseLead } from '@/types/lead.types';
import { getDistanceMiles } from './distance';

export const zLead = z.object({
  id: z.string(),
  business_name: z.string(),
  campaign_id: z.string().nullable(),
  draft_sites: z
    .object({
      domain: z.string().optional(),
      is_claimed: z.boolean().optional(),
    })
    .nullable()
    .optional(),
  users: z
    .object({
      email: z.string().email().optional(),
    })
    .nullable()
    .optional(),
  address_lat: z.number().nullable().optional(),
  address_lon: z.number().nullable().optional(),
});

export type EnrichedLead = BaseLead & {
  draft_sites?: {
    domain?: string;
    is_claimed?: boolean;
  };
  users?: {
    email?: string;
  };
  address_lat?: number | null;
  address_lon?: number | null;
  link_type: 'üìé campaign_id' | 'üì¶ lead_ids[]' | null;
  distance_km?: number;
};

export function enrichLead(
  lead: any,
  campaignMap: Map<string, string[]>
): EnrichedLead | null {
  const parsed = zLead.safeParse(lead);
  if (!parsed.success) {
    console.warn('‚ùå Invalid lead skipped:', parsed.error.format());
    return null;
  }

  const cleaned = parsed.data;
  let link_type: EnrichedLead['link_type'] = null;

  for (const [campId, ids] of campaignMap.entries()) {
    if (ids.includes(cleaned.id)) {
      link_type = cleaned.campaign_id === campId ? 'üìé campaign_id' : 'üì¶ lead_ids[]';
      break;
    }
  }

  return {
    ...lead,
    link_type,
  };
}

export function enrichLeadWithDistance(
  lead: any,
  campaignMap: Map<string, string[]>,
  campaignLat: number,
  campaignLon: number
): EnrichedLead | null {
  const base = enrichLead(lead, campaignMap);
  if (!base) return null;

  const lat = base.address_lat;
  const lon = base.address_lon;

  if (lat !== null && lon !== null && typeof lat === 'number' && typeof lon === 'number') {
    const distMiles = getDistanceMiles(campaignLat, campaignLon, lat, lon);
    return { ...base, distance_km: Math.round(distMiles * 1.60934 * 10) / 10 }; // round to 1 decimal
  }

  return base;
}

import { supabase } from '@/admin/lib/supabaseClient';

export type LeadSummary = {
  id: string;
  business_name: string | null;
  address_city: string | null;
  address_state?: string | null;
  created_at?: string | null;
  industry?: string | null;
  current_campaign_id?: string | null;
  current_campaign_expires_at?: string | null;
};

type LoadLeadsOptions = {
  city?: string;
  state?: string;
  industry?: string;
  sortBy?: 'created_at' | 'business_name';
  sortOrder?: 'asc' | 'desc';
};

const _leadCache = new Map<string, LeadSummary[]>();

function makeCacheKey(opts: LoadLeadsOptions) {
  return JSON.stringify(opts);
}

export async function loadLeads(options: LoadLeadsOptions = {}): Promise<LeadSummary[]> {
  const cacheKey = makeCacheKey(options);
  if (_leadCache.has(cacheKey)) return _leadCache.get(cacheKey)!;

  const { data, error } = await supabase
    .from('leads')
    .select(`
      id,
      business_name,
      address_city,
      address_state,
      created_at,
      industry,
      current_campaign_id,
      current_campaign_expires_at
    `);

  if (error) {
    console.error('Error loading leads:', error.message);
    return [];
  }

  let leads = data || [];

  if (options.city) {
    leads = leads.filter((l) => l.address_city?.toLowerCase() === options.city!.toLowerCase());
  }

  if (options.state) {
    leads = leads.filter((l) => l.address_state?.toLowerCase() === options.state!.toLowerCase());
  }

  if (options.industry) {
    leads = leads.filter((l) => l.industry?.toLowerCase() === options.industry!.toLowerCase());
  }

  if (options.sortBy) {
    leads.sort((a, b) => {
      const aVal = a[options.sortBy!] ?? '';
      const bVal = b[options.sortBy!] ?? '';
      return options.sortOrder === 'desc'
        ? String(bVal).localeCompare(String(aVal))
        : String(aVal).localeCompare(String(bVal));
    });
  }

  _leadCache.set(cacheKey, leads);
  return leads;
}

export async function getTopTwoLeads(
  options: LoadLeadsOptions
): Promise<[LeadSummary, LeadSummary] | null> {
  const leads = await loadLeads(options);
  if (leads.length >= 2) {
    return [leads[0], leads[1]];
  }
  return null;
}

import fetch from 'node-fetch';

function haversineDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
  const R = 6371;
  const toRad = (deg: number) => (deg * Math.PI) / 180;
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a = Math.sin(dLat / 2) ** 2 +
    Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

function normalizeCityName(name: string): string {
  return name
    .trim()
    .toLowerCase()
    .replace(/ \w/g, (c) => c.toUpperCase());
}

export async function getNearbyCities(lat: number, lng: number, radiusMeters = 48280): Promise<
  { name: string; distance: number; lat: number; lng: number }[]
> {
  const query = `
    [out:json][timeout:25];
    (
      node[place~"city|town|village"](around:${radiusMeters},${lat},${lng});
    );
    out body;
  `;
  const url = 'https://overpass-api.de/api/interpreter';
  const res = await fetch(url, {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: `data=${encodeURIComponent(query)}`
  });

  const data = await res.json() as { elements: { tags: { name: string }; lat: number; lon: number }[] };
  if (!data.elements) return [];

  const seen = new Set();
  const enriched = data.elements
    .filter((e: any) => e.tags?.name && e.lat && e.lon)
    .map((e: any) => {
      const norm = normalizeCityName(e.tags.name);
      return {
        name: norm,
        lat: e.lat,
        lng: e.lon,
        distance: haversineDistance(lat, lng, e.lat, e.lon),
      };
    })
    .filter(e => {
      if (seen.has(e.name)) return false;
      seen.add(e.name);
      return true;
    });

  enriched.sort((a, b) => a.distance - b.distance);
  return enriched;
}

