import crypto from 'crypto';
import { safeParse } from '../utils/safeParse';
import { getClientIp, getUserAgent, getReferer } from '../safeHeaders';
import { getOrCreateSessionCookie } from '../cookies/getOrCreateSessionCookie';
import { getMockLocation } from './getMockLocation';
import { getSupabaseCookieAdapter } from '../utils/getSupabaseCookieAdapter';

import { createServerComponentClient } from '@supabase/auth-helpers-nextjs';
import type { SupabaseClient } from '@supabase/supabase-js';
import type { Database } from '@/types/supabase';
import type { MockGeoLocation } from '@/types/location';
import type { ReadonlyRequestCookies } from 'next/dist/server/web/spec-extension/adapters/request-cookies';
import type { ReadonlyHeaders } from 'next/dist/server/web/spec-extension/adapters/headers';

export type RequestContext = {
  cookies: ReadonlyRequestCookies;
  headers: ReadonlyHeaders;
  cookieMode: 'readonly';
  headerMode: 'readonly';

  ip: string;
  userAgent: string;
  referer?: string;
  role?: string;
  userId?: string;
  userEmail?: string;
  abVariant?: string;
  sessionId: string;
  traceId: string;
  geo: MockGeoLocation | null;

  supabase?: {
    client: SupabaseClient<Database>;
    user: {
      id: string;
      email: string;
      role?: string;
    } | null;
  };
};

let _requestContextCache: RequestContext | null = null;

export async function getRequestContext(
  stores?: {
    cookieStore: ReadonlyRequestCookies;
    headerStore: ReadonlyHeaders;
    abRaw?: string | null;
    role?: string;
    userId?: string;
    userEmail?: string;
  },
  withSupabase = false
): Promise<RequestContext> {
  if (_requestContextCache) return _requestContextCache;

  if (!stores?.cookieStore || !stores?.headerStore) {
    throw new Error(
      `getRequestContext: Missing cookieStore or headerStore. Did you forget to call extractUserContext()?`
    );
  }

  const {
    cookieStore,
    headerStore,
    abRaw,
    role,
    userId,
    userEmail,
  } = stores;

  const abVariant = safeParse(abRaw) as string | undefined;

  const [ip, userAgent, referer, sessionId, geo] = await Promise.all([
    getClientIp(),
    getUserAgent(),
    getReferer(),
    getOrCreateSessionCookie(),
    getMockLocation(),
  ]);

  const traceId = crypto.randomUUID();

  const context: RequestContext = {
    cookies: cookieStore,
    headers: headerStore,
    cookieMode: 'readonly',
    headerMode: 'readonly',
    ip,
    userAgent,
    referer,
    role,
    userId,
    userEmail,
    abVariant,
    sessionId,
    traceId,
    geo,
  };

  if (withSupabase) {
    const supabase = createServerComponentClient<Database>({
      cookies: getSupabaseCookieAdapter(cookieStore),
    });

    const {
      data: { user },
    } = await supabase.auth.getUser();

    context.supabase = {
      client: supabase,
      user: user
        ? {
            id: user.id,
            email: user.email ?? '',
            role: user.user_metadata?.role,
          }
        : null,
    };
  }

  _requestContextCache = context;
  return context;
}

type GeoResult = { lat: number; lon: number };
const memoryCache: Record<string, GeoResult> = {};

const getLocalStorageCache = (): Record<string, GeoResult> => {
  if (typeof window === 'undefined') return {};
  try {
    const cached = localStorage.getItem('geoCache');
    return cached ? JSON.parse(cached) : {};
  } catch {
    return {};
  }
};

const setLocalStorageCache = (cache: Record<string, GeoResult>) => {
  if (typeof window === 'undefined') return;
  try {
    localStorage.setItem('geoCache', JSON.stringify(cache));
  } catch {}
};

export async function resolveGeo(city: string, state: string): Promise<GeoResult> {
  const key = `${city.trim().toLowerCase()},${state.trim().toLowerCase()}`;

  // Check in-memory cache first
  if (memoryCache[key]) return memoryCache[key];

  // Check localStorage cache
  const localCache = getLocalStorageCache();
  if (localCache[key]) {
    memoryCache[key] = localCache[key]; // hydrate into memory
    return localCache[key];
  }

  console.log('[üó∫Ô∏è geoCache miss]', key);

  // Fetch from API
  try {
    const res = await fetch(`/api/geocode?city=${encodeURIComponent(city)}&state=${encodeURIComponent(state)}`);
    const json = await res.json();

    const lat = json.lat ?? 39.5;
    const lon = json.lon ?? -98.35;
    const result = { lat, lon };

    // Cache result in both memory + localStorage
    memoryCache[key] = result;
    localCache[key] = result;
    setLocalStorageCache(localCache);

    return result;
  } catch (err) {
    console.warn('[‚ö†Ô∏è geocode failed]', err);
    return { lat: 39.5, lon: -98.35 }; // fallback: US center
  }
}

