// lib/safeQueryParam.ts
type ParamSource = URLSearchParams | Record<string, string | undefined>;

function isURLSearchParams(
  input: ParamSource
): input is URLSearchParams {
  return typeof (input as URLSearchParams).get === 'function';
}

export function getSafeQueryParam(
  searchParams: ParamSource,
  key: string,
  fallback = ''
): string {
  const value = isURLSearchParams(searchParams)
    ? searchParams.get(key)
    : searchParams[key];

  return typeof value === 'string' && value.length > 0 ? value : fallback;
}

export function getNumberParam(
  searchParams: ParamSource,
  key: string,
  fallback = 0
): number {
  const raw = getSafeQueryParam(searchParams, key);
  const num = parseFloat(raw);
  return isNaN(num) ? fallback : num;
}

export function getBooleanParam(
  searchParams: ParamSource,
  key: string,
  fallback = false
): boolean {
  const raw = getSafeQueryParam(searchParams, key).toLowerCase();
  if (raw === 'true' || raw === '1') return true;
  if (raw === 'false' || raw === '0') return false;
  return fallback;
}

export function getArrayParam(
  searchParams: ParamSource,
  key: string,
  fallback: string[] = [],
  separator: string = ','
): string[] {
  if (isURLSearchParams(searchParams)) {
    const all = searchParams.getAll(key);
    if (all.length > 0) return all;
    const commaSplit = searchParams.get(key)?.split(separator);
    return commaSplit?.filter(Boolean) || fallback;
  }

  const value = searchParams[key];
  if (typeof value === 'string') {
    return value.split(separator).filter(Boolean);
  }

  return fallback;
}

// lib/safeSessionStorage.ts
export function safeGet<T = unknown>(key: string): T | null {
    if (typeof window === 'undefined') return null;
    try {
      const raw = sessionStorage.getItem(key) || localStorage.getItem(key);
      if (!raw) return null;
      return JSON.parse(raw);
    } catch {
      return null;
    }
  }

  export function safeSet(key: string, value: unknown, useLocal: boolean = false) {
    if (typeof window === 'undefined') return;
    try {
      const encoded = JSON.stringify(value);
      if (useLocal) {
        localStorage.setItem(key, encoded);
      } else {
        sessionStorage.setItem(key, encoded);
      }
    } catch {}
  }

  export function safeRemove(key: string, useLocal: boolean = false) {
    if (typeof window === 'undefined') return;
    if (useLocal) {
      localStorage.removeItem(key);
    } else {
      sessionStorage.removeItem(key);
    }
  }

export function renderHeadHTML({
  title,
  description,
  iconPath,
  useIco = false,
  appleTouch = true,
}: {
  title: string;
  description: string;
  iconPath: string; // /public/favicons/abc123
  useIco?: boolean;
  appleTouch?: boolean;
}): string {
  const iconHref = useIco
    ? `${iconPath}/favicon.ico`
    : `${iconPath}/favicon.png`;

  const links = [
    `<link rel="icon" href="${iconHref}">`,
    appleTouch ? `<link rel="apple-touch-icon" sizes="180x180" href="${iconPath}/favicon.png">` : '',
  ];

  return [
    `<title>${title}</title>`,
    `<meta name="description" content="${description}">`,
    ...links,
  ]
    .filter(Boolean)
    .join('\n');
}

