import { createClient } from '@supabase/supabase-js';
import fetch from 'node-fetch';
import dotenv from 'dotenv';

dotenv.config();

const supabase = createClient(
  process.env.SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

const API_ENDPOINT = 'http://localhost:3000/api/blocks/index'; // ‚úÖ Use your local or deployed endpoint

type BlockInput = {
  id: string;
  text: string;
  type: string;
  industry: string;
  tone?: string;
};

async function fetchBlocksFromSupabase(): Promise<BlockInput[]> {
  const { data: templates, error } = await supabase.from('templates').select('*');

  if (error) throw new Error(`Supabase error: ${error.message}`);

  const blocks: BlockInput[] = [];

  for (const template of templates) {
    const industry = template.industry || 'default';
    const pages = template.data?.pages || [];

    for (const page of pages) {
      for (let i = 0; i < (page.content_blocks || []).length; i++) {
        const block = page.content_blocks[i];
        const id = `template-${template.id}-page-${page.slug}-block-${i}`;

        blocks.push({
          id,
          text: JSON.stringify(block),
          type: block.type || 'unknown',
          industry,
          tone: 'neutral',
        });
      }
    }
  }

  return blocks;
}

async function postBlocksToApi(blocks: BlockInput[]) {
  const batchSize = 50;

  for (let i = 0; i < blocks.length; i += batchSize) {
    const batch = blocks.slice(i, i + batchSize);

    const res = await fetch(API_ENDPOINT, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ blocks: batch }),
    });

    const contentType = res.headers.get('content-type') || '';

    if (!res.ok) {
      const body = contentType.includes('application/json')
        ? await res.json()
        : await res.text();

      console.error(`‚ùå Failed to index batch ${i}‚Äì${i + batch.length - 1}:`, body);
      throw new Error(`Request failed with status ${res.status}`);
    }

    console.log(`‚úÖ Indexed ${batch.length} blocks (batch ${i / batchSize + 1})`);
  }
}

(async () => {
  try {
    const blocks = await fetchBlocksFromSupabase();
    console.log(`Preparing to index ${blocks.length} blocks...`);
    await postBlocksToApi(blocks);
    console.log('üéâ All blocks indexed successfully.');
  } catch (err: any) {
    console.error('üí• Error during indexing:', err.message || err);
  }
})();

import { readFileSync, writeFileSync, existsSync } from 'fs';
import path from 'node:path';
import { fileURLToPath } from 'url';
import { dirname } from 'path';

console.log('üü° lint-digest.ts: starting analysis...');

if (typeof process !== 'undefined' && process.argv[1] === fileURLToPath(import.meta.url)) {
  console.log('üü¢ lint-digest.ts: running as main module');
}

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Only run if executed directly
if (process.argv[1] === __filename) {
  console.log('üöÄ Starting lint-digest.ts...');

  const lintFile = 'lint-report.json';
  const txtOut = 'lint-summary.txt';
  const mdOut = 'lint-summary.md';

  if (!existsSync(lintFile)) {
    console.error(`‚ùå Missing ${lintFile}. Make sure ESLint ran correctly.`);
    process.exit(1);
  }

  let results: any[];
  try {
    const raw = readFileSync(lintFile, 'utf-8');
    results = JSON.parse(raw);
    if (!Array.isArray(results)) throw new Error('Expected an array of lint results');
  } catch (err) {
    console.error(`‚ùå Failed to read or parse ${lintFile}:`, err);
    process.exit(1);
  }

  type Message = {
    line: number;
    column: number;
    message: string;
    ruleId: string | null;
    severity: 'error' | 'warning';
  };

  type LintEntry = {
    file: string;
    errors: number;
    warnings: number;
    total: number;
    messages: Message[];
  };

  function getTopFolder(filePath: string) {
    const parts = filePath.split('/');
    const idx = parts.findIndex((p) => p === 'quicksites-v2');
    return parts[idx + 1] || 'root';
  }

  const filtered: LintEntry[] = results
    .filter(
      (entry: any) =>
        entry.filePath.includes('/quicksites-v2/') &&
        !entry.filePath.includes('/.venv/') &&
        !entry.filePath.includes('/node_modules/')
    )
    .map((entry: any) => ({
      file: entry.filePath,
      errors: entry.errorCount,
      warnings: entry.warningCount,
      total: entry.errorCount + entry.warningCount,
      messages: entry.messages.map((m: any) => ({
        line: m.line,
        column: m.column,
        message: m.message,
        ruleId: m.ruleId,
        severity: m.severity === 2 ? 'error' : 'warning',
      })),
    }))
    .filter((entry: LintEntry) => entry.total > 0);

  const grouped = new Map<string, LintEntry[]>();
  for (const entry of filtered) {
    const folder = getTopFolder(entry.file);
    if (!grouped.has(folder)) grouped.set(folder, []);
    grouped.get(folder)!.push(entry);
  }

  for (const entries of grouped.values()) {
    entries.sort((a, b) => {
      if (a.errors > 0 && b.errors === 0) return -1;
      if (a.errors === 0 && b.errors > 0) return 1;
      return b.total - a.total;
    });
  }

  // Write .txt summary
  const txtLines: string[] = [];
  txtLines.push(`Found ${filtered.length} files with issues:\n`);
  for (const [folder, entries] of [...grouped.entries()].sort()) {
    txtLines.push(`${folder}/`);
    for (const entry of entries) {
      txtLines.push(
        `  ${entry.total} issues (${entry.errors} errors, ${entry.warnings} warnings): ${entry.file}`
      );
    }
    txtLines.push('');
  }
  console.log('üìÑ Writing lint-summary.txt...');
  writeFileSync(txtOut, txtLines.join('\n'));

  // Write .md summary
  const mdLines: string[] = [];
  mdLines.push(`# ESLint Report`);
  mdLines.push(`Found **${filtered.length}** files with issues.\n`);
  for (const [folder, entries] of [...grouped.entries()].sort()) {
    mdLines.push(`## üìÅ \`${folder}/\``);
    for (const entry of entries) {
      mdLines.push(`### üîπ \`${entry.file}\``);
      mdLines.push(
        `- **${entry.total} issues**: ${entry.errors} error(s), ${entry.warnings} warning(s)\n`
      );
      for (const m of entry.messages) {
        mdLines.push(
          `  - [${m.severity}] Line ${m.line}:${m.column} ‚Äì ${m.message} (${m.ruleId || 'unknown'})`
        );
      }
      mdLines.push('');
    }
  }
  console.log('üìÑ Writing lint-summary.md...');
  writeFileSync(mdOut, mdLines.join('\n'));

  // Final check: confirm both outputs exist
  if (!existsSync(txtOut) || !existsSync(mdOut)) {
    console.error('‚ùå Failed to write summary files.');
    process.exit(1);
  }

  console.log(`‚úÖ Summary complete ‚Äî ${grouped.size} folders, ${filtered.length} files`);
}

