import 'dotenv/config';
import { execSync } from 'child_process';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

async function run() {
  const { data: job, error } = await supabase
    .from('regeneration_queue')
    .select('*')
    .eq('status', 'queued')
    .order('created_at', { ascending: true })
    .limit(1)
    .maybeSingle();

  if (!job) {
    console.log('‚úÖ No queued jobs.');
    return;
  }

  console.log('üîÑ Running job for:', job.domain);
  await supabase
    .from('regeneration_queue')
    .update({ status: 'processing', started_at: new Date().toISOString() })
    .eq('id', job.id);

  try {
    const cmd = `node ../init.js "${job.city}" "${job.state}" "${job.template_id}" --push-to-github`;
    const output = execSync(cmd, { encoding: 'utf-8' });

    await supabase
      .from('regeneration_queue')
      .update({
        status: 'done',
        finished_at: new Date().toISOString(),
        log: output,
      })
      .eq('id', job.id);

    await supabase.from('user_action_logs').insert([
      {
        domain_id: job.domain_id,
        action_type: 'regeneration_complete',
        triggered_by: job.triggered_by || 'queue_bot',
      },
    ]);

    console.log('‚úÖ Job complete.');
  } catch (err: any) {
    console.error('‚ùå Job failed:', err.message);

    await supabase
      .from('regeneration_queue')
      .update({
        status: 'error',
        finished_at: new Date().toISOString(),
        log: err.message,
      })
      .eq('id', job.id);
  }
}

run();

import fs from 'node:fs';
import path from 'node:path';

const PROJECT_ROOT = path.resolve('.');
const FILE_EXTS = ['.ts', '.tsx', '.js', '.jsx'];
const COMPONENT_ALIAS = '@/components/';

function toKebabCase(input: string): string {
  return input
    .replace(/([a-z0-9])([A-Z])/g, '$1-$2')
    .replace(/([A-Z]+)([A-Z][a-z])/g, '$1-$2')
    .toLowerCase();
}

function walk(dir: string, files: string[] = []) {
  for (const entry of fs.readdirSync(dir)) {
    const fullPath = path.join(dir, entry);
    const stat = fs.statSync(fullPath);
    if (stat.isDirectory()) {
      walk(fullPath, files);
    } else if (FILE_EXTS.includes(path.extname(fullPath))) {
      files.push(fullPath);
    }
  }
  return files;
}

function updateImportsToKebabCase() {
  const files = walk(PROJECT_ROOT);
  let updated = 0;

  for (const file of files) {
    let code = fs.readFileSync(file, 'utf-8');
    const original = code;

    // Replace '@/components/some-component' ‚Üí '@/components/some-component'
    code = code.replace(
      /(['"])@\/components\/((?:[A-Za-z0-9]+\/)*)([A-Z][A-Za-z0-9]*)\1/g,
      (_, quote, pathPart, filePart) => {
        return `${quote}@/components/${pathPart}${toKebabCase(filePart)}${quote}`;
      }
    );

    if (code !== original) {
      fs.writeFileSync(file, code, 'utf-8');
      console.log(`üìù Updated imports in: ${path.relative(PROJECT_ROOT, file)}`);
      updated++;
    }
  }

  console.log(`\n‚úÖ Done. Updated ${updated} file(s).`);
}

updateImportsToKebabCase();

import fs from 'node:fs';
