// Base type for shared props
export type BaseBlock = {
  _id?: string;
  tone?: string;
  industry?: string;
  tags?: string[];
  meta?: Record<string, any>;
};

// Individual block definitions
export type TextBlock = BaseBlock & {
  type: 'text';
  content: {
    value: string;
  };
};

export type ImageBlock = BaseBlock & {
  type: 'image';
  content: {
    url: string;
    alt: string;
  };
};

export type VideoBlock = BaseBlock & {
  type: 'video';
  content: {
    url: string;
    caption?: string;
  };
};

export type AudioBlock = BaseBlock & {
  type: 'audio';
  content: {
    url: string;
    title?: string;
    provider?: 'spotify' | 'soundcloud' | 'suno';
  };
};

export type QuoteBlock = BaseBlock & {
  type: 'quote';
  content: {
    text: string;
    attribution?: string;
  };
};

export type ButtonBlock = BaseBlock & {
  type: 'button';
  content: {
    label: string;
    href: string;
    style?: 'primary' | 'secondary' | 'ghost';
  };
};

export type GridBlock = BaseBlock & {
  type: 'grid';
  content: {
    columns: number;
    items: Block[];
  };
};

export type HeroBlock = BaseBlock & {
  type: 'hero';
  content: {
    headline: string;
    subheadline?: string;
    cta_text?: string;
    cta_link?: string;
    image_url?: string;
    layout_mode?: 'inline' | 'background' | 'full_bleed';
    blur_amount?: number;
    parallax_enabled?: boolean;
    image_position?: 'top' | 'center' | 'bottom';
    image_x?: number;
    image_y?: number;
  };
};

export type ServicesBlock = BaseBlock & {
  type: 'services';
  content: {
    title?: string;
    subtitle?: string;
    items: string[];
  };
};

export type FAQBlock = BaseBlock & {
  type: 'faq';
  content: {
    title?: string;
    subtitle?: string;
    items: {
      question: string;
      answer: string;
    }[];
    layout?: 'list' | 'accordion';
  };
};

export type CtaBlock = BaseBlock & {
  type: 'cta';
  content: {
    label: string;
    link: string;
    appearance?: 'button' | 'link';
  };
};

export type TestimonialBlock = BaseBlock & {
  type: 'testimonial';
  content: {
    testimonials: {
      quote: string;
      attribution?: string;
      avatar_url?: string;
      rating?: number;
    }[];
    randomized?: boolean;
    layout?: 'list' | 'carousel';
  };
};

export type FooterBlock = BaseBlock & {
  type: 'footer';
  content: {
    businessName: string;
    address: string;
    cityState: string;
    phone: string;
    links: {
      label: string;
      href: string;
    }[];
    logo_url?: string;
    social_links?: {
      platform: string;
      url: string;
    }[];
    copyright?: string;
  };
};

export type ServiceAreaBlock = BaseBlock & {
  type: 'service_areas';
  content: {
    title?: string;
    subtitle?: string;
    cities: string[];
    allCities?: string[];
    sourceLat?: number;
    sourceLng?: number;
    radiusMiles?: number;
  };
};

export type HeaderBlock = BaseBlock & {
  type: 'header';
  content: {
    logoUrl?: string;
    navItems: { label: string; href: string }[];
  };
};

export type ContactFormBlock = BaseBlock & {
  type: 'contact_form';
  content: {
    title: string;
    notification_email: string;
  };
};

// Unified Block union
export type Block =
  | TextBlock
  | ImageBlock
  | VideoBlock
  | AudioBlock
  | QuoteBlock
  | ButtonBlock
  | GridBlock
  | HeroBlock
  | ServicesBlock
  | CtaBlock
  | TestimonialBlock
  | FooterBlock
  | ServiceAreaBlock
  | HeaderBlock
  | FAQBlock
  | ContactFormBlock;

// Block with enforced _id
export type BlockWithId = Block & { _id: string };

// Normalization helper
export function normalizeBlock(block: Partial<Block>): BlockWithId {
  return {
    ...block,
    _id: block._id ?? crypto.randomUUID(),
  } as BlockWithId;
}

